    enum AttributesEnum {
        Stamp,
        Header,
        FooterTitle,
        FooterSubtitle,
        FooterStage,
        FooterSequenceLength,
        FooterPerformers,
        FooterShare,
        FooterClimate,
        FooterABHash,
        FooterSigBone,
        FooterVuln,
        FooterHeartDist,
        FooterChoreoHash,
        TempoDouble,
        TempoHalf,
        SideView,
        Pause,
        VulnerableStamp
    }
    enum AttributeValuesEnum {
        Numeric,
        Alphabetic,
        Symbolic,
        StageOptions,
        ShareOptions,
        ClimateOptions,
        SigBoneOptions,
        VulnOptions,
        NumericSmall,
        SequenceOptions,
        MovementOverlayOptions
    }
}
