    "contracts/BCK222TigerMinter.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.10;

import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./interfaces/IBCKEthFactory.sol";

import "hardhat/console.sol";

contract BCK222TigerMinter is Ownable, AccessControl {
    IBCKEthFactory public bckEthFactory;
    address public treasury;
    bool public mintWhitelist = true;
    bool public mintOpen = false;
    string public ipfsLocation =
        "QmSRGNgpUt3fqJEVAxSVMxfQ15TMgtzKkRgUuaZBhmAhLi/";

    uint256 public mintPrice = 0.111 ether;
    uint256 public packPrice = 0.5 ether;

    // Tiger Rarity
    // 0 - Common
    // 1 - Uncommon
    // 2 - Rare
    // 3 - Legendary
    // 4 - Exotic/Honorary

    mapping(address => bool) public onWhiteList;

    // array from which the ids will be selected
    uint256[][5] public tokenIds;

    uint256[] public tokenIdsLeftPerRarity = [81, 70, 38, 15, 7];

    // events
    event boughtTiger(address to, uint256 tokenId);
    event boughtPack(address to, uint256[] tokenIds);

    constructor(address _factoryAddress, address _treasuryAddress) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);

        bckEthFactory = IBCKEthFactory(_factoryAddress);
        treasury = _treasuryAddress;

        tokenIds[0] = [
            1,
            2,
            3,
            4,
            5,
            6,
            7,
            8,
