            213
        ];
        tokenIds[4] = [214, 215, 218, 219, 220, 221, 222];
    }

    // External functions
    // Call
    function totalTokensLeft() public view returns (uint256) {
        return
            tokenIdsLeftPerRarity[0] +
            tokenIdsLeftPerRarity[1] +
            tokenIdsLeftPerRarity[2] +
            tokenIdsLeftPerRarity[3] +
            tokenIdsLeftPerRarity[4];
    }

    function isWhitelisted(address _user) external view returns (bool) {
        return onWhiteList[_user];
    }

    // Tx
    function buyTiger() external payable {
        require(mintOpen, "Tiger Mint is not currently open");
        if (mintWhitelist) {
            require(
                onWhiteList[msg.sender] == true,
                string(
                    abi.encodePacked(
                        "Address ",
                        Strings.toHexString(uint160(msg.sender), 20),
                        " is not whitelisted"
                    )
                )
            );
        }
        require(msg.value == (mintPrice), "Invalid pay amount");
        require(totalTokensLeft() > 0, "Tigers are sold out");
        // if whitelist is open people will be removed from the whitelist after buying
        if (mintWhitelist) {
            onWhiteList[msg.sender] = false;
        }

        uint256 random = _generateRandomNumber(totalTokensLeft());
        uint256 rarity = _returnRarityNumber(random);
        uint256 tokenIdToBeMinted = _findTokenIdAndReplace(random, rarity);

        // transfer funds from contract to treasury and mint the token
        payable(treasury).transfer(address(this).balance);
        bckEthFactory.mintSpecific(tokenIdToBeMinted, msg.sender, ipfsLocation);
        emit boughtTiger(msg.sender, tokenIdToBeMinted);
    }

    function buyPack() external payable {
        require(mintOpen, "Tiger Mint is not currently open");
        if (mintWhitelist) {
            require(
                onWhiteList[msg.sender] == true,
                string(
                    abi.encodePacked(
                        "Address ",
                        Strings.toHexString(uint160(msg.sender), 20),
                        " is not whitelisted"
                    )
                )
            );
            // if whitelist is open people will be removed from the whitelist after buying
            onWhiteList[msg.sender] = false;
        }
        require(msg.value == packPrice, "Invalid pay amount");
        require(totalTokensLeft() > 4, "Not enough tigers to buy a pack");
        require(_rarePlusLeft() > 0, "No more rares left");

        uint256[] memory mintTokenArray = new uint256[](5);

        for (uint256 i = 0; i < 5; i++) {
            uint256 random = _generateRandomNumber(totalTokensLeft());
            // we mint the rare+ on the first round to make sure we don't run out of rares during the random mints and have to cancel the mint unnecessary
            if (i == 0) {
                // we will generate a random number with the maximum number of the amount of _rarePlusLeft
                uint256 preRandom = _generateRandomNumber(_rarePlusLeft());
                // then we will add the total amount of tokens from common and uncommon to get the right number for the rest of the function
                random =
                    preRandom +
                    tokenIdsLeftPerRarity[0] +
                    tokenIdsLeftPerRarity[1];
            }
            uint256 rarity = _returnRarityNumber(random);
            mintTokenArray[i] = _findTokenIdAndReplace(random, rarity);
        }
        // transfer funds from contract to treasury and mint the token
        payable(treasury).transfer(address(this).balance);
        bckEthFactory.mintSpecificBatch(
            mintTokenArray,
            msg.sender,
            ipfsLocation
        );
        emit boughtPack(msg.sender, mintTokenArray);
    }

    // Admin functions
    function setTokenIdsPerRarity(uint256[] memory _tokenIds, uint256 _rarity)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        tokenIds[_rarity] = _tokenIds;
        if (_tokenIds.length != tokenIdsLeftPerRarity[_rarity])
            tokenIdsLeftPerRarity[_rarity] = _tokenIds.length;
    }

    function setWhiteListStatus(address _user, bool _whitelisted)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        onWhiteList[_user] = _whitelisted;
    }

    function batchWhiteList(address[] memory _users, bool _whitelisted)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        for (uint256 i = 0; i < _users.length; i++) {
            onWhiteList[_users[i]] = _whitelisted;
        }
    }

    function setMintPrice(uint256 _priceInWei)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        mintPrice = _priceInWei;
    }

    function setPackPrice(uint256 _priceInWei)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        packPrice = _priceInWei;
    }

    function setTreasuryAddress(address _treasury)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        treasury = _treasury;
    }

    function setMintWhitelist(bool _withWhitelist)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        mintWhitelist = _withWhitelist;
    }

    function setIpfsLocation(string memory _ipfsLocation)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        ipfsLocation = _ipfsLocation;
    }

    function setMintOpen(bool _isOpen) external onlyRole(DEFAULT_ADMIN_ROLE) {
        mintOpen = _isOpen;
    }

    // Internal functions
    function _findTokenIdAndReplace(uint256 _randomNumber, uint256 _rarity)
        internal
        returns (uint256)
    {
        // this will be used to subtract from _randomNumber so we find the right index in the _rarity
        uint256 tokensWithLowerRarityLeft = 0;
        for (uint256 i = 0; i < _rarity; i++) {
            tokensWithLowerRarityLeft =
                tokensWithLowerRarityLeft +
                tokenIdsLeftPerRarity[i];
        }
        uint256 indexOnRarityArray = _randomNumber - tokensWithLowerRarityLeft;
        // extract tokenToBeMinted before it gets replaced
        uint256 tokenToBeMinted = tokenIds[_rarity][indexOnRarityArray];
        // if indexOnRarityArray is the last token in the array there is no reason to replace it with itself
        if (indexOnRarityArray < tokenIdsLeftPerRarity[_rarity] - 1) {
            // replace the token on the index with the last token so all mintable tokens are still within range
            tokenIds[_rarity][indexOnRarityArray] = tokenIds[_rarity][
                tokenIdsLeftPerRarity[_rarity] - 1
            ];
        }
        // tokenIdsLeft of that _rarity is now 1 less
        tokenIdsLeftPerRarity[_rarity] = tokenIdsLeftPerRarity[_rarity] - 1;
        return tokenToBeMinted;
    }

    function _rarePlusLeft() internal view returns (uint256) {
        return
            tokenIdsLeftPerRarity[2] +
            tokenIdsLeftPerRarity[3] +
            tokenIdsLeftPerRarity[4];
    }

    function _returnRarityNumber(uint256 _generatedNumber)
        internal
        view
        returns (uint256)
    {
        if (_generatedNumber < tokenIdsLeftPerRarity[0]) return 0;
        if (
            _generatedNumber <
            tokenIdsLeftPerRarity[0] + tokenIdsLeftPerRarity[1]
        ) return 1;
        if (
            _generatedNumber <
            tokenIdsLeftPerRarity[0] +
                tokenIdsLeftPerRarity[1] +
                tokenIdsLeftPerRarity[2]
        ) return 2;
        if (
            _generatedNumber <
            tokenIdsLeftPerRarity[0] +
                tokenIdsLeftPerRarity[1] +
                tokenIdsLeftPerRarity[2] +
                tokenIdsLeftPerRarity[3]
        ) return 3;
        return 4;
    }

    function _generateRandomNumber(uint256 _max)
        internal
        view
        returns (uint256)
    {
        uint256 num = uint256(
            keccak256(
                abi.encodePacked(
                    totalTokensLeft() + block.number,
                    msg.sender,
                    block.timestamp
                )
            )
        ) % _max;
        return num;
    }
}
