            let initializedSlot := _initialized.slot

            if iszero(
                and(
                    eq(sload(initializingSlot), 1), // !_initializing
                    lt(sload(initializedSlot), version) // _initialized < version
                )
            ) {
                // throw the `AlreadyInitialized` error
                mstore(0x00, 0x0dc149f0)
                revert(0x1c, 0x04)
