    "contracts/vault/libs/Asset.sol": {
      "content": "// SPDX-License-Identifier: LZBL-1.1
// Copyright 2023 LayerZero Labs Ltd.
// You may obtain a copy of the License at
// https://github.com/LayerZero-Labs/license/blob/main/LICENSE-LZBL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Governance} from "./Governance.sol";

library Asset {
    using SafeERC20 for IERC20;
    using Governance for Governance.Info;

    struct Info {
        IERC20 token;
        bool registered;
        bool enabled; // to allow minting
        uint usdvToTokenRate;
        // all the following values are in token amount
        uint collateralized;
    }

    error TokenDecimalInvalid(uint provided, uint min);
    error Disabled();
    error AlreadyRegistered();
    error SlippageTooHigh();
    error NotRegistered();

    modifier onlyRegistered(Info storage _self) {
        if (!_self.registered) revert NotRegistered();
        _;
    }

    function initialize(Info storage _self, address _token, uint8 _shareDecimals) internal {
        if (_self.registered) revert AlreadyRegistered();

        // set token
        _self.token = IERC20(_token);
        _self.enabled = true;
        _self.registered = true;

        // set conversion rate
        uint8 tokenDecimals = IERC20Metadata(_token).decimals();
        if (_shareDecimals > tokenDecimals) {
            revert TokenDecimalInvalid(tokenDecimals, _shareDecimals);
        }
        _self.usdvToTokenRate = 10 ** (tokenDecimals - _shareDecimals);
    }

    // @dev if _newFund is true, _from must approve the token transfer
    // @dev if internal mint such as turning yield into collateral, _newFund should be false
    function credit(Info storage _self, address _from, uint64 _usdvAmount, bool _newFund) internal {
        // assert the asset is enabled
        if (!_self.enabled) revert Disabled();

        uint tokenAmount = _usdv2Token(_self, _usdvAmount);
        _self.collateralized += tokenAmount;

        if (_newFund) {
            _self.token.safeTransferFrom(_from, address(this), tokenAmount);
        }
    }

    function redeem(
        Info storage _self,
        Governance.Info storage _govInfo,
        address _receiver,
        uint64 _usdvAmount,
        uint64 _minUsdvAmount
    ) internal onlyRegistered(_self) returns (uint amountAfterFee) {
        uint tokenAmount = _usdv2Token(_self, _usdvAmount);
        _self.collateralized -= tokenAmount;
        // pay redemption fee
        amountAfterFee = _govInfo.payRedemptionFee(_self.token, tokenAmount);
        if (amountAfterFee < _usdv2Token(_self, _minUsdvAmount)) revert SlippageTooHigh();

        // transfer collateral to receiver
        _self.token.safeTransfer(_receiver, amountAfterFee);
    }

    function setEnabled(Info storage _self, bool _enabled) internal onlyRegistered(_self) {
        _self.enabled = _enabled;
    }

    // ========================= View =========================
    /// @return reward in usdv
    function distributable(Info storage _self) internal view returns (uint) {
        uint rebasedFunds = _self.token.balanceOf(address(this)) - _self.collateralized;
        return rebasedFunds / _self.usdvToTokenRate;
    }

    function redeemOutput(
        Info storage _self,
        Governance.Info storage _govInfo,
        uint64 _shares
    ) internal view returns (uint) {
        uint tokenAmount = _usdv2Token(_self, _shares);
        return tokenAmount - _govInfo.getRedemptionFee(tokenAmount);
    }

    // ========================= Internal =========================
    function _usdv2Token(Info storage _self, uint64 _shares) private view returns (uint) {
        return _shares * _self.usdvToTokenRate;
    }
}
"
    },
    "contracts/vault/libs/Governance.sol": {
      "content": "// SPDX-License-Identifier: LZBL-1.1
// Copyright 2023 LayerZero Labs Ltd.
// You may obtain a copy of the License at
// https://github.com/LayerZero-Labs/license/blob/main/LICENSE-LZBL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Role} from "../interfaces/IVaultManager.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

library Governance {
    using SafeERC20 for IERC20;

    uint16 internal constant ONE_HUNDRED_PERCENT = 10000; // 100%
    uint16 internal constant RESERVE_FEE_BPS_CAP = 300; // 3%
    // A % fee is applicable on yield distribution for the vault service.
    uint16 internal constant REDEMPTION_FEE_BPS_CAP_MIN = 10; //0.1%

    struct Fee {
        uint16 bps;
        uint16 cap;
    }

    struct Info {
        mapping(Role => Fee) fees;
        mapping(Role => address) roles;
        uint operatorLastPing; // timestamp
    }

    event PaidRedemptionFee(address indexed to, uint amount);

    // ========================= Operator =========================
    function ping(Info storage _self) internal {
        _self.operatorLastPing = block.timestamp;
    }

    // ========================= Fees =========================
    function getRedemptionFee(Info storage _self, uint _amount) internal view returns (uint fee) {
        return (_amount * _self.fees[Role.FOUNDATION].bps) / ONE_HUNDRED_PERCENT;
    }

    function payRedemptionFee(Info storage _self, IERC20 _token, uint _amount) internal returns (uint) {
        uint fee = getRedemptionFee(_self, _amount);
        // pay redemption fee to operator
        address _to = _self.roles[Role.OPERATOR];
        _token.safeTransfer(_to, fee);
        emit PaidRedemptionFee(_to, fee);

        return _amount - fee;
    }
}
"
    },
    "contracts/vault/libs/Vault.sol": {
      "content": "// SPDX-License-Identifier: LZBL-1.1
// Copyright 2023 LayerZero Labs Ltd.
// You may obtain a copy of the License at
// https://github.com/LayerZero-Labs/license/blob/main/LICENSE-LZBL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/SafeCast.sol";

library Vault {
    using SafeCast for uint192;

    struct ColorBook {
        uint64 shares;
        uint64 debt;
        uint64 rewards;
    }

    struct Info {
        uint192 accumRewardPerShare; //multiplied by RewardMultiplier
        uint64 totalShares;
        mapping(uint32 color => ColorBook book) colorBooks;
    }

    uint192 internal constant REWARD_MULTIPLIER = 1e18;
    uint64 internal constant INT64_MAX = uint64(type(int64).max); // because we use int64 as delta

    error Overflow();
    error InsufficientShares();

    event WithdrewPending(uint32 indexed color, uint64 pendingReward, uint64 newReward);
    event RemovedShares(uint32 indexed color, uint64 sharesDelta, uint64 newReward);
    event AddedShares(uint32 indexed color, uint64 sharesDelta, uint64 newReward);
    event AddedRewards(uint32 indexed color, uint64 amount);

    function netShareValue(Info storage _self, uint64 _shares) internal view returns (uint64) {
        uint192 nav = (_self.accumRewardPerShare * _shares) / REWARD_MULTIPLIER;
        return nav.toUint64();
    }

    function getPendingReward(Info storage _self, uint32 _color) internal view returns (uint64) {
        ColorBook memory book = _self.colorBooks[_color];
        uint64 nav = netShareValue(_self, book.shares);
        return nav - book.debt;
    }

    /// @dev add shares
    function addShares(Info storage _self, uint32 _color, uint64 _sharesDelta) internal {
        ColorBook memory book = _self.colorBooks[_color];

        uint64 beforeShares = book.shares;
        book.shares += _sharesDelta;
        _self.totalShares += _sharesDelta;
        if (_self.totalShares > INT64_MAX) revert Overflow();

        // settle if any pending
        uint64 newReward = settle(_self, book, beforeShares, book.shares);

        // persist the state
        _self.colorBooks[_color] = book;

        emit AddedShares(_color, _sharesDelta, newReward);
    }

    /// @dev remove shares
    function removeShares(Info storage _self, uint32 _color, uint64 _sharesDelta) internal {
        ColorBook memory book = _self.colorBooks[_color];

        uint64 beforeShares = book.shares;
        if (_sharesDelta > book.shares) revert InsufficientShares();

        book.shares -= _sharesDelta;
        _self.totalShares -= _sharesDelta;

        // settle if any pending
        uint64 newReward = settle(_self, book, beforeShares, book.shares);

        // persist the state
        _self.colorBooks[_color] = book;

        emit RemovedShares(_color, _sharesDelta, newReward);
    }

    /// @dev there may be rounding error that the usdv vault balance is not enough to pay the reward
    function withdrawPending(Info storage _self, uint32 _color, uint64 _cap) internal returns (uint64 pendingReward) {
        ColorBook memory book = _self.colorBooks[_color];
        // settle if any pending
        uint64 newReward = settle(_self, book, book.shares, book.shares);

        // check if the reward is enough to pay
        pendingReward = _cap >= book.rewards ? book.rewards : _cap;
        book.rewards -= pendingReward;

        // persist the state
        _self.colorBooks[_color] = book;

        emit WithdrewPending(_color, pendingReward, newReward);
    }

    /// @dev store the pending reward
    /// @dev steam payment is gas costly and it has some failure surfaces.
    function settle(
        Info storage _self,
        ColorBook memory _book,
        uint64 _beforeShares,
        uint64 _afterShares
    ) private view returns (uint64 newReward) {
        // harvest the outstanding into stored
        newReward = netShareValue(_self, _beforeShares) - _book.debt;
        _book.rewards += newReward;
        // update the debt
        _book.debt = netShareValue(_self, _afterShares);
    }

    function addReward(Info storage _self, uint _rewardInUSDV) internal {
        // we need to calculate the accumulative reward in shares to compound the yields
        _self.accumRewardPerShare += (uint192(_rewardInUSDV) * REWARD_MULTIPLIER) / _self.totalShares;
    }

    // function to add to store
    function addRewardByColor(Info storage _self, uint32 _color, uint64 _amount) internal {
        _self.colorBooks[_color].rewards += _amount;
        emit AddedRewards(_color, _amount);
    }
}
"
    },
    "hardhat-deploy/solc_0.8/proxy/Proxied.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

abstract contract Proxied {
    /// @notice to be used by initialisation / postUpgrade function so that only the proxy's admin can execute them
    /// It also allows these functions to be called inside a contructor
    /// even if the contract is meant to be used without proxy
    modifier proxied() {
        address proxyAdminAddress = _proxyAdmin();
        // With hardhat-deploy proxies
        // the proxyAdminAddress is zero only for the implementation contract
        // if the implementation contract want to be used as a standalone/immutable contract
        // it simply has to execute the `proxied` function
        // This ensure the proxyAdminAddress is never zero post deployment
        // And allow you to keep the same code for both proxied contract and immutable contract
        if (proxyAdminAddress == address(0)) {
            // ensure can not be called twice when used outside of proxy : no admin
            // solhint-disable-next-line security/no-inline-assembly
            assembly {
                sstore(
                    0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103,
                    0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF
                )
            }
        } else {
            require(msg.sender == proxyAdminAddress);
        }
        _;
    }

    modifier onlyProxyAdmin() {
        require(msg.sender == _proxyAdmin(), "NOT_AUTHORIZED");
        _;
    }

    function _proxyAdmin() internal view returns (address ownerAddress) {
        // solhint-disable-next-line security/no-inline-assembly
        assembly {
            ownerAddress := sload(0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103)
        }
    }
}
"
