/**
 *  AstrCoin token contract. Implements
 */
contract AstrCoin is StandardToken, Ownable {
  string public constant name     = "AstrCoin";
  string public constant symbol   = "ASTR";
  uint8 public  constant decimals = 4;    // 4 decimal places should be enough in general

  uint256 public constant INITIAL_SUPPLY = 200000000 * (10 ** uint256(decimals)); // 200 mill
  bool public mintingFinished            = false;

  event Mint(address indexed to, uint256 amount);
  event MintFinished();

  mapping (address => bool) public frozenAccount;
    event FrozenFunds(address target, bool frozen);

   /**
     * [freezeAccount description]
     */
    function freezeAccount(address target, bool freeze) onlyOwner public{
        frozenAccount[target] = freeze;
        FrozenFunds(target, freeze);
    }


  modifier canMint() {
    require(!mintingFinished);
    _;
  }

  // Constructor
  function AstrCoin()  {
      totalSupply          = INITIAL_SUPPLY;
      balances[msg.sender] = totalSupply; // Send all tokens to owner
  }
  /**
   *  Burn away the specified amount of AstrCoin tokens
   */
  function burn(uint _value) onlyOwner public returns (bool) {
    balances[msg.sender] = balances[msg.sender].sub(_value);
    totalSupply          = totalSupply.sub(_value);
    Transfer(msg.sender, 0x0, _value);
    return true;
  }

  /**
   * @dev Function to mint tokens
   * @param _to The address that will receive the minted tokens.
   * @param _amount The amount of tokens to mint.
   * @return A boolean that indicates if the operation was successful.
   */
  function mint(address _to, uint256 _amount) onlyOwner canMint public returns (bool) {
    totalSupply = totalSupply.add(_amount);
    balances[_to] = balances[_to].add(_amount);
    Mint(_to, _amount);
    Transfer(address(0), _to, _amount);
    return true;
  }



  /**
   * @dev Function to stop minting new tokens.
   * @return True if the operation was successful.
   */
  function finishMinting() onlyOwner public returns (bool) {
    mintingFinished = true;
    MintFinished();
    return true;
  }
}
