    "contracts/RNFTVesting.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./interfaces/ISmartWalletWhitelist.sol";
import {ROOT} from "./ROOT.sol";
import {RNFTV2} from "./RNFTV2.sol";
import "hardhat/console.sol";

/// @title ROOT Vesting Contract
/// @notice This contract is used for vesting ROOT tokens for the team and other stakeholders
/// @author 0xGrayMan

contract RNFTVesting is Ownable, ReentrancyGuard {
    ROOT public root;

    uint256 public tge;

    uint256 public cliff;

    uint256 public duration;

    uint256 public bpUpfrontPostCliff;

    uint256 public withdrawnTokens;

    address public smartWalletChecker;

    RNFTV2 public rnftv2;

    uint256 public beneficiaryTokens = 2000000000000000000000;

    mapping(uint256 => uint256) public withdrawnTokensByBeneficiary;

    event TokensReleased(uint256 tokenId, uint256 amount);

    constructor(
        address _root,
        uint256 _cliff,
        uint256 _duration,
        uint256 _bpUpfrontPostCliff,
        address _smartWalletChecker,
        address _rnftv2
    ) {
        root = ROOT(_root);
        cliff = _cliff;
        duration = _duration;
        bpUpfrontPostCliff = _bpUpfrontPostCliff;
        smartWalletChecker = _smartWalletChecker;
        rnftv2 = RNFTV2(_rnftv2);
    }

    modifier onlyWhitelisted() {
        if (tx.origin != msg.sender) {
            require(
                address(smartWalletChecker) != address(0),
                "Not whitelisted"
            );
            require(
                ISmartWalletWhitelist(smartWalletChecker).check(msg.sender),
                "Not whitelisted"
            );
        }
        _;
    }

    function setTGE(uint256 _tge) external onlyOwner {
        require(tge == 0, "ROOTVesting : TGE has already been set");
        require(
            _tge > block.timestamp,
            "ROOTVesting : TGE should be in the future"
        );
        tge = _tge;
    }

    function setBeneficiaryTokens(
        uint256 _beneficiaryTokens
    ) external onlyOwner {
        beneficiaryTokens = _beneficiaryTokens;
    }

    function releaseTokens(
        uint256 _tokenId
    ) external nonReentrant onlyWhitelisted {
        require(
            msg.sender == rnftv2.ownerOf(_tokenId) || msg.sender == owner(),
            "ROOTVesting : Only nft holder or owner can release tokens"
        );
        _releaseTokens(_tokenId);
    }

    function _releaseTokens(uint256 _tokenId) internal {
        require(
            msg.sender == rnftv2.ownerOf(_tokenId) || msg.sender == owner(),
            "ROOTVesting : Only nft holder or owner can release tokens"
        );
        uint256 totalClaimable = _totalClaimable();
        uint256 toClaim = totalClaimable -
            withdrawnTokensByBeneficiary[_tokenId];
        require(toClaim > 0, "ROOTVesting : No tokens are due");
        withdrawnTokens += toClaim;
        withdrawnTokensByBeneficiary[_tokenId] = totalClaimable;
        root.mint(msg.sender, toClaim);
        emit TokensReleased(_tokenId, toClaim);
    }

    function releaseTokensForUser() external nonReentrant onlyWhitelisted {
        uint256[] memory tokens = rnftv2.getUserTokens(msg.sender);
        uint256 i = 0;
        for (i = 0; i < tokens.length; i++) {
            _releaseTokens(tokens[i]);
        }
    }

    function _totalClaimable() internal view returns (uint256) {
        if (block.timestamp < tge + cliff) {
            return 0;
        } else if (block.timestamp > tge + cliff + duration) {
            return beneficiaryTokens;
        } else {
            return
                ((beneficiaryTokens * bpUpfrontPostCliff) / 10000) +
                ((beneficiaryTokens *
                    (block.timestamp - tge - cliff) *
                    (10000 - bpUpfrontPostCliff)) / (duration * 10000));
        }
    }

    function getTotalClaimable() external view returns (uint256) {
        return _totalClaimable();
    }

    function totalClaimableUnclaimed(
        uint256 _tokenId
    ) external view returns (uint256) {
        return _totalClaimable() - withdrawnTokensByBeneficiary[_tokenId];
    }

    function totalClaimableUnclaimedForUser(
        address _holder
    ) external view returns (uint256 _total) {
        uint256[] memory tokens = rnftv2.getUserTokens(_holder);
        uint256 i = 0;
        for (i = 0; i < tokens.length; i++) {
            _total += (_totalClaimable() -
                withdrawnTokensByBeneficiary[tokens[i]]);
        }
    }

    function withdrawnTokensByUser(
        address _holder
    ) external view returns (uint256 _total) {
        uint256[] memory tokens = rnftv2.getUserTokens(_holder);
        uint256 i = 0;
        for (i = 0; i < tokens.length; i++) {
            _total += withdrawnTokensByBeneficiary[tokens[i]];
        }
    }
}
"
    },
    "contracts/ROOT.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {OFTWithFee} from "@layerzerolabs/solidity-examples/contracts/token/oft/v2/fee/OFTWithFee.sol";

contract ROOT is Ownable, OFTWithFee {
    using SafeERC20 for IERC20;
    event MintAllowanceSet(address indexed minter, uint256 amount);

    mapping(address => uint256) public mintAllowance;

    mapping(address => bool) public isBlacklisted;

    uint256 public maxSupply;

    constructor(
        address lzEndpoint,
        uint256 _initialSupply,
        uint256 _maxSupply,
        address _initialSupplyReceiver
    ) OFTWithFee("ROOT", "ROOT", 9, lzEndpoint) {
        require(
            _initialSupply <= _maxSupply,
            "Initial supply should be lower than max supply"
        );
        require(
            _initialSupplyReceiver != address(0),
            "Invald receiver address"
        );
        maxSupply = _maxSupply;
        _mint(_initialSupplyReceiver, _initialSupply);
    }

    function sendFrom(
        address _from,
        uint16 _dstChainId,
        bytes32 _toAddress,
        uint _amount,
        uint _minAmount,
        LzCallParams calldata _callParams
    ) public payable virtual override {
        require(!isBlacklisted[msg.sender], "ROOT: sender is blacklisted");
        require(!isBlacklisted[_from], "ROOT: sender is blacklisted");
        super.sendFrom(
            _from,
            _dstChainId,
            _toAddress,
            _amount,
            _minAmount,
            _callParams
        );
    }

    function sendAndCall(
        address _from,
        uint16 _dstChainId,
        bytes32 _toAddress,
        uint _amount,
        uint _minAmount,
        bytes calldata _payload,
        uint64 _dstGasForCall,
        LzCallParams calldata _callParams
    ) public payable virtual override {
        require(!isBlacklisted[msg.sender], "ROOT: sender is blacklisted");
        require(!isBlacklisted[_from], "ROOT: sender is blacklisted");
        super.sendAndCall(
            _from,
            _dstChainId,
            _toAddress,
            _amount,
            _minAmount,
            _payload,
            _dstGasForCall,
            _callParams
        );
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(!isBlacklisted[msg.sender], "ROOT: sender is blacklisted");
        require(!isBlacklisted[from], "ROOT: sender is blacklisted");
        require(!isBlacklisted[to], "ROOT: recipient is blacklisted");

        super._transfer(from, to, amount);
    }

    function mint(address to, uint256 amount) external {
        //only addresses sufficient allowances can mint
        require(
            mintAllowance[msg.sender] >= amount,
            "ROOT: insufficient mint allowance"
        );
        require(!isBlacklisted[msg.sender], "ROOT: sender is blacklisted");
        require(!isBlacklisted[to], "ROOT: recipient is blacklisted");
        mintAllowance[msg.sender] -= amount;
        _mint(to, amount);
    }

    function _mint(address account, uint256 amount) internal override {
        require(totalSupply() + amount <= maxSupply, "Max supply exceed");
        require(!isBlacklisted[msg.sender], "ROOT: sender is blacklisted");
        require(!isBlacklisted[account], "ROOT: recipient is blacklisted");
        super._mint(account, amount);
    }

    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
    }

    function setBlacklistStatus(
        address account,
        bool status
    ) external onlyOwner {
        isBlacklisted[account] = status;
    }

    function setMintAllowance(
        address minter,
        uint256 amount
    ) external onlyOwner {
        require(!isBlacklisted[minter], "ROOT: minter is blacklisted");
        require(
            (amount == 0) || (mintAllowance[minter] == 0),
            "Approve mint from non-zero to non-zero allowance"
        );
        mintAllowance[minter] = amount;
        emit MintAllowanceSet(minter, amount);
    }

    function incrementMintAllowance(
        address minter,
        uint256 amount
    ) external onlyOwner {
        mintAllowance[minter] += amount;
        emit MintAllowanceSet(minter, mintAllowance[minter]);
    }

    function decrementMintAllowance(
        address minter,
        uint256 amount
    ) external onlyOwner {
        require(
            mintAllowance[minter] >= amount,
            "ROOT: decrement amount exceeds mint allowance"
        );
        mintAllowance[minter] -= amount;
        emit MintAllowanceSet(minter, mintAllowance[minter]);
    }

    function emergencyWithdraw(
        address token,
        address to,
        uint256 amount
    ) external onlyOwner {
        IERC20(token).safeTransfer(to, amount);
    }
}
"
    },
