            emit Claim(account, amount);
    		return true;
    	}

    	return false;
    }

}


/// @title CBDI Staking smartcontract 
contract CBDIStaking is Ownable2Step, ReentrancyGuard {
 using SafeERC20 for IERC20;
 
 ///Custom errors
 error AmountIsHigherThanStaked();
 error ZeroAmount();
 error ZeroAddress();
 error MinCooldownPeriodBetweenTwoClaimsIsNotPassedYet();
 error ParamsNotInRange();
 error NoRewardsAvailableYet();
 error CannotClaimStakingToken();
 error MAX_FEES_LIMIT_EXCEEDS();
 /// reward manager smart contract
 CBDIRewardManager public rewardDistributer;
 /// fees wallet
 address public feeWallet;
 /// CBDI token
 IERC20 public token;
 
 /// penalty fees
 uint256 public pentaltyFee = 10;
 /// min locking period for staked amount
 uint256 public minLockingPeriod =  30 days;
 /// min cooldown b/w two consequtive claims
 uint256 public cooldownPeriod = 30 days;
 /// no. of stakers
 uint256 public totalStakers;
 /// mapping for total rewards earned by user
 mapping(address => uint256) public totalRewardEarned;
 /// mapping for keep track of last deposit time
 mapping(address => uint256) public lastDepositTime;
 /// mapping to keep track of last claim time
 mapping(address => uint256) public lastClaimTime;
 /// mapping to check if a staker already exist
 mapping(address => bool) private isStaker;
 
 
 /// set staking token and create the staking smartcontract
 /// create reward distributer contract, which will manage 
 /// rewards
 constructor () {
    token = IERC20(0x40907F43f8237E04f2e94Fa1d4793F8317323EB0);
    rewardDistributer = new CBDIRewardManager(0x40907F43f8237E04f2e94Fa1d4793F8317323EB0);
    feeWallet = msg.sender;
 }
 

 /// @dev stake CBDI tokens
 /// @param amount: no. of tokens that a user want to stake
 /// Requirements -
 /// Amount must be greater than zero
 function stake (uint256 amount) external nonReentrant {
    if(amount == 0){
        revert ZeroAmount();
    }
    token.safeTransferFrom(msg.sender, address(this), amount);
    if(!isStaker[msg.sender]){
        isStaker[msg.sender] = true;
        totalStakers = totalStakers + 1;
    }
    rewardDistributer._staked(msg.sender, amount);
    lastClaimTime[msg.sender] = block.timestamp;
    lastDepositTime[msg.sender] = block.timestamp;

 }
 

 /// @dev unstake the tokens
 /// @param amount: no. of CBDI tokens that a user want to unstake
 /// Requirements -
 /// User must have amount staked and input amount should not be greator than 
 /// actual staked amount
 /// There is penalty fees if unstaked before lockup period.
 function unstake(uint256 amount) external nonReentrant {
    if(amount == 0){
        revert ZeroAmount();
    }
    uint256 staked = rewardDistributer.balanceOf(msg.sender);
    if(amount > staked){
        revert AmountIsHigherThanStaked();
    }
    rewardDistributer._unstaked(msg.sender, amount);
    
    if(rewardDistributer.balanceOf(msg.sender) == 0){
        isStaker[msg.sender] = false;
        totalStakers = totalStakers - 1;
    }
    uint256 fees = 0;
    if(block.timestamp - lastDepositTime[msg.sender] < minLockingPeriod){
         fees = (amount * pentaltyFee) / 100;
    }
    if(fees > 0){
        token.safeTransfer(feeWallet, fees);
    }
    token.safeTransfer(msg.sender,  amount - fees);

 }
 
 /// @notice user can claim there reward 
 /// Requirements -
 /// rewards amount must be greator than 0
 /// claim time difference b/w two claims must be greator than cooldownPeriod
 function claim () external {
    if(block.timestamp - lastClaimTime[msg.sender] < cooldownPeriod){
        revert MinCooldownPeriodBetweenTwoClaimsIsNotPassedYet();
    }
    uint256 availableToClaim = getPendingEarnings(msg.sender);
    if(availableToClaim == 0){
        revert NoRewardsAvailableYet();
    }
    totalRewardEarned[msg.sender] += availableToClaim;
    lastClaimTime[msg.sender] = block.timestamp;
    rewardDistributer.processAccount(payable(msg.sender));
 }
 

 /// @dev inject rewards to reward distributer contract
 /// @param amount: number of tokens to add a reward
 function injectRewards(uint256 amount) external onlyOwner {
    token.safeTransferFrom(msg.sender, address(rewardDistributer), amount);
    rewardDistributer.injectRewards(amount);
 }
 
 /// @dev claim other erc20, if accidently sent by someone
 /// @param otherToken: other token address
 /// @param amount: amount to rescue
 function claimOtherERC20 (address otherToken, uint256 amount) external onlyOwner {
    if(otherToken == address(token)){
        revert CannotClaimStakingToken();
    }
    IERC20(otherToken).safeTransfer(owner(), amount);
 }
 
 /// @dev udpate penalty fee
 /// @param newFee: new fees for penalty
 /// Requirements -
 /// fees must be less than equals to 20
 function updatePenaltyFee (uint256 newFee) external onlyOwner {
    if(newFee > 20){
        revert MAX_FEES_LIMIT_EXCEEDS();
    }
    pentaltyFee = newFee;
 }
 
 /// @dev update fees wallet
 /// @param newFeeWallet: new fees wallet address
 function updateFeesWallet (address newFeeWallet) external onlyOwner {
   if(newFeeWallet == address(0)){
    revert ZeroAddress();
   } 
   feeWallet = newFeeWallet;
 }
 
 /// @dev update staking locking period and min cooldown period b/w two consequitve claims
 /// @param minStakePeriod - new min lockup period time in seconds
 /// @param minRewardCooldown - new cooldown time in seconds
 function updateMinStakeAndRewardCooldown(uint256 minStakePeriod, uint256 minRewardCooldown) external onlyOwner {
    if(minStakePeriod > 180 days || minRewardCooldown > 30 days){
        revert ParamsNotInRange();
    }
    minLockingPeriod = minStakePeriod;
    cooldownPeriod = minRewardCooldown;
 }
 
 /// @notice get available amount to claim for a user
 /// @param user: user address to check available earnings
 function getPendingEarnings (address user) public view returns (uint256){
    return rewardDistributer.withdrawableDividendOf(user);
 }

 /// @notice returns total tokens staked by user
 function getStakedByUser(address user) public view returns (uint256){
   return  rewardDistributer.balanceOf(user);
 }
 /// @notice return total tokens staked in the pool
 function getTotalStakedInPool () public view returns (uint256){
    return token.balanceOf(address(this));
 }
 
 /// @notice total tokens injected by owner till date as a reward
 function totalRewardDistributed() public view returns (uint256) {
    return rewardDistributer.totalDividendsDistributed();
 }

}
