// File contracts/ParimutuelContract.sol

// SPDX-License-Identifier: MIT

pragma solidity ^0.8.18;

using SafeERC20 for IERC20;

contract ParimutuelContract is ReentrancyGuard, Ownable {
    uint256 public numberOfOutcomes;
    mapping(address => mapping(uint256 => uint256)) public betAmounts; // bettor => outcome => amount
    mapping(uint256 => uint256) public totalPerOutcome; // outcome => total amount per outcome
    uint256 public total; // total amount across all pools
    uint256 public winningOutcome;
    uint256 public contractFees; // total fees collected by the contract

    uint256 public feeRate; // fee rate in basis points (1 basis point = 0.01%)
    uint256 public rebateRate; // rebate rate in basis points (1 basis point = 0.01%)

    uint256 public constant MINIMUM_BET_SIZE = 1e6; // 1 USDC
    uint256 public constant BPS = 10000; // basis points
    uint256 public constant SCALE_FACTOR = 1; // scaling factor for calculating the amount to be paid out
    IERC20 public usdc;

    // Fee incentives
    mapping(uint256 => mapping(address => bool)) public earlyBettors; // outcome => bettor => isEarlyBettor
    mapping(uint256 => uint256) public earlyBettorsCount; // outcome => number of early bettors
    mapping(uint256 => mapping(address => uint256)) // outcome => early bettor => amount contributed
        public earlyBettorContributions;
    uint256 public rebatePool = 0; // total amount of rebates available

    enum States {
        Open,
        Closed,
        Resolved,
        Cancelled
    }

    States public state = States.Open;

    receive() external payable {
        revert("Do not send Ether directly");
    }

    constructor(
        uint256 _numberOfOutcomes,
        uint256 _feeRate,
        uint256 _rebateRate,
        IERC20 _usdc
    ) {
        require(_numberOfOutcomes > 1, "Invalid number of outcomes");
        require(_feeRate <= BPS, "Invalid fee rate"); // fee rate must be between 0 and 10000 basis points
        require(_rebateRate <= BPS, "Invalid rebate rate"); // fee rate must be between 0 and 10000 basis points
        numberOfOutcomes = _numberOfOutcomes;
        winningOutcome = type(uint256).max;
        total = 0;
        feeRate = _feeRate;
        rebateRate = _rebateRate;
        usdc = _usdc;
    }

    function bet(uint256 outcome, uint256 amount) public nonReentrant {
        require(state == States.Open, "Betting is currently closed");
        require(outcome < numberOfOutcomes, "Invalid outcome");
        require(
            amount >= MINIMUM_BET_SIZE,
            "Bet amount must be at least 1 USDC"
        );

        usdc.safeTransferFrom(msg.sender, address(this), amount);

        // Check the current pool size before the bet is placed
        if (totalPerOutcome[outcome] < 50000 * 1e6) {
            uint256 remaining = 50000 * 1e6 - totalPerOutcome[outcome];
            uint256 contribution = Math.min(amount, remaining);
            // Check if the address is already recorded as an early bettor for this outcome
            if (
                earlyBettors[outcome][msg.sender] == false && contribution > 0
            ) {
                earlyBettors[outcome][msg.sender] = true;
            }
            earlyBettorContributions[outcome][msg.sender] += contribution;
        }

        betAmounts[msg.sender][outcome] += amount;
        totalPerOutcome[outcome] += amount;
        total += amount;
    }

    function close() public onlyOwner {
        require(state == States.Open, "Contract is not open");
        state = States.Closed;
    }

    function resolve(uint256 _winningOutcome) public onlyOwner {
        require(state == States.Closed, "Contract is not closed");
        require(_winningOutcome < numberOfOutcomes, "Invalid outcome");
        winningOutcome = _winningOutcome;
        state = States.Resolved;

        contractFees =
            ((total - totalPerOutcome[winningOutcome]) *
                feeRate *
                SCALE_FACTOR) /
            (BPS * SCALE_FACTOR); // Calculating fees from total bet amount across all pools
        rebatePool =
            (contractFees * rebateRate * SCALE_FACTOR) /
            (BPS * SCALE_FACTOR); // Calculating the rebate pool from the fees using the rebate rate
        contractFees -= rebatePool; // Adjusting the contract fees to account for the rebate
    }

    function cancel() public onlyOwner {
        state = States.Cancelled;
    }

    function claim() public nonReentrant {
        require(state == States.Resolved, "Contract is not resolved");
        uint256 userBetAmount = betAmounts[msg.sender][winningOutcome];
        require(userBetAmount > 0, "No bet placed on winning outcome");
        uint256 totalForWinningOutcome = totalPerOutcome[winningOutcome];
        require(totalForWinningOutcome > 0, "No liquidity for winning outcome");
        require(total > 0, "No liquidity in contract");

        uint256 grossAmount = (userBetAmount * total * SCALE_FACTOR) /
            (totalForWinningOutcome * SCALE_FACTOR);

        // calculate the fee based on the fee rate
        uint256 fee = ((grossAmount - userBetAmount) * feeRate * SCALE_FACTOR) /
            (BPS * SCALE_FACTOR);

        uint256 netAmount = grossAmount - fee;

        // If the user is an early bettor, add the rebate to the net amount.
        if (earlyBettors[winningOutcome][msg.sender]) {
            uint256 userContribution = earlyBettorContributions[winningOutcome][
                msg.sender
            ];
            uint256 totalContribution = Math.min(
                totalForWinningOutcome,
                50000 * 1e6
            );
            uint256 userRebate = (rebatePool *
                userContribution *
                SCALE_FACTOR) / (totalContribution * SCALE_FACTOR);

            netAmount += userRebate;
        }

        betAmounts[msg.sender][winningOutcome] = 0;
        // Transfer the tokens from this contract to the user
        usdc.safeTransfer(msg.sender, netAmount);
    }

    function refund() public nonReentrant {
        require(state == States.Cancelled, "Contract is not cancelled");

        uint256 totalAmount = 0;
        for (uint i = 0; i < numberOfOutcomes; i++) {
            totalAmount += betAmounts[msg.sender][i];
            betAmounts[msg.sender][i] = 0;
        }
        require(totalAmount > 0, "Nothing to refund");
        usdc.safeTransfer(msg.sender, totalAmount);
    }

    function getDepositedCollateral() public view returns (uint256) {
        return usdc.balanceOf(address(this));
    }

    function getDepositsForOutcome(
        uint256 outcome
    ) public view returns (uint256) {
        return totalPerOutcome[outcome];
    }

    function getUserPositions(
        address user
    ) public view returns (uint256[] memory) {
        uint256[] memory positions = new uint256[](numberOfOutcomes);
        for (uint i = 0; i < numberOfOutcomes; i++) {
            positions[i] = betAmounts[user][i];
        }
        return positions;
    }

    function withdrawFees() public onlyOwner {
        require(contractFees > 0, "No fees to withdraw");
        uint256 fees = contractFees;
        contractFees = 0;
        usdc.safeTransfer(msg.sender, fees);
    }

    function isEarlyBettor(
        address user,
        uint256 outcome
    ) public view returns (bool) {
        require(outcome < numberOfOutcomes, "Invalid outcome");
        return earlyBettors[outcome][user];
    }
}
