        uint256 _quantityToBuy,
        address _buyer,
        bytes32[] memory _proof,
        uint256 _signatureTimestamp,
        bytes memory signature
    ) external payable;
}


// File contracts/Wrapper.sol


pragma solidity 0.8.16;


// Utils


// Traxx-Marketplace Interface

/**
 * @title Wrapper Contract
 * @author TokenTraxx Team
 * @dev The purpose of this contract is to receive native currency from WithPaper and send
 * equivalent TRAXX to the TraxxMarketplace contract
 */

contract Wrapper is Initializable, AccessControlEnumerableUpgradeable {
    /**
     *  @dev Stores the Marketplace Interface
     */
    IMarketplace marketContract;

    /**
     *  @dev Stores the Verifier contract address
     */
    address public verifierAddress;

    /**
     *  @dev Stores the Traxx contract address
     */
    address public traxxAddress;

    /**
     *  @dev Stores the Traxx Marketplace contract address
     */
    address public marketAddress;

    /**
     * @dev A struct representing pricing and signature data for an NFT purchase.
     *
     * This struct encapsulates essential information related to the pricing of an NFT and the associated signature for verification.
     *
     * @param nftPriceInNative The price of the NFT in the native currency.
     * @param nftPriceInTraxx The price of the NFT in TRAXX tokens.
     * @param tokenBuyer The address of the NFT buyer.
     * @param tokenId The unique identifier of the NFT.
     * @param quantity The quantity of NFTs being purchased.
     * @param timestamp A timestamp value associated with the signature.
     * @param signature The cryptographic signature for verifying the authenticity of the data.
     *
     * @dev This struct is commonly used to pass pricing and signature data in various NFT-related functions.
     */
    struct PriceData {
        uint256 nftPriceInNative;
        uint256 nftPriceInTraxx;
        address tokenBuyer;
        uint256 tokenId;
        uint256 quantity;
        uint256 timestamp;
        bytes signature;
    }

    /**
     * @dev enforces that the caller has the DEFAULT_ADMIN_ROLE
     */
    modifier onlyAdmin() {
        require(
            hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "Caller does not have Admin role"
        );
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initializes the contract with essential addresses.
     *
     * This function is used to set up the contract with the addresses of the marketplace contract, verifier contract, and TRAXX token contract.
     *
     * @param _marketAddress The address of the marketplace contract.
     * @param _verifierAddress The address of the verifier contract.
     * @param _traxxAddress The address of the TRAXX token contract.
     *
     * @dev The function performs the following checks and actions:
     * - Verifies that '_marketAddress' is a valid contract address.
     * - Ensures that '_verifierAddress' is not the zero address.
     * - Verifies that '_traxxAddress' is a valid contract address.
     * - Initializes contract state variables with the provided addresses.
     * - Grants the DEFAULT_ADMIN_ROLE to the caller of this function.
     * - Initializes 'marketContract' with the provided marketplace contract address.
     *
     * @notice This function should be called once during contract deployment to set up the required addresses and permissions.
     */
    function initialize(
        address _marketAddress,
        address _verifierAddress,
        address _traxxAddress
    ) external initializer {
        require(
            AddressUpgradeable.isContract(_marketAddress),
            "Market is not a contract"
        );
        require(_verifierAddress != address(0), "Zero Address");
        require(
            AddressUpgradeable.isContract(_traxxAddress),
            "Traxx is not a contract"
        );

        verifierAddress = _verifierAddress;
        traxxAddress = _traxxAddress;
        marketAddress = _marketAddress;

        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);

        marketContract = IMarketplace(_marketAddress);
    }

    /**
     * @dev allows contract to receive funds
     */
    receive() external payable {}

    /**
     * @dev Sets the address of the verifier contract.
     *
     * This function allows the admin to set the address of the verifier contract.
     * Only the admin can execute this function.
     *
     * @param _verifierAddress The address of the new verifier contract.
     * @dev Reverts if the provided address is the zero address.
     */
    function setVerifierAddress(address _verifierAddress) external onlyAdmin {
        require(_verifierAddress != address(0), "Zero Address");
        verifierAddress = _verifierAddress;
    }

    /**
     * @dev Sets the address of the Traxx contract.
     *
     * This function allows the admin to set the address of the Traxx contract, which should be a valid contract address.
     * Only the admin can execute this function.
     *
     * @param _traxxAddress The address of the new Traxx contract.
     * @dev Reverts if the provided address is not a valid contract address.
     */
    function setTraxxAddress(address _traxxAddress) external onlyAdmin {
        require(AddressUpgradeable.isContract(_traxxAddress), "Not a contract");
        traxxAddress = _traxxAddress;
    }

    /**
     * @dev Sets the address of the Market contract.
     *
     * This function allows the admin to set the address of the Market contract, which should be a valid contract address.
     * It also initializes the 'marketContract' variable with the provided contract address.
     * Only the admin can execute this function.
     *
     * @param _contractAddress The address of the new Market contract.
     * @dev Reverts if the provided address is not a valid contract address.
     */
    function setMarketContract(address _contractAddress) external onlyAdmin {
        require(
            AddressUpgradeable.isContract(_contractAddress),
            "Not a contract"
        );
        marketAddress = _contractAddress;
        marketContract = IMarketplace(_contractAddress);
    }

    /**
     * @dev Calculates the keccak256 hash of the provided parameters.
     *
     * This function takes several parameters and returns the keccak256 hash of their encoded values.
     *
     * @param _nftPriceInNative The price of the NFT in native currency.
     * @param _owner The address of the NFT owner.
     * @param _tokenId The unique identifier of the NFT.
     * @param _quantity The quantity of NFTs.
     * @param _timestamp A timestamp value.
     * @return The keccak256 hash of the encoded parameters.
     *
     * @notice This function is also used to generate a hash in backend scripts.
     */
    function getEncodedHash(
        uint256 _nftPriceInNative,
        address _owner,
        uint256 _tokenId,
        uint256 _quantity,
        uint256 _timestamp
    ) public pure returns (bytes32) {
        return
            keccak256(
                abi.encodePacked(
                    _nftPriceInNative,
                    _owner,
                    _tokenId,
                    _quantity,
                    _timestamp
                )
            );
    }

    /**
     * @dev Verifies the signature of the provided 'PriceData' structure.
     *
     * This internal function calculates a hash value from the 'PriceData' structure,
     * validates the signature against the verifier contract, and returns true if the
     * signature is valid, indicating that the data has not been tampered with.
     *
     * @param priceData The 'PriceData' structure containing pricing and signature information.
     * @return True if the signature is valid, false otherwise.
     */
    function _getSignatureVerified(
        PriceData memory priceData
    ) internal view returns (bool) {
        bytes32 hashValue = getEncodedHash(
            priceData.nftPriceInNative,
            priceData.tokenBuyer,
            priceData.tokenId,
            priceData.quantity,
            priceData.timestamp
        );

        bytes32 ethSignedMessageHash = ECDSAUpgradeable.toEthSignedMessageHash(
            hashValue
        );

        return
            SignatureCheckerUpgradeable.isValidSignatureNow(
                verifierAddress,
                ethSignedMessageHash,
                priceData.signature
            );
    }

    /**
     * @dev Facilitates a delegated purchase of an NFT listing on the marketplace.
     *
     * This function allows a user to delegate the purchase of an NFT listing by providing the necessary details and a valid signature.
     *
     * @param _listingId The identifier of the NFT listing.
     * @param _currency The currency used for the purchase.
     * @param _price The price of the NFT listing.
     * @param _quantityToBuy The quantity of NFTs to buy.
     * @param _buyer The address of the buyer.
     * @param _proof An array of proofs for validation.
     * @param _signatureTimestamp The timestamp associated with the signature.
     * @param _signature The signature for verifying the authenticity of the data.
     * @param _priceData The 'PriceData' structure containing pricing and signature information.
     *
     * @dev This function performs several checks to ensure the validity of the delegated purchase:
     * - Checks that the 'tokenBuyer' in '_priceData' is not a zero address.
     * - Verifies that the '_priceData.tokenBuyer' matches the '_buyer' address provided.
     * - Ensures that the '_priceData.timestamp' is greater than or equal to the current block timestamp, preventing the use of expired signatures.
     * - Validates that the value sent with the transaction ('msg.value') is equal to '_priceData.nftPriceInNative'.
     * - Verifies the authenticity of the provided signature using '_getSignatureVerified'.
     * - Approves the necessary TRAXX tokens for the purchase using the 'approve' function.
     * - Finally, delegates the purchase to the marketplace contract.
     *
     * @notice Ensure that you have provided all the required information and a valid signature for a successful delegated purchase.
     */
    function delegateCall(
        uint256 _listingId,
        address _currency,
        uint256 _price,
        uint256 _quantityToBuy,
        address _buyer,
        bytes32[] memory _proof,
        uint256 _signatureTimestamp,
        bytes memory _signature,
        PriceData memory _priceData
    ) external payable {
        require(
            _priceData.tokenBuyer != address(0) &&
                _priceData.tokenBuyer == _buyer,
            "Invalid buyer"
        );

        require(_priceData.timestamp >= block.timestamp, "Signature expired");

        require(
            msg.value == _priceData.nftPriceInNative,
            "Invalid native token amount"
        );

        require(_getSignatureVerified(_priceData), "Invalid signature");

        // TRAXX Approve
        bool approvalResponse = IERC20(traxxAddress).approve(
            marketAddress,
            _price * _quantityToBuy
        );

        require(approvalResponse, "Approval failed");

        marketContract.delegatedBuy(
            _listingId,
            _currency,
            _price,
            _quantityToBuy,
            _buyer,
            _proof,
            _signatureTimestamp,
            _signature
        );
    }

    /**
     * @dev Withdraws TRAXX tokens from the contract's balance.
     *
     * This function allows the admin to withdraw a specified amount of TRAXX tokens from the contract's balance to a designated recipient address.
     *
     * @param _receiver The address to which the TRAXX tokens will be transferred.
     * @param _amount The amount of TRAXX tokens to withdraw.
     *
     * @dev The function performs the following checks and actions:
     * - Ensures that the contract holds a sufficient balance of TRAXX tokens to cover the withdrawal.
     * - Transfers the specified '_amount' of TRAXX tokens to the designated '_receiver' address.
     *
     * @notice Only the admin is permitted to execute this function.
     */
    function withdrawTraxx(
        address _receiver,
        uint256 _amount
    ) external onlyAdmin {
        require(
            IERC20(traxxAddress).balanceOf(address(this)) >= _amount,
            "Insufficient TRAXX"
        );
        IERC20(traxxAddress).transfer(_receiver, _amount);
    }

    /**
     * @dev Withdraws native tokens (ETH) from the contract's balance.
     *
     * This function allows the admin to withdraw a specified amount of native tokens (ETH) from the contract's balance to a designated recipient address.
     *
     * @param _receiver The address to which the native tokens will be transferred.
     * @param _amount The amount of native tokens (ETH) to withdraw.
     *
     * @dev The function performs the following checks and actions:
     * - Ensures that the contract holds a sufficient balance of native tokens (ETH) to cover the withdrawal.
     * - Transfers the specified '_amount' of native tokens (ETH) to the designated '_receiver' address.
     *
     * @notice Only the admin is permitted to execute this function.
     */
    function withdrawNative(
        address _receiver,
        uint256 _amount
    ) external onlyAdmin {
        require(address(this).balance >= _amount, "Insufficient Native Token");
        payable(_receiver).transfer(_amount);
    }

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[48] private __gap;
}
