      "content": "import {Owned} from "lib/solmate/src/auth/Owned.sol";
import {IERC20} from "./vendor/IERC20.sol";

// Dumb contract which only holds LINK, to be used by the bridge deployments
contract Purse is Owned {
    constructor(address owner) Owned(owner) {}

    // approve the given contract to spend our fu8nds
    function approve(address token, address bridgeAddress) public onlyOwner {
        IERC20(token).approve(address(bridgeAddress), 2 ** 256 - 1);
    }

    receive() external payable {}

    function withdraw(address _beneficiary) public onlyOwner {
        uint256 amount = address(this).balance;
        (bool sent, ) = _beneficiary.call{value: amount}("");
        require(sent, "failed to send");
    }

    function withdrawToken(address _beneficiary, address _token, uint256 _amount) public onlyOwner {
        uint256 amount = _amount;
        IERC20(_token).transfer(_beneficiary, amount);
    }
}
