pragma solidity ^0.8.20;


contract Nuts is ERC721, ERC721Enumerable {

    IERC20 public immutable nutcoin;

    uint256 public constant MAX_NFT_SUPPLY = 10000;
    uint256 public MAX_NFT_PER_MINT = 100;


    constructor() ERC721("Nuts", "NUTS") {
    	// input set to the address of Nutcoin (NUT) ERC20 token contract.
        nutcoin = IERC20(0x473F4068073Cd5b2aB0E4Cc8E146F9EDC6fb52CC);
    }
    
    function _baseURI() internal pure override returns (string memory) {
        return "ipfs://bafybeih4isbjpv27sstnbfshcxze74fj6bpwq4eyiyz7i4myvmfytrvuly/";
    }
    
    /**
    * Mint NFT(s)
    */
    function mint(uint256 quantity) external {
        require(totalSupply() < MAX_NFT_SUPPLY, "All NFTs already minted");
        require(quantity > 0, "Minting quantity cannot be 0");
        require(quantity <= MAX_NFT_PER_MINT, "Can only mint up to 100 NFTs at a time");
        require(totalSupply() + quantity <= MAX_NFT_SUPPLY, "Exceeds MAX_NFT_SUPPLY");
       
        
        // 1,000,000,000 required of Nutcoin (NUT) to be burnt in order to mint 1 NUTS NFT
        uint256 nutcoin_amount = 1000000000 ether;
        nutcoin.transferFrom(msg.sender, address(this), nutcoin_amount * quantity);
        
        
        for(uint i = 0; i < quantity; i++) {
            uint mintIndex = totalSupply();
            if (totalSupply() < MAX_NFT_SUPPLY) {
                _safeMint(msg.sender, mintIndex);
            }
        }
    }
    
    // The following functions are overrides required by Solidity.

    function _update(address to, uint256 tokenId, address auth)
        internal
        override(ERC721, ERC721Enumerable)
        returns (address)
    {
        return super._update(to, tokenId, auth);
    }

    function _increaseBalance(address account, uint128 value)
        internal
        override(ERC721, ERC721Enumerable)
    {
        super._increaseBalance(account, value);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
