    Counters.Counter private _tokenIdCounter;
    address payable receiver;

    address  public royaltyAddress = 0xB84b1D02bc41A3c39F4B486be24E45f95758AeD2;
    address  public router = 0xB84b1D02bc41A3c39F4B486be24E45f95758AeD2;


    struct NFT {
        string id;
        uint fee;
        string uri;
        uint percentage;
        string name;
    }

    //nfts mapping
    mapping(string => NFT) public nfts;

    mapping(uint256 => string) internal tokenURIs;

    string public baseURI = "https://pangs-collection.s3.amazonaws.com/meta/";
    string public baseExtension = ".json";
    address public signer = 0x0Da65F9ecab9bCF585f624CbEB6f113e4B85E255;

    uint256 public royalty = 600;
    
    bool public whitelistedOnly = true;
    mapping(address => bool) public whiteListed;

    //MINT FEE
    uint256 public fee = 10000000000000; // .25eth
    uint256 public whitelistFee = 100000000000000000;

    //Supply
    uint256 totalSup = 10000;

    mapping (address => bool) private _isWhiteListed;

  
    constructor(
        address payable _receiver

    ) ERC721("Wu Pangs", "WuPangs") {
        receiver = _receiver;
 
    }

    function _baseExtension() internal view override returns (string memory) {
        return baseExtension;
    }
  
    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

  function setReceiver(address payable _receiver) public onlyOwner{
      receiver = _receiver;

  }

function compareStrings(string memory a, string memory b) internal pure returns (bool) {
    return (keccak256(abi.encodePacked((a))) == keccak256(abi.encodePacked((b))));
}


function mintNFT(uint _amount, bytes calldata signature) public payable {
    require(!paused(),"Sale must be active to mint");
    require(fee != 0,"NFT is not registered");
    require( _tokenIdCounter.current() + _amount <= totalSup, "Amount more than total supply");

    if(isExcludedFromFees(msg.sender) || isValidSignature(signature)){
        if(_amount > 1){
            uint256 totalFee = ((_amount - 1)  * fee) + whitelistFee;
            require(msg.value >= totalFee, "Insufficient funds.");
        }else{
            require(msg.value >= whitelistFee * _amount, "Insufficient funds.");
        }
            _isWhiteListed[msg.sender] = false;

    }else{
        require(msg.value >= fee * _amount, "Insufficient funds.");
    }
        receiver.transfer(msg.value);
    
    for(uint i = 0; i < _amount; i++){
        internalMint(msg.sender);
    }


}

    function whiteListAddress(address[]memory account, bool excluded) public onlyOwner {
        for(uint i = 0; i < account.length; i++){
        _isWhiteListed[account[i]] = excluded;
        }           

    }

    function isExcludedFromFees(address account) public view returns(bool) {
        return _isWhiteListed[account];
    }


    function tokenURI(uint256 Id) 
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return tokenURIs[Id];
    }

    function Owned(address _owner)
        external
        view
        returns (uint256[] memory)
    {
        uint256 tokenCount = balanceOf(_owner);
        if (tokenCount == 0) {
            return new uint256[](0);
        } else {
            uint256[] memory result = new uint256[](tokenCount);
            uint256 index;
            for (index = 0; index < tokenCount; index++) {
                result[index] = tokenOfOwnerByIndex(_owner, index);
            }
            return result;
        }
    }

    function tokenExists(uint256 _id) external view returns (bool) {
        return (_exists(_id));
    }

    function royaltyInfo(uint256, uint256 _salePrice)
        external
        view
        override
        returns (address _receiver, uint256 royaltyAmount)
    {
        return (royaltyAddress, (_salePrice * royalty) / 10000);
    }

    function unlock() public onlyOwner {
        whitelistedOnly = false;
    }

    function lock() public onlyOwner {
        whitelistedOnly = true;
    }
    
    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    
    function setURI(uint256 tokenId, string memory uri) external onlyOwner {
        tokenURIs[tokenId] = uri;
    }

    function setMintFee(uint256 _fee) external onlyOwner {
        fee = _fee;
    }

    function setWLFee(uint256 _fee) external onlyOwner {
        whitelistFee = _fee;
    }
    
    function setRoyalty(uint16 _royalty) external onlyOwner {
        require(_royalty >= 0, "Royalty must be greater than or equal to 0%");
        require(_royalty <= 750, "Royalty must be greater than or equal to 7.5%" );
        royalty = _royalty;
    }
    
    function setRoyaltyAddress(address _royaltyAddress) external onlyOwner {
        royaltyAddress = _royaltyAddress;
    }
    
    
    //Overrides
    
     function internalMint(address to) internal {
        _tokenIdCounter.increment();
        _safeMint(to, _tokenIdCounter.current());
        tokenURIs[_tokenIdCounter.current()] = string(abi.encodePacked(baseURI, _tokenIdCounter.current().toString(), baseExtension));
    }

    function safeMint(address to) public onlyOwner {
        internalMint(to);
    }
    
    
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable, IERC165)
        returns (bool)
    {
        return
            interfaceId == type(IERC2981).interfaceId ||
            super.supportsInterface(interfaceId);
    }
    
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal override(ERC721, ERC721Enumerable) whenNotPaused {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    

    function _burn(uint256 tokenId)
        internal
        override(ERC721, ERC721URIStorage)
    {
        super._burn(tokenId);
    }


  function updateRouter(address _router) external onlyOwner {
    router = _router;
  }

  function _safeMigrationMint(
    address _router,
    uint256 _tokenId,
    address _to
  ) internal {
    _safeMint(_router, _tokenId);
    emit Migration(_to, _tokenId);
  }

  function safeTransferFrom(
    address from,
    address to,
    uint256 tokenId,
    bytes memory data
  ) public virtual override {
    if (_msgSender() == router && from == router && to != router && !_exists(tokenId)) {
      require(tokenId > 0, "Token ID invalid");
      _safeMigrationMint(router, tokenId, to);
    }
    require(
      _isApprovedOrOwner(_msgSender(), tokenId),
      "ERC721: transfer caller is not owner nor approved"
    );
    _safeTransfer(from, to, tokenId, data);
  }

 /// @dev withdraw fees

    
    /// @dev withdraw ERC20 tokens
    function withdrawTokens(address _tokenContract) external onlyOwner {
        IERC20 tokenContract = IERC20(_tokenContract);
        uint256 _amount = tokenContract.balanceOf(address(this));
        tokenContract.transfer(owner(), _amount);
        emit WithdrawWrongTokens(msg.sender,_tokenContract,_amount);
    }

    /// @dev withdraw ERC721 tokens to the contract owner
    function withdrawNFT(address _tokenContract, uint256[] memory _id) external onlyOwner {
        IERC721 tokenContract = IERC721(_tokenContract);
        for (uint256 i = 0; i < _id.length; i++) {
            tokenContract.safeTransferFrom(address(this), owner(), _id[i]);
            emit WithdrawWrongNfts(msg.sender,_tokenContract,_id[i]);
        }
    }

    function isValidSignature(
        bytes memory signature
    ) view internal returns (bool) {
        bytes32 data = keccak256(abi.encodePacked(_msgSender()));
        return signer == data.toEthSignedMessageHash().recover(signature);
    }
    function setSigner(address val) public onlyOwner() {
        require(val != address(0), "Pang: val is the zero address");
        signer = val;
    }
}
