// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <=0.8.17;

import "./BlockPlatform.sol";

contract BlockPlatformStandard is BlockPlatform {

    address internal withdrawAddress;

    event ModifyWithdrawAddress(address indexed withdrawAddress);

    event TransferToken(address indexed from, address indexed to, address indexed token, uint256 amount,string orderId);

    function getWithdrawAddress() public view returns(address){
        return withdrawAddress;
    }

    function modifyWithdrawAddress(address newAddress) public onlyOwner checkWithdrawAddress(newAddress) {
        require (withdrawAddress != newAddress,"The withdraw address is approved");
        withdrawAddress = newAddress;
        emit ModifyWithdrawAddress(withdrawAddress);
    }

    function transferToken(address token,uint256 amount,string memory orderId) public returns (bool) {
        require(coinList[token], "transfer token not allowed");
        uint256 finalAmount = super.transferCommon(token,withdrawAddress,amount);
        emit TransferToken(msg.sender, withdrawAddress, token, finalAmount,orderId); 
        return true;
    }

}"
