// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <=0.8.17;

import "./BlockCommon.sol";

contract BlockPlatform is BlockCommon {

    address internal immutable owner;

    mapping(address => bool) internal coinList;

    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _; 
    }

    constructor() {
        owner = msg.sender;
    }

    event AddCoinList(address indexed tokenAddress);

    event CloseCoinList(address indexed tokenAddress);


    function getCoinList(address newAddress) public view returns (bool) {
        return coinList[newAddress];
    }

    function addCoinList(address newAddress) public onlyOwner checkTokenAddress(newAddress)  {
        require (!coinList[newAddress],"The token address is approved");
        coinList[newAddress] = true;
        emit AddCoinList(newAddress);
    }

    function closeCoinList(address newAddress) public onlyOwner checkTokenAddress(newAddress) {
        require (coinList[newAddress],"This token address is no longer allowed");
        coinList[newAddress] = false;
        emit CloseCoinList(newAddress);
    }

}
"
