    "src/SailCloth.sol": {
      "content": "pragma solidity 0.8.23;

import { ERC721 } from "solady/src/tokens/ERC721.sol";
import { LibString } from "solady/src/utils/LibString.sol";
import { Ownable } from "solady/src/auth/Ownable.sol";
import { MerkleProofLib } from "solady/src/utils/MerkleProofLib.sol";
import { LibBitmap } from "solady/src/utils/LibBitmap.sol";

contract SailCloth is Ownable, ERC721 {
    using LibString for string;
    using LibString for uint256;
    using LibBitmap for LibBitmap.Bitmap;

    /*###############################################################
                            ERRORS
    ###############################################################*/
    error AlreadyClaimed();
    error InvalidProof();
    error EmptyMerkleRoot();

    /*###############################################################
                            EVENTS
    ###############################################################*/

    event Claimed(address indexed user, bytes32 root, uint256 index, uint256 amount);

    /*###############################################################
                            STORAGE
    ###############################################################*/

    string public baseURI = "https://beratone.io/sailcloth/";
    bytes32 internal merkleRootForMint;
    LibBitmap.Bitmap internal claims;
    uint256 internal mintCount;


    /*###############################################################
                            CONSTRUCTOR
    ###############################################################*/
    constructor() {
        _initializeOwner(msg.sender);
    }

    /*###############################################################
                            VIEWS
    ###############################################################*/
    function name() public view override returns (string memory) {
        return "BeraTone Founder's Sailcloth";
    }
    function symbol() public view override returns (string memory) {
        return "BFS";
    }
    function tokenURI(uint256 _id) public view override returns (string memory) {
        return baseURI.concat(_id.toString());
    }

    /*###############################################################
                            OWNER
    ###############################################################*/
    function setBaseURI(string memory _baseURI) public onlyOwner {
        baseURI = _baseURI;
    }
    function setMerkleRootForMint(bytes32 _merkleRootForMint) public onlyOwner {
        merkleRootForMint = _merkleRootForMint;
    }

    /*###############################################################
                            PUBLIC
    ###############################################################*/
    function mint(address _user, uint256 _index, uint256 _amount, bytes32[] calldata _proof) external {
        if (merkleRootForMint == bytes32(0)) revert EmptyMerkleRoot();
        if (claims.get(_index)) revert AlreadyClaimed();
        bytes32 node = keccak256(abi.encodePacked(_index, _user, _amount));
        if (!MerkleProofLib.verify(_proof, merkleRootForMint, node)) revert InvalidProof();
        claims.set(_index);
        for (uint256 i; i < _amount; i++) {
            _mint(_user, mintCount + i);
        }
        mintCount += _amount;
        emit Claimed(_user, merkleRootForMint, _index, _amount);
    }

    function hasClaimed(uint256 _index) public view returns (bool) {
        return claims.get(_index);
    }




}"
