      "content": "// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.4.22 <0.9.0;

// Import the IERC20 interface for ERC-20 token interaction
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Escrow {
    address payable public arbiter = payable(0x0D930E42Cd8C161F13C3F2Bdc3212D850B18EC89);
    address public constant usdtContractAddress = 0xdAC17F958D2ee523a2206206994597C13D831ec7;
    uint totalRevenue = 0;
    uint public numberOfOrders = 0;

    enum State {
        awate_delivery,
        complete,
        refunded // New State
    }

    struct EscrowOrder {
        address payable buyer;
        address payable seller;
        State state;
        uint256 usdtAmount;
    }

    mapping(uint256 => EscrowOrder) public escrowOrders;
    mapping(uint256 => bool) public doesOrderExist;
    IERC20 private usdt;

    constructor() {
        usdt = IERC20(usdtContractAddress);
    }

    modifier instate(uint256 orderId, State expectedState) {
        require(escrowOrders[orderId].state == expectedState, "Invalid state for this escrow order");
        _;
    }

    modifier onlyBuyer(uint256 orderId) {
        require(msg.sender == escrowOrders[orderId].buyer || msg.sender == arbiter, "Only the buyer or arbiter can call this function");
        _;
    }

    modifier onlySeller(uint256 orderId) {
        require(msg.sender == escrowOrders[orderId].seller || msg.sender == arbiter, "Only the seller or arbiter can call this function");
        _;
    }

    function createEscrowOrder(
        uint256 orderId,
        address payable _seller,
        uint256 _usdtAmount
    ) public {
        require(!doesOrderExist[orderId], "Order with the specified ID already exists");
        usdt.transferFrom(msg.sender, address(this), _usdtAmount);
        EscrowOrder memory newOrder = EscrowOrder({
            buyer: payable(msg.sender),
            seller: _seller,
            state: State.awate_delivery,
            usdtAmount: _usdtAmount
        });
        escrowOrders[orderId] = newOrder;
        doesOrderExist[orderId] = true;
    }

    function confirmDelivery(
        uint256 orderId
    ) public onlyBuyer(orderId) instate(orderId, State.awate_delivery) {
        uint256 totalAmount = escrowOrders[orderId].usdtAmount;
        uint256 sellerAmount = (totalAmount * 90) / 100;
        uint256 arbiterAmount = totalAmount - sellerAmount;

        usdt.transfer(escrowOrders[orderId].seller, sellerAmount);
        usdt.transfer(arbiter, arbiterAmount);

        escrowOrders[orderId].state = State.complete;
        totalRevenue += escrowOrders[orderId].usdtAmount;
        numberOfOrders++;
    }

    // Function to return payment
    function returnPayment(
        uint256 orderId
    ) public onlySeller(orderId) instate(orderId, State.awate_delivery) {
        usdt.transfer(escrowOrders[orderId].buyer, escrowOrders[orderId].usdtAmount);
        escrowOrders[orderId].state = State.refunded;
    }
}
"
    },
