// File: contracts/Escrow.sol


pragma solidity ^0.8.17;



library Structs {
    struct Request {
        address depositor;
        address artistAddress;
        uint256 lockAmount;
        uint256 releaseTime;
        bool isRejectedOrExpired;
    }
}

contract Escrow is ReentrancyGuard {
    using Math for uint256;

    mapping(string => Structs.Request) public requests;
    // keeps track of Accepted Requests
    mapping(string => bool) public isAccepted;

    event RequestMade(address indexed _collector, address indexed _artist, uint256 indexed _value, string _requestId);
    event RequestResult(
        address indexed _collector,
        address indexed _artist,
        uint256 indexed _value,
        string _requestId,
        bool _requestResult
    );
    event ClaimedExpiredRequest(address indexed _collector, string indexed _requestId);

    constructor() {}

    receive() external payable {}

    function makeRequest(address _artistAddress, uint256 _lockTime, string calldata _requestId) public payable {
        require(_artistAddress != address(0), "Invalid artist");
        require(requests[_requestId].depositor == address(0), "Request Id already exists");
        require(_lockTime <= 31536000, "Lock Time exceeds allowed max lock time");
        require(msg.value > 0, "Lock Amount cannot be zero");

        requests[_requestId].depositor = msg.sender;
        requests[_requestId].artistAddress = _artistAddress;
        requests[_requestId].lockAmount = msg.value;
        requests[_requestId].releaseTime = block.timestamp + _lockTime;

        emit RequestMade(msg.sender, _artistAddress, msg.value, _requestId);
    }

    function completeRequest(string calldata _requestId, bool _result) public payable {
        require(!isAccepted[_requestId], "Request already accepted");
        require(!requests[_requestId].isRejectedOrExpired, "Request already rejected");
        require(requests[_requestId].releaseTime >= block.timestamp, "Request has expired");
        require(
            requests[_requestId].artistAddress == msg.sender,
            "Address doesn't match to assigned artist Address to this request"
        );
        require(requests[_requestId].lockAmount <= address(this).balance);

        if (_result) {
            isAccepted[_requestId] = true;
        } else {
            requests[_requestId].isRejectedOrExpired = true;
        }

        (bool sent,) = payable(requests[_requestId].artistAddress).call{value: requests[_requestId].lockAmount}("");
        require(sent, "Failed to send Ether");

        emit RequestResult(
            requests[_requestId].depositor,
            requests[_requestId].artistAddress,
            requests[_requestId].lockAmount,
            _requestId,
            _result
        );
    }

    function claimExpiredRequest(string calldata _requestId) public payable {
        require(!isAccepted[_requestId], "Request already accepted");
        require(!requests[_requestId].isRejectedOrExpired, "Request already rejected");
        require(requests[_requestId].releaseTime <= block.timestamp, "Request is still active");
        require(
            requests[_requestId].depositor == msg.sender,
            "Request not registered or Address doesn't match to depositor Address of this request"
        );

        (bool sent,) = payable(requests[_requestId].depositor).call{value: requests[_requestId].lockAmount}("");
        require(sent, "Failed to send Ether");

        requests[_requestId].isRejectedOrExpired = true;

        emit ClaimedExpiredRequest(msg.sender, _requestId);
    }
}
