pragma solidity >=0.8.2 <0.9.0;

contract SocialFi is Ownable {

    using EnumerableSet for EnumerableSet.AddressSet;

    error FirstShareSubjectOnly();
    error InsufficientPayment();
    error SendFundsFail();
    error CannotSellLastShare();
    error InsufficientShares();

    address public protocolFeeDestination;
    uint256 public protocolFeePercent;
    uint256 public subjectFeePercent;

    event Trade(address trader, address subject, bool isBuy, uint256 shareAmount, uint256 ethAmount, uint256 protocolEthAmount, uint256 subjectEthAmount, uint256 supply);

    mapping(address => mapping(address => uint256)) public sharesBalance;
    mapping(address => uint256) public sharesSupply;
    mapping(address => EnumerableSet.AddressSet) private shareHolders;

    function setFeeDestination(address _feeDestination) public onlyOwner {
        protocolFeeDestination = _feeDestination;
    }

    function setProtocolFeePercent(uint256 _feePercent) public onlyOwner {
        protocolFeePercent = _feePercent;
    }

    function setSubjectFeePercent(uint256 _feePercent) public onlyOwner {
        subjectFeePercent = _feePercent;
    }

    function getPrice(uint256 supply, uint256 amount) public pure returns (uint256) {
        uint256 sum1;
        uint256 sum2;

        if (supply == 0) {
            sum1 = 0;
            sum2 = amount * (amount + 1) * (2 * amount + 1) / 6;
        } else {
            sum1 = (supply - 1) * (supply) * (2 * (supply - 1) + 1) / 6;
            sum2 = (supply - 1 + amount) * (supply + amount) * (2 * (supply - 1 + amount) + 1) / 6;
        }

        uint256 summation = sum2 - sum1;
        return summation * 1 ether / 16000;
    }

    function getBuyPrice(address sharesSubject, uint256 amount) public view returns (uint256) {
        return getPrice(sharesSupply[sharesSubject], amount);
    }

    function getSellPrice(address sharesSubject, uint256 amount) public view returns (uint256) {
        return getPrice(sharesSupply[sharesSubject] - amount, amount);
    }

    function getBuyPriceAfterFee(address sharesSubject, uint256 amount) public view returns (uint256) {
        uint256 price = getBuyPrice(sharesSubject, amount);
        uint256 protocolFee = price * protocolFeePercent / 1 ether;
        uint256 subjectFee = price * subjectFeePercent / 1 ether;
        return price + protocolFee + subjectFee;
    }

    function getSellPriceAfterFee(address sharesSubject, uint256 amount) public view returns (uint256) {
        uint256 price = getSellPrice(sharesSubject, amount);
        uint256 protocolFee = price * protocolFeePercent / 1 ether;
        uint256 subjectFee = price * subjectFeePercent / 1 ether;
        return price - protocolFee - subjectFee;
    }

    function buyShares(address sharesSubject, uint256 amount) public payable {
        uint256 supply = sharesSupply[sharesSubject];
        if (supply == 0 && sharesSubject != msg.sender) {
            revert FirstShareSubjectOnly();
        }
        
        uint256 price = getPrice(supply, amount);
        uint256 protocolFee = price * protocolFeePercent / 1 ether;
        uint256 subjectFee = price * subjectFeePercent / 1 ether;
        uint256 totalPrice = price + protocolFee + subjectFee;

        if (msg.value < totalPrice) {
            revert InsufficientPayment();
        }

        sharesBalance[sharesSubject][msg.sender] += amount;
        if (sharesBalance[sharesSubject][msg.sender] == amount) {
            shareHolders[sharesSubject].add(msg.sender);
        }
        sharesSupply[sharesSubject] = supply + amount;
        
        (bool success1, ) = protocolFeeDestination.call{value: protocolFee}("");
        (bool success2, ) = sharesSubject.call{value: subjectFee}("");
        
        if (!success1 || !success2) {
            revert SendFundsFail();
        }

        emit Trade(msg.sender, sharesSubject, true, amount, price, protocolFee, subjectFee, supply + amount);
    }

    function sellShares(address sharesSubject, uint256 amount) public payable {
        uint256 supply = sharesSupply[sharesSubject];

        if (supply <= amount) {
            revert CannotSellLastShare();
        }

        if (sharesBalance[sharesSubject][msg.sender] < amount) {
            revert InsufficientShares();
        }

        uint256 price = getPrice(supply - amount, amount);
        uint256 protocolFee = price * protocolFeePercent / 1 ether;
        uint256 subjectFee = price * subjectFeePercent / 1 ether;

        sharesBalance[sharesSubject][msg.sender] -= amount;
        sharesSupply[sharesSubject] = supply - amount;
        
        (bool success1, ) = msg.sender.call{value: price - protocolFee - subjectFee}("");
        (bool success2, ) = protocolFeeDestination.call{value: protocolFee}("");
        (bool success3, ) = sharesSubject.call{value: subjectFee}("");

        if (!success1 || !success2 || !success3) {
            revert SendFundsFail();
        }

        emit Trade(msg.sender, sharesSubject, false, amount, price, protocolFee, subjectFee, supply - amount);
    }

    function getAllHoldersForShare(address sharesSubject) public view returns (address[] memory) {
        uint256 length = shareHolders[sharesSubject].length();
        address[] memory holders = new address[](length);
        
        for (uint256 i = 0; i < length; ++i) {
            holders[i] = shareHolders[sharesSubject].at(i);
        }
        
        return holders;
    }
}
