    ) external payable;

    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;
}

contract Skrimples is ERC20, AccessControl {
    using SafeMath for uint256;

    bytes32 public constant BLACKLISTER_ROLE = keccak256("BLACKLISTER_ROLE");

    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;
    address public constant deadAddress = address(0xdead);

    bool private swapping;

    address public marketingWallet;
    address public devWallet;
    address public bonePileWallet;
    address public liquidity;

    uint256 public maxTransactionAmount;
    uint256 public swapTokensAtAmount;
    uint256 public maxWallet;

    bool public limitsInEffect = true;
    bool public tradingActive = false;
    bool public swapEnabled = false;
    bool public blacklistEnabled = true;

    uint256 public buyTotalFees;
    uint256 public buyMarketingFee;
    uint256 public buyLiquidityFee;
    uint256 public buyDevFee;
    uint256 public buyBonePileFee;

    uint256 public sellTotalFees;
    uint256 public sellMarketingFee;
    uint256 public sellLiquidityFee;
    uint256 public sellDevFee;
    uint256 public sellBonePileFee;

    uint256 public tokensForMarketing;
    uint256 public tokensForLiquidity;
    uint256 public tokensForDev;
    uint256 public tokensForBonePile;

    // exlcude from fees and max transaction amount
    mapping(address => bool) private _isExcludedFromFees;
    mapping(address => bool) public _isExcludedMaxTransactionAmount;
    mapping(address => bool) public blacklists;

    // store addresses that are automatic market maker pairs. Transfers *to* these addresses
    // could be subject to a maximum transfer amount
    mapping(address => bool) public automatedMarketMakerPairs;

    modifier onlyAdmin() {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Caller is not admin");
        _;
    }

    modifier onlyBlacklister() {
        require(hasRole(BLACKLISTER_ROLE, msg.sender), "Caller is not blacklister");
        _;
    }

    event UpdateUniswapV2Router(
        address indexed newAddress,
        address indexed oldAddress
    );

    event LimitsRemoved();

    event BlacklistDisabled();

    event ExcludeFromFees(address indexed account, bool isExcluded);

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    event marketingWalletUpdated(
        address indexed newWallet,
        address indexed oldWallet
    );

    event devWalletUpdated(
        address indexed newWallet,
        address indexed oldWallet
    );

    event liquidityUpdated(
        address indexed newWallet,
        address indexed oldWallet
    );

    event bonePileWalletUpdated(
        address indexed newWallet,
        address indexed oldWallet
    );

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiquidity
    );

    address admin = msg.sender;

    constructor(address blacklister) ERC20("Skrimples", "SKRIMP") {
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
            0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
        );
        // Grant the contract deployer the default admin role: will be able
        // to grant and revoke any roles
        _setupRole(DEFAULT_ADMIN_ROLE, admin);
        // Renouce blacklister role after blacklist is disabled
        _setupRole(BLACKLISTER_ROLE, blacklister);

        excludeFromMaxTransaction(address(_uniswapV2Router), true);
        uniswapV2Router = _uniswapV2Router;

        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), _uniswapV2Router.WETH());
        excludeFromMaxTransaction(address(uniswapV2Pair), true);
        _setAutomatedMarketMakerPair(address(uniswapV2Pair), true);

        // Initial tax of 97% to catch bots, once lowered cannot be raised higher than 5%
        uint256 _buyMarketingFee = 97;
        uint256 _buyLiquidityFee = 0;
        uint256 _buyDevFee = 0;
        uint256 _buyBonePileFee = 0;

        uint256 _sellMarketingFee = 2;
        uint256 _sellLiquidityFee = 0;
        uint256 _sellDevFee = 2;
        uint256 _sellBonePileFee = 1;

        uint256 totalSupply = 69000000000 * 1e18;

        // Limits imposed till the chart is stable
        maxTransactionAmount = (totalSupply * 1) / 100;
        maxWallet = (totalSupply * 1) / 100;
        swapTokensAtAmount = (totalSupply * 5) / 10000; // 0.05% swap wallet

        // Fees
        buyMarketingFee = _buyMarketingFee;
        buyLiquidityFee = _buyLiquidityFee;
        buyDevFee = _buyDevFee;
        buyBonePileFee = _buyBonePileFee;
        buyTotalFees = buyMarketingFee + buyLiquidityFee + buyDevFee + buyBonePileFee;

        sellMarketingFee = _sellMarketingFee;
        sellLiquidityFee = _sellLiquidityFee;
        sellDevFee = _sellDevFee;
        sellBonePileFee = _sellBonePileFee;
        sellTotalFees = sellMarketingFee + sellLiquidityFee + sellDevFee + sellBonePileFee;

        marketingWallet = address(0x70ae2cC09b5BaEaeC0bFe6f9F13331872Ea8aEF8);
        devWallet = address(0x8d9C26ee524Ae225CA3d640f8A9d73C32b6632FB);
        bonePileWallet = address(0x03888625CCCbAfE87fC4e264B54616cEAD476258);
        liquidity = address(0x70ae2cC09b5BaEaeC0bFe6f9F13331872Ea8aEF8);

        // exclude from paying fees
        excludeFromFees(address(admin), true);
        excludeFromFees(address(this), true);
        excludeFromFees(address(0xdead), true);
        excludeFromFees(marketingWallet, true);
        // exclude from max transaction amount
        excludeFromMaxTransaction(address(admin), true);
        excludeFromMaxTransaction(address(this), true);
        excludeFromMaxTransaction(address(0xdead), true);
        excludeFromMaxTransaction(marketingWallet, true);

        /*
            _mint is an internal function in ERC20.sol that is only called here,
            and CANNOT be called ever again
        */
        _mint(msg.sender, totalSupply);
    }

    receive() external payable {}

    function burn(uint256 amount) public virtual {
        _burn(_msgSender(), amount);
    }

    function blacklist(address _address, bool _isBlacklisting) external onlyBlacklister {
        blacklists[_address] = _isBlacklisting;
    }

    // Disable blacklist once token is stable, cannot be enabled again
    function disableBlacklist() external onlyBlacklister returns (bool) {
        blacklistEnabled = false;
        emit BlacklistDisabled();
        return true;
    }

    // once enabled, is forever enabled
    function enableTrading() external onlyAdmin {
        tradingActive = true;
        swapEnabled = true;
    }

    // remove limits after token is stable, cannot be enabled again
    function removeLimits() external onlyAdmin returns (bool) {
        limitsInEffect = false;
        emit LimitsRemoved();
        return true;
    }

    // change the minimum amount of tokens to sell from fees
    function updateSwapTokensAtAmount(uint256 newAmount)
        external onlyAdmin
        returns (bool)
    {
        require(
            newAmount >= (totalSupply() * 1) / 100000,
            "Swap amount cannot be lower than 0.001% total supply."
        );
        require(
            newAmount <= (totalSupply() * 5) / 1000,
            "Swap amount cannot be higher than 0.5% total supply."
        );
        swapTokensAtAmount = newAmount;
        return true;
    }

    // MaxTxAmount and MaxWalletAmount, cannot set lower than 2% of totalSupply
    function updateMaximums(uint256 newNum) external onlyAdmin {
        require(
            newNum >= ((totalSupply() * 2) / 100) / 1e18,
            "Cannot set maximums lower than 2%"
        );
        maxWallet = newNum * (10**18);
        maxTransactionAmount = newNum * (10**18);
    }

    function excludeFromMaxTransaction(address updAds, bool isEx)
        public onlyAdmin {
        _isExcludedMaxTransactionAmount[updAds] = isEx;
    }

    // only use to disable buy/sell fees if absolutely necessary (emergency use only)
    function updateSwapEnabled(bool enabled) external onlyAdmin {
        swapEnabled = enabled;
    }

    function updateFees(
        uint256 _marketingFeeBuy,
        uint256 _liquidityFeeBuy,
        uint256 _devFeeBuy,
        uint256 _bonePileFeeBuy,
        uint256 _marketingFeeSell,
        uint256 _liquidityFeeSell,
        uint256 _devFeeSell,
        uint256 _bonePileFeeSell
    ) external onlyAdmin {
        require(
            _marketingFeeBuy + _liquidityFeeBuy + _devFeeBuy + _bonePileFeeBuy <= 5,
            "buyTotalFees cannot exceed 5% of transaction"
        );
        require(
            _marketingFeeSell + _liquidityFeeSell + _devFeeSell + _bonePileFeeBuy <= 5,
            "sellTotalFees cannot exceed 5% of transaction"
        );
        buyMarketingFee = _marketingFeeBuy;
        buyLiquidityFee = _liquidityFeeBuy;
        buyDevFee = _devFeeBuy;
        buyBonePileFee = _bonePileFeeBuy;
        buyTotalFees = buyMarketingFee + buyLiquidityFee + buyDevFee + buyBonePileFee;
        sellMarketingFee = _marketingFeeSell;
        sellLiquidityFee = _liquidityFeeSell;
        sellDevFee = _devFeeSell;
        sellBonePileFee = _bonePileFeeSell;
        sellTotalFees = sellMarketingFee + sellLiquidityFee + sellDevFee + sellBonePileFee;
    }

    function excludeFromFees(address account, bool excluded) public onlyAdmin {
        _isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }

    function setAutomatedMarketMakerPair(address pair, bool value)
        public onlyAdmin {
        require(
            pair != uniswapV2Pair,
            "The pair cannot be removed from automatedMarketMakerPairs"
        );

        _setAutomatedMarketMakerPair(pair, value);
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        automatedMarketMakerPairs[pair] = value;

        emit SetAutomatedMarketMakerPair(pair, value);
    }

    function updateMarketingWallet(address newMarketingWallet)
        external onlyAdmin {
        emit marketingWalletUpdated(newMarketingWallet, marketingWallet);
        marketingWallet = newMarketingWallet;
    }

    function updateLiquidity(address newLiquidity)
        external onlyAdmin {
        emit liquidityUpdated(newLiquidity, liquidity);
        liquidity = newLiquidity;
    }

    function updateDevWallet(address newWallet) external onlyAdmin {
        emit devWalletUpdated(newWallet, devWallet);
        devWallet = newWallet;
    }

    function updateBonePileWallet(address newWallet) external onlyAdmin {
        emit bonePileWalletUpdated(newWallet, bonePileWallet);
        bonePileWallet = newWallet;
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) override internal virtual {
        if (blacklistEnabled) {
            require(!blacklists[to] && !blacklists[from], "Blacklisted");
        }
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        if (limitsInEffect) {
            if (
                from != address(admin) &&
                to != address(admin) &&
                to != address(0) &&
                to != address(0xdead) &&
                !swapping
            ) {
                if (!tradingActive) {
                    require(
                        _isExcludedFromFees[from] || _isExcludedFromFees[to],
                        "Trading is not active."
                    );
                }

                //when buy
                if (
                    automatedMarketMakerPairs[from] &&
                    !_isExcludedMaxTransactionAmount[to]
                ) {
                    require(
                        amount <= maxTransactionAmount,
                        "Buy transfer amount exceeds the maxTransactionAmount."
                    );
                    require(
                        amount + balanceOf(to) <= maxWallet,
                        "Max wallet exceeded"
                    );
                }
                //when sell
                else if (
                    automatedMarketMakerPairs[to] &&
                    !_isExcludedMaxTransactionAmount[from]
                ) {
                    require(
                        amount <= maxTransactionAmount,
                        "Sell transfer amount exceeds the maxTransactionAmount."
                    );
                } else if (!_isExcludedMaxTransactionAmount[to]) {
                    require(
                        amount + balanceOf(to) <= maxWallet,
                        "Max wallet exceeded"
                    );
                }
            }
        }

        uint256 contractTokenBalance = balanceOf(address(this));

        bool canSwap = contractTokenBalance >= swapTokensAtAmount;

        if (
            canSwap &&
            swapEnabled &&
            !swapping &&
            !automatedMarketMakerPairs[from] &&
            !_isExcludedFromFees[from] &&
            !_isExcludedFromFees[to]
        ) {
            swapping = true;

            swapBack();

            swapping = false;
        }

        bool takeFee = !swapping;

        // if any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        uint256 fees = 0;

        // only take fees on buys/sells, do not take on wallet transfers
        if (takeFee) {
            // on sell
            if (automatedMarketMakerPairs[to] && sellTotalFees > 0) {
                fees = amount.mul(sellTotalFees).div(100);
                tokensForLiquidity += (fees * sellLiquidityFee) / sellTotalFees;
                tokensForDev += (fees * sellDevFee) / sellTotalFees;
                tokensForMarketing += (fees * sellMarketingFee) / sellTotalFees;
                tokensForBonePile += (fees * sellBonePileFee) / sellTotalFees;
            }
            // on buy
            else if (automatedMarketMakerPairs[from] && buyTotalFees > 0) {
                fees = amount.mul(buyTotalFees).div(100);
                tokensForLiquidity += (fees * buyLiquidityFee) / buyTotalFees;
                tokensForDev += (fees * buyDevFee) / buyTotalFees;
                tokensForMarketing += (fees * buyMarketingFee) / buyTotalFees;
                tokensForBonePile += (fees * buyBonePileFee) / buyTotalFees;
            }

            if (fees > 0) {
                super._transfer(from, address(this), fees);
            }

            amount -= fees;
        }

        super._transfer(from, to, amount);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            liquidity,
            block.timestamp
        );
    }

    function swapBack() private {
        uint256 contractBalance = balanceOf(address(this));
        uint256 totalTokensToSwap = tokensForLiquidity +
            tokensForMarketing +
            tokensForDev +
            tokensForBonePile;
        bool success;

        if (contractBalance == 0 || totalTokensToSwap == 0) {
            return;
        }

        // Halve the amount of liquidity tokens
        uint256 liquidityTokens = (contractBalance * tokensForLiquidity) /
            totalTokensToSwap /
            2;
        uint256 amountToSwapForETH = contractBalance.sub(liquidityTokens);

        uint256 initialETHBalance = address(this).balance;

        swapTokensForEth(amountToSwapForETH);

        uint256 ethBalance = address(this).balance.sub(initialETHBalance);

        uint256 ethForMarketing = ethBalance.mul(tokensForMarketing).div(
            totalTokensToSwap
        );
        uint256 ethForDev = ethBalance.mul(tokensForDev).div(totalTokensToSwap);

        uint256 ethForBonePile = ethBalance.mul(tokensForBonePile).div(totalTokensToSwap);

        uint256 ethForLiquidity = ethBalance - ethForMarketing - ethForDev - ethForBonePile;

        tokensForLiquidity = 0;
        tokensForMarketing = 0;
        tokensForDev = 0;
        tokensForBonePile = 0;

        (success, ) = address(devWallet).call{value: ethForDev}("");

        if (liquidityTokens > 0 && ethForLiquidity > 0) {
            addLiquidity(liquidityTokens, ethForLiquidity);
            emit SwapAndLiquify(
                amountToSwapForETH,
                ethForLiquidity,
                tokensForLiquidity
            );
        }


        (success, ) = address(bonePileWallet).call{value: ethForBonePile}("");

        (success, ) = address(marketingWallet).call{
            value: address(this).balance
        }("");
    }
}
"
    },
    "contracts/SkrimplesBoneyard.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./Skrimples.sol";

// Skrimples is the stinkiest, scrappiest pup in all of Shibarium. 
//
// The Boneyard is where Skrimples and his Ruffhouse Gang bury their tokens to earn $SKRIMP.
//
// Stake your $Skrimp to earn more!
//
// This staking contract is a safely modified rendition of MasterChef

contract SkrimplesBoneyard is AccessControl {

    using SafeMath for uint256;
    using SafeERC20 for IERC20;



    // Info of each user.
    struct UserInfo {
        uint256 amount;     // How many tokens the user has pledged.
        uint256 rewardDebt; // Reward debt. See explanation below.
        //
        // We do some genius math here. Basically, any point in time, the amount of Skrimples
        // entitled to a user, but pending distibution is:
        //
        // pending reward = (user.amount * pool.accSkrimplesPerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws ptokens to a pool. Here's what happens:
        //   1. The pool's `accSkrimplesPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }

    // Info of each pool.
    struct PoolInfo {
        IERC20 pToken;           // Address of token contract.
        uint256 allocPoint;       // Allocation points assigned to this pool. Skrimples distributed per block.
        uint256 lastRewardBlock;  // Last block number that Skrimples distribution occurs.
        uint256 accSkrimplesPerShare; // Accumulated Skrimples per share, times 1e12. See below.
        uint256 totalStaked;     // Total number of tokens staked in pool
    }

    Skrimples public skrimples;
    // Dev address.
    address public devaddr;
    // Treasury address, where the rewards come from.
    address public treasury;
    // Block number when bonus Skrimples period ends.
    uint256 public bonusEndBlock;
    // Skrimples tokens created per block.
    uint256 public skrimplesPerBlock;
    // Bonus muliplier for early skrimples stakers.
    uint256 public constant BONUS_MULTIPLIER = 10;
    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes tokens.
    mapping (uint256 => mapping (address => UserInfo)) public userInfo;
    // Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;
    // The block number when Skrimples mining starts.
    uint256 public startBlock;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);


    constructor(
        Skrimples _skrimples,
        address _devaddr,
        address _treasury,
        uint256 _skrimplesPerBlock,
        uint256 _startBlock,
        uint256 _bonusEndBlock
    ) {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        skrimples = _skrimples;
        devaddr = _devaddr;
        treasury = _treasury;
        skrimplesPerBlock = _skrimplesPerBlock;
        bonusEndBlock = _bonusEndBlock;
        startBlock = _startBlock;     
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    // Add a new token to the pool. Can only be called by the owner.
    // DO NOT add the same token more than once. Rewards will be messed up if you do.
    function addToken(uint256 _allocPoint, IERC20 _pToken, bool _withUpdate) public {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Caller is not admin");
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 lastRewardBlock = block.number > startBlock ? block.number : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(PoolInfo({
            pToken: _pToken,
            allocPoint: _allocPoint,
            lastRewardBlock: lastRewardBlock,
            accSkrimplesPerShare: 0,         
            totalStaked: 0
        }));
    }

    // Update the given pool's Skrimples allocation point. Can only be called by the owner.
    function set(uint256 _pid, uint256 _allocPoint, bool _withUpdate) public {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Caller is not admin");
        if (_withUpdate) {
            massUpdatePools();
        }
        totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(_allocPoint);
        poolInfo[_pid].allocPoint = _allocPoint;
    }
    // use the multiplier to reward early stakers
    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {
        if (_to <= bonusEndBlock) {
            return _to.sub(_from).mul(BONUS_MULTIPLIER);
        } else if (_from >= bonusEndBlock) {
            return _to.sub(_from);
        } else {
            return bonusEndBlock.sub(_from).mul(BONUS_MULTIPLIER).add(
                _to.sub(bonusEndBlock)
            );
        }
    }

    // View function to see pending Skrimples on frontend.
    function pendingSkrimples(uint256 _pid, address _user) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accSkrimplesPerShare = pool.accSkrimplesPerShare;
        uint256 pSupply = pool.pToken.balanceOf(address(this));
        if (block.number > pool.lastRewardBlock && pSupply != 0) {
            uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
            uint256 skrimplesReward = multiplier.mul(skrimplesPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
            accSkrimplesPerShare = accSkrimplesPerShare.add(skrimplesReward.mul(1e12).div(pSupply));
        }
        return user.amount.mul(accSkrimplesPerShare).div(1e12).sub(user.rewardDebt);
    }

    // Update reward variables for all pools. Be careful of gas spending!
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 pSupply = pool.pToken.balanceOf(address(this));
        if (pSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
        uint256 skrimplesReward = multiplier.mul(skrimplesPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
        skrimples.transferFrom(treasury, devaddr, skrimplesReward.div(10));
        skrimples.transferFrom(treasury, address(this), skrimplesReward);
        pool.accSkrimplesPerShare = pool.accSkrimplesPerShare.add(skrimplesReward.mul(1e12).div(pSupply));
        pool.lastRewardBlock = block.number;
    }

    // Deposit tokens to SkrimpleBoneyard for Skrimples allocation.
    function deposit(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);
        if (user.amount > 0) {
            uint256 pending = user.amount.mul(pool.accSkrimplesPerShare).div(1e12).sub(user.rewardDebt);
            if(pending > 0) {
                safeSkrimplesTransfer(msg.sender, pending);
            }
        }
        if(_amount > 0) {
            pool.pToken.safeTransferFrom(address(msg.sender), address(this), _amount);
            user.amount = user.amount.add(_amount);
        }
        user.rewardDebt = user.amount.mul(pool.accSkrimplesPerShare).div(1e12);
        pool.totalStaked = pool.totalStaked.add(_amount);
        emit Deposit(msg.sender, _pid, _amount);
    }

    // Withdraw tokens from SkrimplesBoneyard.
    function withdraw(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");
        updatePool(_pid);
        uint256 pending = user.amount.mul(pool.accSkrimplesPerShare).div(1e12).sub(user.rewardDebt);
        if(pending > 0) {
            safeSkrimplesTransfer(msg.sender, pending);
        }
        if(_amount > 0) {
            user.amount = user.amount.sub(_amount);
            pool.pToken.safeTransfer(address(msg.sender), _amount);
        }
        user.rewardDebt = user.amount.mul(pool.accSkrimplesPerShare).div(1e12);
        pool.totalStaked = pool.totalStaked.sub(_amount);
        emit Withdraw(msg.sender, _pid, _amount);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        pool.pToken.safeTransfer(address(msg.sender), user.amount);
        emit EmergencyWithdraw(msg.sender, _pid, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
        pool.totalStaked = pool.totalStaked.sub(user.amount);
    }

   function updateSkrimplesPerBlock(
        uint256 _skrimplesPerBlock
    ) external {
        require(
            hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "Caller is not admin"
        );
        skrimplesPerBlock = _skrimplesPerBlock;
    }

    function updateBonusEndBlock(
        uint256 _bonusEndBlock
        ) external {
            require(
                hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
                "Caller is not admin"
            );
            bonusEndBlock = _bonusEndBlock;
        }


    // Safe Skrimples transfer function, just in case if rounding error causes pool to not have enough Skrimples.
    function safeSkrimplesTransfer(address _to, uint256 _amount) internal {
        uint256 skrimplesBal = skrimples.balanceOf(address(this));
        if (_amount > skrimplesBal) {
            skrimples.transfer(_to, skrimplesBal);
        } else {
            skrimples.transfer(_to, _amount);
        }
    }

    // Update dev address.
    function dev(address _devaddr) public {
        require(
                hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
                "Caller is not admin"
            );
        devaddr = _devaddr;
    }
    // Update treasury address.
    function updateTreasury(address _treasury) public {
        require(
                hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
                "Caller is not admin"
            );
        treasury = _treasury;
    }
}"
    }
  },
