pragma solidity ^0.8.14;

contract WhitelistStaking {
    using SafeERC20 for IERC20;

    uint256 public constant STAKE_MAX_MULTIPLES = 3;
    uint256 public constant STAKE_MULTIPLE = 1500 ether;
    uint256 public constant STAKE_DURATION = 90 days;
    uint256 public constant STAKE_DISABLE_TIMESTAMP = 1655506740; // Friday, June 17, 2022 10:59:00 PM | UTC

    IERC20 private immutable mainToken;

    struct UserInfo {
        uint256 stakingStart;
        uint256 level;
    }

    mapping(address => UserInfo) public userInfo; // userAddress => UserInfo

    constructor(IERC20 _tokenContract) {
        mainToken = _tokenContract;
    }

    event Stake(address indexed userAddress, uint256 newLevel);

    // Deposit staking funds
    function stake(uint256 _levelsToIncrease) external {
        require(_levelsToIncrease > 0, "Zero level increase");
        require(block.timestamp <= STAKE_DISABLE_TIMESTAMP, "Staking is over");

        UserInfo storage user = userInfo[msg.sender];

        uint256 newUserLevel = user.level + _levelsToIncrease;
        require(
            newUserLevel <= STAKE_MAX_MULTIPLES,
            "Level increase is too high"
        );

        user.stakingStart = block.timestamp;
        user.level = newUserLevel;

        uint256 amountToStake = _levelsToIncrease * STAKE_MULTIPLE;
        mainToken.safeTransferFrom(msg.sender, address(this), amountToStake);

        emit Stake(msg.sender, newUserLevel);
    }

    event Unstake(address indexed userAddress);

    // Withdraw staking funds
    function unstake() external {
        UserInfo storage user = userInfo[msg.sender];

        uint256 userLevel = user.level;
        require(userLevel > 0, "User is not staking");

        require(
            block.timestamp >= user.stakingStart + STAKE_DURATION,
            "Stake is still locked"
        );

        delete userInfo[msg.sender];

        uint256 amountToUnstake = userLevel * STAKE_MULTIPLE;
        mainToken.safeTransfer(msg.sender, amountToUnstake);

        emit Unstake(msg.sender);
    }
}
