abstract contract Tradable {
    using SafeMath for uint256;

    struct Recipient {
        address recipient;
        uint256 points;
    }

    address internal _proxyAddress;
    Recipient[] private _saleRecipients;
    uint256 private _sellerFeeBasisPoints;
    Recipient[] private _feeRecipients;
    string private _contractURI;

    mapping(uint256 => address) internal _creators;
    mapping(uint256 => string) internal _tokenURIs;

    constructor(
        address proxy,
        Recipient[] memory saleRecipients,
        uint256 sellerFeeBasisPoints,
        Recipient[] memory feeRecipients,
        string memory url
    ) {
        require(
            sellerFeeBasisPoints <= 99,
            "Fee must be less than or equal to 99"
        );
        _proxyAddress = proxy;
        uint256 allSalePoints = 0;
        for (uint256 i = 0; i < saleRecipients.length; i++) {
            _saleRecipients.push(saleRecipients[i]);
            allSalePoints += saleRecipients[i].points;
        }
        require(allSalePoints == 100, "The sum of sale shares must be 100");
        _sellerFeeBasisPoints = sellerFeeBasisPoints;
        uint256 allFeePoints = 0;
        for (uint256 i = 0; i < feeRecipients.length; i++) {
            _feeRecipients.push(feeRecipients[i]);
            allFeePoints += feeRecipients[i].points;
        }
        require(allFeePoints == 100, "The sum of fee shares must be 100");
        _contractURI = url;
    }

    function getProxyAddress() public view returns (address) {
        return _proxyAddress;
    }

    function getSaleRecipients() public view returns (Recipient[] memory) {
        return _saleRecipients;
    }

    function getFeeRecipients() public view returns (Recipient[] memory) {
        return _feeRecipients;
    }

    function getSellerFeeBasisPoints() public view returns (uint256) {
        return _sellerFeeBasisPoints;
    }

    function _setTokenURI(uint256 tokenId, string memory _tokenURI)
        internal
        virtual
    {
        _tokenURIs[tokenId] = _tokenURI;
    }

    /**
     * 获取第一次销售分成的数量
     */
    function getFistAmount(address owner, uint256 tokenId)
        public
        view
        virtual
        returns (uint256);

    function contractURI() public view returns (string memory) {
        return _contractURI;
    }
}

contract ERC1155Tradable is Tradable, ERC1155Pausable, Ownable {
    using Counters for Counters.Counter;
    using Strings for uint256;
    Counters.Counter private _tokenIds;

    mapping(uint256 => mapping(address => uint256)) private _firstSales;

    string private _name;
    string private _symbol;

    constructor(
        string memory name_,
        string memory symbol_,
        address proxy,
        Recipient[] memory saleRecipients,
        uint256 sellerFeeBasisPoints,
        Recipient[] memory feeRecipients,
        string memory url
    )
        Tradable(
            proxy,
            saleRecipients,
            sellerFeeBasisPoints,
            feeRecipients,
            url
        )
        ERC1155("")
    {
        _name = name_;
        _symbol = symbol_;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function uri(uint256 tokenId) public view override returns (string memory) {
        return _tokenURIs[tokenId];
    }

    function mint(
        uint256 tokenId,
        address to,
        uint256 amount,
        string memory url
    ) public whenNotPaused onlyOwner returns (uint256) {
        _mint(to, tokenId, amount, "");
        _setTokenURI(tokenId, url);
        _creators[tokenId] = to;
        _firstSales[tokenId][to] = amount;
        tokenSupply[tokenId] = amount;
        return tokenId;
    }

    function isApprovedForAll(address owner, address operator)
        public
        view
        override
        returns (bool)
    {
        ProxyRegistry proxyRegistry = ProxyRegistry(_proxyAddress);
        if (proxyRegistry.proxies(operator)) {
            return true;
        }

        return super.isApprovedForAll(owner, operator);
    }

    function pause() public virtual onlyOwner whenNotPaused {
        _pause();
    }

    function unpause() public virtual onlyOwner whenPaused {
        _unpause();
    }

    function _safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        uint256 amount,
        bytes memory data
    ) internal override {
        super._safeTransferFrom(from, to, tokenId, amount, data);
        if (_creators[tokenId] == from) {
            if (_firstSales[tokenId][from] >= amount) {
                _firstSales[tokenId][from] -= amount;
            } else {
                _firstSales[tokenId][from] = 0;
            }
        }
    }

    function getFistAmount(address owner, uint256 tokenId)
        public
        view
        override
        returns (uint256)
    {
        return _firstSales[tokenId][owner];
    }

    mapping(uint256 => uint256) public tokenSupply;

    function totalSupply(uint256 _id) public view returns (uint256) {
        return tokenSupply[_id];
    }

    function burn(
        address account,
        uint256 id,
        uint256 value
    ) public {
        require(
            account == _msgSender() || isApprovedForAll(account, _msgSender()),
            "ERC1155: caller is not owner nor approved"
        );
        _burn(account, id, value);
        tokenSupply[id] -= value;
        if (tokenSupply[id] == 0) {
            delete _tokenURIs[id];
        }
        delete _creators[id];
    }

    function burnBatch(
        address account,
        uint256[] memory ids,
        uint256[] memory values
    ) public {
        require(
            account == _msgSender() || isApprovedForAll(account, _msgSender()),
            "ERC1155: caller is not owner nor approved"
        );
        _burnBatch(account, ids, values);
        for (uint256 i = 0; i < ids.length; i++) {
            uint256 id = ids[i];
            delete _creators[ids[i]];
            tokenSupply[id] -= values[i];
            if (tokenSupply[id] == 0) {
                delete _tokenURIs[id];
            }
        }
    }
}
