    "contracts/interfaces/IDegenopolyNode.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.20;

import {IERC721Upgradeable} from '@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol';

interface IDegenopolyNode is IERC721Upgradeable {
    function color() external view returns (string memory);

    function rewardPerSec() external view returns (uint256);

    function purchasePrice() external view returns (uint256);

    function totalSupply() external view returns (uint256);

    function mint(address to) external;

    function burn(uint256 tokenId) external;

    function syncReward(address _account) external;

    function claimReward(address _account) external returns (uint256 pending);

    function claimableReward(
        address _account
    ) external view returns (uint256 pending);
}
"
    },
    "contracts/interfaces/IDegenopolyNodeFamily.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.20;

import {IERC721Upgradeable} from '@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol';

interface IDegenopolyNodeFamily is IERC721Upgradeable {
    function color() external view returns (string memory);

    function rewardBoost() external view returns (uint256);

    function mint(address to) external;
}
"
    },
    "contracts/interfaces/IDegenopolyNodeManager.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.20;

interface IDegenopolyNodeManager {
    function getMultiplierFor(address _account) external view returns (uint256);

    function balanceOf(
        address _account
    ) external view returns (uint256 balance);

    function claimableReward(
        address _account
    ) external view returns (uint256 pending);

    function mintNodeFamily(address _account) external;

    function burnNodeFamily(address _account) external;

    function addMultiplier(address _account, uint256 _multiplier) external;
}
"
