    "contracts/GrandChaseXYZ.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "erc721a/contracts/ERC721A.sol";
import "./MerkleAllowlist.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "erc721a/contracts/extensions/ERC721ABurnable.sol";
import {DefaultOperatorFilterer} from "./DefaultOperatorFilterer.sol";


contract GrandChaseXYZ is Ownable, ERC721A, ReentrancyGuard, ERC721ABurnable, MerkleAllowlist, DefaultOperatorFilterer{

    //Contract URI
    string public CONTRACT_URI = "https://grand-chase-xyz.s3.amazonaws.com/contract.json";  //TODO upload contract and get link

    mapping(address => bool) public firstWaveHasMinted;
    mapping(address => bool) public secondWaveHasMinted;
    mapping(address => bool) public thirdWaveHasMinted;
    mapping(address => bool) public fourthWaveHasMinted;


    bool public REVEALED;
    string public UNREVEALED_URI = "https://grand-chase-xyz.s3.amazonaws.com/GC_Final_2K.mp4";
    string public BASE_URI;

    bool public isFirstChaseEnabled = false;
    bool public isSecondChaseEnabled = false;
    bool public isThirdChaseEnabled = false;
    bool public isFourthChaseEnabled = false;

    uint public MAX_POPULATION = 7777;
    uint public PLEDGE = 0.0269 ether;
    uint public FIRSTWAVE_BATCH_SIZE = 1;
    uint public SECONDWAVE_BATCH_SIZE = 2;
    uint public THIRDWAVE_BATCH_SIZE = 3;
    uint public FOURTHWAVE_BATCH_SIZE = 5;


    constructor() ERC721A("GrandChaseXYZ", "GCXYZ") {}

    function teamMint(uint256 quantity, address receiver) public onlyOwner {
        require(
            totalSupply() + quantity <= MAX_POPULATION,
            "Max collection size reached!"
        );
        //mint the quantity
        _safeMint(receiver, quantity);
    }

    modifier callerIsUser() {
        require(tx.origin == msg.sender, "The caller is another contract");
        _;
    }

    function chaseWave1(uint256 quantity, bytes32[] memory proof)
        public
        payable
        onlyWave1Allowlist(proof)
    {
        //uint256 pledge = 0.00 ether;
        require(!firstWaveHasMinted[msg.sender], "Can only mint once during Wave 1!"); 
        require(isFirstChaseEnabled == true, "First Wave sale has not begun yet");  
        require(totalSupply() + quantity <= MAX_POPULATION, "Max Population Size reached!");
        require(quantity <= FIRSTWAVE_BATCH_SIZE, "Tried to mint quanity over limit, retry with reduced quantity");

        firstWaveHasMinted[msg.sender] = true;

        //Mint them
        _safeMint(msg.sender, quantity);
    }

    function chaseWave2(uint256 quantity, bytes32[] memory proof)
        public
        payable
        onlyWave2Allowlist(proof)
    {
        //uint256 pledge = 0.00 ether;
        require(!secondWaveHasMinted[msg.sender], "Can only mint once during Wave 2!"); 
        require(isSecondChaseEnabled == true, "Second Wave sale has not begun yet");  
        require(totalSupply() + quantity <= MAX_POPULATION, "Max Population Size reached!");
        require(quantity <= SECONDWAVE_BATCH_SIZE, "Tried to mint quanity over limit, retry with reduced quantity");

        secondWaveHasMinted[msg.sender] = true;

        //Mint them
        _safeMint(msg.sender, quantity);
    }

    function chaseWave3(uint256 quantity, bytes32[] memory proof)
        public
        payable
        onlyWave3Allowlist(proof)
    {
        uint256 pledge = (PLEDGE) * quantity;
        require(!thirdWaveHasMinted[msg.sender], "Can only mint once during Wave 3!"); 
        require(isThirdChaseEnabled == true, "Third Wave sale has not begun yet");  
        require(totalSupply() + quantity <= MAX_POPULATION, "Max Population Size reached!");
        require(quantity <= THIRDWAVE_BATCH_SIZE, "Tried to mint quanity over limit, retry with reduced quantity");

        thirdWaveHasMinted[msg.sender] = true;

        //Mint them
        _safeMint(msg.sender, quantity);
        if (msg.value > pledge) {
            payable(msg.sender).transfer(msg.value - pledge);
        }
    }

    function chaseFinal(uint256 quantity)
        external
        payable
        callerIsUser 
        nonReentrant
    {
        uint256 pledge = (PLEDGE) * quantity;
        require(!fourthWaveHasMinted[msg.sender], "Can only mint once");
        require(isFourthChaseEnabled == true, "Public sale has not begun yet");
        require(totalSupply() + quantity <= MAX_POPULATION, "Max Population Size reached!");
        require(quantity <= FOURTHWAVE_BATCH_SIZE, "Tried to mint quanity over limit, retry with reduced quantity");
        require(msg.value >= pledge, "Must send enough eth for public mint");

        fourthWaveHasMinted[msg.sender] = true;

        _safeMint(msg.sender, quantity);
        if (msg.value > pledge) {
            payable(msg.sender).transfer(msg.value - pledge);
        }
    }

    function withdrawFunds() external onlyOwner nonReentrant {
        (bool success, ) = msg.sender.call{value: address(this).balance}("");
        require(success, "Transfer failed.");
    }

    function setFirstWaveEnabled(bool _isFirstChaseEnabled) public onlyOwner {
        isFirstChaseEnabled = _isFirstChaseEnabled;
    }

    function setSecondWaveEnabled(bool _isSecondChaseEnabled) public onlyOwner {
        isSecondChaseEnabled = _isSecondChaseEnabled;
    }

    function setFirstAndSecondWaveEnabled(bool _isWaveEnabled) public onlyOwner {
        isFirstChaseEnabled = _isWaveEnabled;
        isSecondChaseEnabled = _isWaveEnabled;
    }

    /*function setThirdWaveEnabled(bool _isThirdChaseEnabled) public onlyOwner {
        isThirdChaseEnabled = _isThirdChaseEnabled;
    }*/

    function LetTheChaseStart(bool _isFourthChaseEnabled) public onlyOwner {
        isFourthChaseEnabled = _isFourthChaseEnabled;
    }

    function setFourthWaveEnabled(bool _isFourthChaseEnabled) public onlyOwner {
        isFourthChaseEnabled = _isFourthChaseEnabled;
    }

    function setBaseURI(bool _revealed, string memory _baseURI) public onlyOwner {
        BASE_URI = _baseURI;
        REVEALED = _revealed;
    }

    function contractURI() public view returns (string memory) {
        return CONTRACT_URI;
    }

    function setContractURI(string memory _contractURI) public onlyOwner {
        CONTRACT_URI = _contractURI;
    }

    function transferFrom(address from, address to, uint256 tokenId) public payable override (ERC721A, IERC721A) onlyAllowedOperator {
        super.transferFrom(from, to, tokenId);
    }

    function safeTransferFrom(address from, address to, uint256 tokenId) public payable override (ERC721A, IERC721A) onlyAllowedOperator {
        super.safeTransferFrom(from, to, tokenId);
    }

    function safeTransferFrom(address from, address to, uint256 tokenId, bytes memory data)
        public payable
        override (ERC721A, IERC721A)
        onlyAllowedOperator
    {
        super.safeTransferFrom(from, to, tokenId, data);
    }

    function totalPopuluation() public view returns (uint256) {
        return totalSupply();
    }

    function deGrand() public view returns (address) {
        return owner();
    }

    function brand() public view returns (string memory) {
        return symbol();
    }

    function tokenURI(uint256 _tokenId)
        public
        view
        override (ERC721A, IERC721A)
        returns (string memory)
    {
        if (REVEALED) {
            return
                string(abi.encodePacked(BASE_URI, Strings.toString(_tokenId)));
        } else {
            return UNREVEALED_URI;
        }
    }
}
"
    },
    "contracts/IOperatorFilterRegistry.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

interface IOperatorFilterRegistry {
    function isOperatorAllowed(address registrant, address operator) external returns (bool);
    function register(address registrant) external;
    function registerAndSubscribe(address registrant, address subscription) external;
    function registerAndCopyEntries(address registrant, address registrantToCopy) external;
    function updateOperator(address registrant, address operator, bool filtered) external;
    function updateOperators(address registrant, address[] calldata operators, bool filtered) external;
    function updateCodeHash(address registrant, bytes32 codehash, bool filtered) external;
    function updateCodeHashes(address registrant, bytes32[] calldata codeHashes, bool filtered) external;
    function subscribe(address registrant, address registrantToSubscribe) external;
    function unsubscribe(address registrant, bool copyExistingEntries) external;
    function subscriptionOf(address addr) external returns (address registrant);
    function subscribers(address registrant) external returns (address[] memory);
    function subscriberAt(address registrant, uint256 index) external returns (address);
    function copyEntriesOf(address registrant, address registrantToCopy) external;
    function isOperatorFiltered(address registrant, address operator) external returns (bool);
    function isCodeHashOfFiltered(address registrant, address operatorWithCode) external returns (bool);
    function isCodeHashFiltered(address registrant, bytes32 codeHash) external returns (bool);
    function filteredOperators(address addr) external returns (address[] memory);
    function filteredCodeHashes(address addr) external returns (bytes32[] memory);
    function filteredOperatorAt(address registrant, uint256 index) external returns (address);
    function filteredCodeHashAt(address registrant, uint256 index) external returns (bytes32);
    function isRegistered(address addr) external returns (bool);
    function codeHashOf(address addr) external returns (bytes32);
}
"
