    "contracts/MerkleAllowlist.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract MerkleAllowlist is Ownable {
  bytes32 public wave1MerkleRoot;
  bytes32 public wave2MerkleRoot;
  bytes32 public wave3MerkleRoot;


  //Frontend verify functions
  function wave1VerifyPublicSender(address userAddress, bytes32[] memory proof) public view returns (bool) {
    return _verify(proof, _hash(userAddress), wave1MerkleRoot);
  }
  function wave2VerifyPublicSender(address userAddress, bytes32[] memory proof) public view returns (bool) {
    return _verify(proof, _hash(userAddress), wave2MerkleRoot);
  }
  function wave3VerifyPublicSender(address userAddress, bytes32[] memory proof) public view returns (bool) {
    return _verify(proof, _hash(userAddress), wave3MerkleRoot);
  }

  //Internal verify functions
  function _wave1VerifyPublicSender(bytes32[] memory proof) internal view returns (bool) {
    return _verify(proof, _hash(msg.sender), wave1MerkleRoot);
  }
  function _wave2VerifyPublicSender(bytes32[] memory proof) internal view returns (bool) {
    return _verify(proof, _hash(msg.sender), wave2MerkleRoot);
  }
  function _wave3VerifyPublicSender(bytes32[] memory proof) internal view returns (bool) {
    return _verify(proof, _hash(msg.sender), wave3MerkleRoot);
  }

  function _verify(bytes32[] memory proof, bytes32 addressHash, bytes32 allowlistMerkleRoot)
    internal
    pure
    returns (bool)
  {
    return MerkleProof.verify(proof, allowlistMerkleRoot, addressHash);
  }

  function _hash(address _address) internal pure returns (bytes32) {
    return keccak256(abi.encodePacked(_address));
  }

  /*
  OWNER FUNCTIONS
  */

  function setWave1AllowlistMerkleRoot(bytes32 merkleRoot) external onlyOwner {
    wave1MerkleRoot = merkleRoot;
  }
  function setWave2AllowlistMerkleRoot(bytes32 merkleRoot) external onlyOwner {
    wave2MerkleRoot = merkleRoot;
  }
  function setWave3AllowlistMerkleRoot(bytes32 merkleRoot) external onlyOwner {
    wave3MerkleRoot = merkleRoot;
  }

  /*
  MODIFIER
  */

  modifier onlyWave1Allowlist(bytes32[] memory proof) {
    require(_wave1VerifyPublicSender(proof), "MerkleAllowlist: Caller is not allowlisted");
    _;
  }
  modifier onlyWave2Allowlist(bytes32[] memory proof) {
    require(_wave2VerifyPublicSender(proof), "MerkleAllowlist: Caller is not allowlisted");
    _;
  }
 modifier onlyWave3Allowlist(bytes32[] memory proof) {
    require(_wave3VerifyPublicSender(proof), "MerkleAllowlist: Caller is not allowlisted");
    _;
  }
}
"
    },
    "contracts/OperatorFilterer.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import {IOperatorFilterRegistry} from "./IOperatorFilterRegistry.sol";

contract OperatorFilterer {
    error OperatorNotAllowed(address operator);

    IOperatorFilterRegistry constant operatorFilterRegistry =
        IOperatorFilterRegistry(0x000000000000AAeB6D7670E522A718067333cd4E);

    constructor(address subscriptionOrRegistrantToCopy, bool subscribe) {
        // If an inheriting token contract is deployed to a network without the registry deployed, the modifier
        // will not revert, but the contract will need to be registered with the registry once it is deployed in
        // order for the modifier to filter addresses.
        if (address(operatorFilterRegistry).code.length > 0) {
            if (subscribe) {
                operatorFilterRegistry.registerAndSubscribe(address(this), subscriptionOrRegistrantToCopy);
            } else {
                if (subscriptionOrRegistrantToCopy != address(0)) {
                    operatorFilterRegistry.registerAndCopyEntries(address(this), subscriptionOrRegistrantToCopy);
                } else {
                    operatorFilterRegistry.register(address(this));
                }
            }
        }
    }

    modifier onlyAllowedOperator() virtual {
        // Check registry code length to facilitate testing in environments without a deployed registry.
        if (address(operatorFilterRegistry).code.length > 0) {
            if (!operatorFilterRegistry.isOperatorAllowed(address(this), msg.sender)) {
                revert OperatorNotAllowed(msg.sender);
            }
        }
        _;
    }
}
"
    },
