// File: contracts/PausableTransfer.sol


pragma solidity ^0.8.0;

abstract contract PausableTransfer is Context {
    event PausedTransfer(address account);
    event UnpausedTransfer(address account);
    bool private _pausedTransfer;
    constructor() {
        _pausedTransfer = true;
    }
    modifier whenNotPausedTransfer() {
        _requireNotPausedTransfer();
        _;
    }
    modifier whenPausedTransfer() {
        _requirePausedTransfer();
        _;
    }
    function pausedTransfer() public view virtual returns (bool) {
        return _pausedTransfer;
    }
    function _requireNotPausedTransfer() internal view virtual {
        require(!pausedTransfer(), "Pausable: paused");
    }
    function _requirePausedTransfer() internal view virtual {
        require(pausedTransfer(), "Pausable: not paused");
    }
    function _pauseTransfer() internal virtual whenNotPausedTransfer {
        _pausedTransfer = true;
        emit PausedTransfer(_msgSender());
    }
    function _unpauseTransfer() internal virtual whenPausedTransfer {
        _pausedTransfer = false;
        emit UnpausedTransfer(_msgSender());
    }
}
