// File: token/StandardToken.sol


pragma solidity ^0.8.20;







contract StandardToken is
    Initializable,
    ERC20Upgradeable,
    ERC20BurnableUpgradeable,
    ERC20PausableUpgradeable,
    OwnableUpgradeable,
    UUPSUpgradeable
{
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    enum distributionTitle {
        PrivateSale1,      // 0
        PrivateSale2,      // 1
        Listing,           // 2
        CommunitiesReward, // 3
        FutureDevelopment, // 4
        Marketing,         // 5
        DAO,               // 6
        Team               // 7
    }

    struct distributionData {
        uint256 percentage;
        uint256 totalSupply;
        uint256 remainingSupply;
    }

    mapping(uint8 => distributionData) public distributions;
    mapping(address => bool) public antiWhaleList;
    mapping(address => bool) public minter;

    uint256 public totalPercentage;
    uint256 public maxSupply;

    // maximum limit of per transaction
    // (!) input value precision follow "decimals"
    uint256 public rateMaxTransfer;

    event Mint(address to, uint256 amount, uint8 distributionIndex);
    event UpdateMinter(address account, bool status);
    event UpdateAntiWhaleList(address account, bool status);
    event UpdateRateMaxTransfer(uint256 rate);

    modifier checkAvailability(uint256 _quantity, uint8 distributionIndex) {
        require(
            _quantity <= distributions[distributionIndex].remainingSupply,
            "mint exceed supply"
        );
        _;
    }

    modifier onlyMinter() {
        require(minter[msg.sender], "only minter can mint");
        _;
    }

    modifier antiWhale(
        address from,
        address to,
        uint256 amount
    ) {
        if (maxTransferAmount() > 0) {
            if (antiWhaleList[from] || antiWhaleList[to]) {
                require(
                    amount <= maxTransferAmount(),
                    "antiWhale: Transfer amount exceeds the maxTransferAmount"
                );
            }
        }
        _;
    }

    function initialize(uint256[] memory _percentage, uint256 _maxSupply)
        public
        initializer
    {
        __ERC20_init("Jinko", "JINKO");
        __ERC20Burnable_init();
        __ERC20Pausable_init();
        __Ownable_init();
        __UUPSUpgradeable_init();

        minter[_msgSender()] = true;
        rateMaxTransfer = 10000;
        totalPercentage = 10000; // 100% * 2 decimal places
        maxSupply = _maxSupply;

        uint256 _currentPercentage = 0;
        for (uint8 i = 0; i < uint256(type(distributionTitle).max) + 1; i++) {
            _currentPercentage += _percentage[i];
            if (_currentPercentage > totalPercentage) {
                revert("Total percentage must be 100");
            }
            distributions[i].percentage = _percentage[i];
            distributions[i].totalSupply =
                (_percentage[i] * maxSupply) /
                totalPercentage;
            distributions[i].remainingSupply =
                (_percentage[i] * maxSupply) /
                totalPercentage;
        }

        if (_currentPercentage < totalPercentage) {
            revert("Total percentage must be 100");
        }
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    // get circulating supply (total supply - burn)
    function circulateSupply() public view returns (uint256) {
        return totalSupply() - (balanceOf(address(0)));
    }

    // get max transfer amount
    function maxTransferAmount() public view returns (uint256) {
        return (circulateSupply() * rateMaxTransfer) / totalPercentage;
    }

    // get distribution data by distribution index
    function getTokenDistribution(uint8 _distributionIndex)
        public
        view
        returns (uint256 percentage, uint256 totalSupply, uint256 remainingSupply)
    {
        distributionData memory _d = distributions[_distributionIndex];
        percentage      = _d.percentage;
        totalSupply     = _d.totalSupply;
        remainingSupply = _d.remainingSupply;
    }

    // mint token to address with distribution index
    function mint(
        address to,
        uint256 amount,
        uint8 distributionIndex
    ) public onlyMinter checkAvailability(amount, distributionIndex) {
        distributions[distributionIndex].remainingSupply -= amount;
        _mint(to, amount);
        emit Mint(to, amount, distributionIndex);
    }

    // override transfer function
    function transfer(address to, uint256 value)
        public
        virtual
        override
        antiWhale(msg.sender, to, value)
        returns (bool)
    {
        address owner = _msgSender();
        _transfer(owner, to, value);
        return true;
    }

    // override transferFrom function
    function transferFrom(address from, address to, uint256 value) 
        public 
        virtual 
        override 
        antiWhale(from, to, value) 
        returns (bool) 
    {
        address spender = _msgSender();
        _spendAllowance(from, spender, value);
        _transfer(from, to, value);
        return true;
    }

    // update minter status by owner only
    function updateMinter(address _address, bool isMinter)
        public
        virtual
        onlyOwner
        returns (bool)
    {
        minter[_address] = isMinter;
        emit UpdateMinter(_address, isMinter);
        return true;
    }

    // update anti whale list by owner only
    function updateAntiWhaleList(address _address, bool _isAntiWhaleList)
        public
        onlyOwner
        returns (bool)
    {
        antiWhaleList[_address] = _isAntiWhaleList;
        emit UpdateAntiWhaleList(_address, _isAntiWhaleList);
        return true;
    }

    // update rate max transfer in 2 decimal places by owner only
    function updateRateMaxTransfer(uint256 _rate)
        external
        onlyOwner
        returns (bool)
    {
        rateMaxTransfer = _rate;
        emit UpdateRateMaxTransfer(rateMaxTransfer);
        return true;
    }

    function _authorizeUpgrade(address newImplementation)
        internal
        override
        onlyOwner
    {}

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override(ERC20Upgradeable, ERC20PausableUpgradeable) {
        ERC20PausableUpgradeable._beforeTokenTransfer(from, to, amount);
    }
}
