        address presaleToken_,
        address beneficiary_,
        uint256 tokenPrice_,
        uint256 minPurchase_,
        uint256 mintingThreshold_
    ) internal {
        presaleToken = presaleToken_;
        beneficiary = beneficiary_;
        tokenPrice = tokenPrice_;
        minPurchase = minPurchase_;
        mintingThreshold = mintingThreshold_;
    }

    function _receiveToken(address token_, address from_, uint256 amount_) internal {
        if (token_ == address(0)) {
            require(msg.value >= amount_, "!Balance");
            SafeTransferLib.safeTransferETH(beneficiary, amount_);
        } else {
            SafeTransferLib.safeTransferFrom(token_, from_, beneficiary, amount_);
        }
    }

    function _updateContribution(address contributor_, uint256 amount_, bool isStaking_) internal {
        if (isStaking_) {
            stakedAmounts[contributor_] += amount_;
            StakingManager(stakingManager).depositByPresale(contributor_, amount_);
        } else {
            contributions[contributor_] += amount_;
        }
    }

    function _updateTotal(uint256 tokenAmount_) internal {
        tokensSold += tokenAmount_;
        require(tokensTotal >= tokensSold, "Exceed");
    }

    // VIEW
    function getDisplayInfo()
        external
        view
        returns (
            uint256 price,
            uint256 min,
            uint256 mintingCriteria,
            uint256 soldToken,
            uint256 totalToken,
            uint256 raisedUsd,
            uint256 totalRaisedUsd
        )
    {
        raisedUsd = (tokensSold * tokenPrice) / 1 ether;
        totalRaisedUsd = (tokensTotal * tokenPrice) / 1 ether;
        return (tokenPrice, minPurchase, mintingThreshold, tokensSold, tokensTotal, raisedUsd, totalRaisedUsd);
    }

    function getAccountInfo(address account_) public view returns (uint256 contribution, uint256 mintableAmount) {
        uint256 contibutionAmount = (contributions[account_] + stakedAmounts[account_]);
        mintableAmount = contibutionAmount / ((mintingThreshold * 1e18) / tokenPrice);
        return (contibutionAmount, mintableAmount - mintedNFTs[account_]);
    }

    function getTokenPresaleAmount(address paymentToken, uint256 paymentAmount) external view returns (uint256) {
        uint256 usdAmount = _getUsdAmount(paymentToken, paymentAmount);
        uint256 tokenAmount = _getTokenPresaleAmount(usdAmount);
        return tokenAmount;
    }

    function getTokenPresalePrice(address paymentToken, uint256 tokenReceiveAmount) external view returns (uint256) {
        uint256 usdAmount = _getTokenPresalePrice(tokenReceiveAmount);
        uint256 paymentAmount = _getTokenUsdAmount(paymentToken, usdAmount);
        return paymentAmount;
    }

    function _getTokenPresaleAmount(uint256 usdAmount) private view returns (uint256) {
        return (usdAmount * 1 ether) / tokenPrice;
    }

    function _getTokenPresalePrice(uint256 tokenAmount) private view returns (uint256) {
        return (tokenAmount * tokenPrice) / 1 ether;
    }

    /* solhint-disable no-empty-blocks */
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}
