// File: contracts/TheCrypyCreditCard.sol


pragma solidity ^0.8.18;







contract CreditCard is Ownable, IERC721Receiver, ReentrancyGuard {
    using SafeMath for uint256;

    // Events
    event NFTAdded(uint256 tokenId);
    event NFTRemoved(uint256 tokenId);
    event NFTPurchased(address buyer, uint256 tokenId);
    event Received();
    function onERC721Received(
        address _operator,
        address _from,
        uint256 _tokenId,
        bytes calldata _data
    ) external override returns (bytes4) {
        _operator;
        _from;
        _tokenId;
        _data;
        emit Received();
        return 0x150b7a02;
    }

    // State variables
    uint256[] public _nftTokenIds = [1006, 1007, 1008, 1009, 1010, 1011, 1012, 1013, 1014, 1015, 1016, 1017, 1018, 1019, 1020, 1021, 1022, 1023, 1024, 1025, 1026, 1027, 1028, 1029, 1030, 1031, 1032, 1033, 1034, 1035, 1036, 1037, 1038, 1039, 1040, 1041, 1042, 1043, 1044, 1045];
    IERC721 public _nftCollection = IERC721(0x06827dEA49f5ff963BF15BeB7Cfc3B211C50B41C);
    uint256 public _nftPrice = 0.03 ether;
    bool public _isActive = true;

    constructor(address _initialOwner) Ownable(_initialOwner) {}

    // OnlyOwner function to set the NFT collection address
    function setNFTCollectionAddress(IERC721 newAddress) external onlyOwner {
        _nftCollection = newAddress;
    }

    // OnlyOwner function to change the NFT price
    function setNFTPrice(uint256 newPrice) external onlyOwner {
        _nftPrice = newPrice;
    }

    // OnlyOwner function to toggle the active state
    function toggleActiveState() external onlyOwner {
        _isActive = !_isActive;
    }

    // OnlyOwner function to add NFTs to the array
    function addNFTs(uint256[] calldata tokenIds) external onlyOwner {
        for (uint256 i = 0; i < tokenIds.length; i++) {
            _nftTokenIds.push(tokenIds[i]);
            emit NFTAdded(tokenIds[i]);
        }
    }

    // OnlyOwner function to remove NFTs from the array
    function removeNFTs(uint256[] calldata tokenIds) external onlyOwner {
        for (uint256 i = 0; i < tokenIds.length; i++) {
            for (uint256 j = 0; j < _nftTokenIds.length; j++) {
                if (_nftTokenIds[j] == tokenIds[i]) {
                    _nftTokenIds[j] = _nftTokenIds[_nftTokenIds.length - 1];
                    _nftTokenIds.pop();
                    emit NFTRemoved(tokenIds[i]);
                    break;
                }
            }
        }
    }
    // Function to buy NFTs
    function buyNFT(address _address, uint256 _quantity) external payable nonReentrant {
        require(_isActive, "Purchasing is not active.");
        require(_quantity > 0 && _quantity <= _nftTokenIds.length, "Invalid quantity.");
        require(msg.value >= _nftPrice.mul(_quantity), "Ether sent is not correct.");

        for (uint256 i = 0; i < _quantity; i++) {
            uint256 tokenId = _nftTokenIds[_nftTokenIds.length - 1];
            _nftCollection.safeTransferFrom(address(this), _address, tokenId);
            _nftTokenIds.pop();
            emit NFTPurchased(_address, tokenId);
        }
    }

    function withdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No funds to withdraw");
        payable(msg.sender).transfer(balance);
    }

    // Function to withdraw a specific NFT from the contract
    function withdrawNFT(uint256 tokenId) external onlyOwner {
        // Check if the token exists in the array
        bool isTokenFound = false;
        for (uint256 i = 0; i < _nftTokenIds.length; i++) {
            if (_nftTokenIds[i] == tokenId) {
                isTokenFound = true;
                
                // Remove the token from the array by swapping it with the last element and then popping it from the array
                _nftTokenIds[i] = _nftTokenIds[_nftTokenIds.length - 1];
                _nftTokenIds.pop();

                // Transfer the NFT to the owner
                _nftCollection.safeTransferFrom(address(this), msg.sender, tokenId);
                break;
            }
        }

        // Revert the transaction if the token ID was not found in the array
        require(isTokenFound, "Token ID not found in the contract");
    }

    

    
}
