    "/contracts/HGP.sol": {
      "content": "
// SPDX-License-Identifier: MIT
pragma solidity 0.8.16;

import "@openzeppelin/contracts@v4.9.3/access/Ownable.sol";
import "@openzeppelin/contracts@v4.9.3/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts@v4.9.3/utils/Address.sol";
import "@openzeppelin/contracts@v4.9.3/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts@v4.9.3/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts@v4.9.3/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts@v4.9.3/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts@v4.9.3/utils/structs/EnumerableMap.sol";
import "@openzeppelin/contracts@v4.9.3/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts@v4.9.3/security/Pausable.sol";
import "@openzeppelin/contracts@v4.9.3/utils/math/SafeMath.sol";
import "@openzeppelin/contracts@v4.9.3/token/ERC721/IERC721Receiver.sol";

import "./interface/IHGP.sol";

contract HGP is Ownable, ReentrancyGuard, Pausable, IERC721Receiver {

    event NFT721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes data
    );

    event eUnwrapHGP721(
        address indexed asset,
        address indexed who,
        uint256[] ids,
        uint256 amount,
        uint256 timestamp
    );

    event eWrapHGP721(
        address indexed asset,
        address indexed who,
        uint256[] ids,
        uint256 cost,
        uint256 amount,
        uint256 timestamp
    );

    event eClaimHGP20(
        address indexed asset,
        address indexed who,
        uint256 amount,
        uint256 cost,
        uint256 startTimestamp,
        uint256 claimTimestamp
    );

    event eClaimHGP721(
        address indexed asset,
        address indexed who,
        uint256[] ids,
        uint256 cost,
        uint256 amount,
        uint256 startTimestamp,
        uint256 claimTimestamp
    );

    struct MappingParams {
        uint256 duration;
        uint256 vipRewardRate;
        uint256 punishmentRate;
        uint256 expirationTime;
        address to;
        bytes32 signCode;
    }

    struct MappingInfo {
        address asset;
        address hgp20;
        uint256 tokenRate;
    }

    using ECDSA for bytes32;
    using Address for address;
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    using EnumerableMap for EnumerableMap.AddressToUintMap;
    using EnumerableSet for EnumerableSet.Bytes32Set;
    using EnumerableSet for EnumerableSet.UintSet;
    using EnumerableSet for EnumerableSet.AddressSet;

    struct EIP712Domain {
        string name;
        string version;
        uint256 chainId;
        address verifyingContract;
    }

    bytes32 public immutable DOMAIN_SEPARATOR;

    bytes32 public constant EIP712DOMAIN_TYPEHASH =
        keccak256(
            "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
        );

    bytes32 public constant TYPE_HASH =
        keccak256(
            "MappingParams(uint256 duration,uint256 vipRewardRate,uint256 punishmentRate,uint256 expirationTime,address to,bytes32 signCode)"
        );

    //signCodes table
    EnumerableSet.Bytes32Set private _signCodes;

    //asset=>id
    EnumerableSet.AddressSet private _hgp721Map;
    mapping(address => MappingInfo) public _mappingInfo;

    uint256 public _maxMappingCount = 500;

    //user=>asset=>ids
    mapping(address => mapping(address => EnumerableSet.UintSet))
        private _userUnwrapBalance;
    mapping(address => mapping(address => EnumerableSet.UintSet))
        private _userWrapBalance;

    //asset=>ids
    mapping(address => EnumerableSet.UintSet) private _circulateBalance;

    //user=>asset=>time
    mapping(address => EnumerableMap.AddressToUintMap) private _userUnwrapTime;
    mapping(address => EnumerableMap.AddressToUintMap) private _userWrapTime;

    address public _signer;
    address public _vault;
    address public _maker;

    modifier onlyMaker() {
        require(_maker == msg.sender, "must call by maker");
        _;
    }

    constructor(address signer, address vault, address maker) {
        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                EIP712DOMAIN_TYPEHASH,
                keccak256("HGP"),
                keccak256("1"),
                block.chainid,
                address(this)
            )
        );

        require(signer != address(0x0), "signer is zero address!");
        _signer = signer;
        require(vault != address(0x0), "vault is zero address!");
        _vault = vault;
        require(maker != address(0x0), "maker is zero address!");
        _maker = maker;

        _signCodes.add(keccak256(abi.encode(msg.sender, block.timestamp)));
    }

    function withdrawETH(address target) public onlyOwner {
        payable(target).transfer(address(this).balance);
    }

    function urgencyWithdrawErc20(address erc20, address target)
        public
        onlyOwner
    {
        IERC20(erc20).safeTransfer(
            target,
            IERC20(erc20).balanceOf(address(this))
        );
    }

    function updateMaker(address maker) public onlyOwner {
        require(maker != address(0), "the maker address is zero!");
        _maker = maker;
    }

    function updateSigner(address signer) public onlyOwner {
        require(signer != address(0), "the signer address is zero!");
        _signer = signer;
    }

    function updateVault(address vault) public onlyOwner {
        require(vault != address(0), "the vault address is zero!");
        _vault = vault;
    }

    function updateMaxMapppingCount(uint256 count) public onlyOwner {
        _maxMappingCount = count;
    }

    function pause() public onlyOwner {
        super._pause();
    }

    function unpause() public onlyOwner {
        super._unpause();
    }

    function setMapping(
        address asset,
        address hgp20,
        uint256 tokenRate
    ) public onlyOwner {
        if (!_hgp721Map.contains(asset)) {
            _hgp721Map.add(asset);
        }
        _mappingInfo[asset].asset = asset;
        _mappingInfo[asset].hgp20 = hgp20;
        _mappingInfo[asset].tokenRate = tokenRate;
    }

    function delMapping(address asset) public onlyOwner {
        require(_hgp721Map.contains(asset), "asset mapping already delete");
        _hgp721Map.remove(asset);
        _mappingInfo[asset].asset = address(0x0);
        _mappingInfo[asset].hgp20 = address(0x0);
        _mappingInfo[asset].tokenRate = 0;
    }

    function updateCirculateBalance(
        address asset,
        uint256[] memory ids,
        bool enable
    ) public onlyOwner {
        if (enable) {
            for (uint256 i = 0; i < ids.length; i++) {
                require(
                    !_circulateBalance[asset].contains(ids[i]),
                    "invalid id!"
                );
                _circulateBalance[asset].add(ids[i]);
            }
        } else {
            for (uint256 i = 0; i < ids.length; i++) {
                require(
                    !_circulateBalance[asset].contains(ids[i]),
                    "invalid id!"
                );
                _circulateBalance[asset].remove(ids[i]);
            }
        }
    }

    function addLiquidity(address asset, uint256[] memory ids)
        public
        onlyMaker
    {
        IHGP721(address(asset)).safeBatchTransferFrom(
            msg.sender,
            address(this),
            ids,
            ""
        );

        uint256 cost = ids.length * _mappingInfo[asset].tokenRate * 1e18;
        (IHGP20)(_mappingInfo[asset].hgp20).mint(msg.sender, cost);

        for (uint256 i = 0; i < ids.length; i++) {
            require(!_circulateBalance[asset].contains(ids[i]), "invalid id!");
            _circulateBalance[asset].add(ids[i]);
        }
    }

    //
    function getUnwrapBalance(address owner, address asset)
        public
        view
        returns (uint256[] memory ids, uint256 timestamp)
    {
        uint256 length = _userUnwrapBalance[owner][asset].length();
        ids = new uint256[](length);

        for (uint256 i = 0; i < length; i++) {
            ids[i] = _userUnwrapBalance[owner][asset].at(i);
        }

        bool have;
        (have, timestamp) = _userUnwrapTime[owner].tryGet(asset);
    }

    //
    function getCirculateBalance(
        address asset,
        uint256 page,
        uint256 pageMax
    ) public view returns (uint256[] memory ids) {
        require(pageMax > 0, "invalid page size!");

        uint256 all = _circulateBalance[asset].length();
        uint256 maxCount = 0;
        if (all <= pageMax) {
            maxCount = all;
        } else {
            maxCount = pageMax;
            uint256 pages = all / pageMax;

            require(pages >= page, "invalid page size!");

            if (pages == page) {
                maxCount = all % pageMax;
                require(maxCount > 0, "invalid page size!");
            }
        }

        ids = new uint256[](maxCount);

        for (uint256 i = 0; i < maxCount; i++) {
            ids[i] = _circulateBalance[asset].at(i);
        }
    }

    function getWrapBalance(address owner, address asset)
        public
        view
        returns (uint256[] memory ids, uint256 timestamp)
    {
        uint256 length = _userWrapBalance[owner][asset].length();
        ids = new uint256[](length);

        for (uint256 i = 0; i < length; i++) {
            ids[i] = _userWrapBalance[owner][asset].at(i);
        }

        bool have;
        (have, timestamp) = _userWrapTime[owner].tryGet(asset);
    }

    function unwrapHGP721(address asset, uint256[] memory ids)
        public
        whenNotPaused
        nonReentrant
    {
        require(_hgp721Map.contains(asset), "invlaid asset!");

        IHGP721(address(asset)).safeBatchTransferFrom(
            msg.sender,
            address(this),
            ids,
            ""
        );

        uint256 unwrapAmount = _userUnwrapBalance[msg.sender][asset].length();
        require(
            unwrapAmount + ids.length <= _maxMappingCount,
            "there are too many HGP721s in the account to be unwraped!"
        );
        for (uint256 i = 0; i < ids.length; i++) {
            require(
                !_userUnwrapBalance[msg.sender][asset].contains(ids[i]),
                "invlaid asset id!"
            );
            require(
                !_circulateBalance[asset].contains(ids[i]),
                "invlaid asset id!"
            );

            _userUnwrapBalance[msg.sender][asset].add(ids[i]);
            _circulateBalance[asset].add(ids[i]);
        }

        _userUnwrapTime[msg.sender].set(asset, block.timestamp);
        emit eUnwrapHGP721(asset, msg.sender, ids, ids.length, block.timestamp);
    }

    function claimHGP20(
        address asset,
        MappingParams calldata params,
        bytes memory dataSignature
    ) public whenNotPaused nonReentrant {
        require(_hgp721Map.contains(asset), "invlaid asset!");

        bool have;
        uint256 unwrapTimestamp;
        (have, unwrapTimestamp) = _userUnwrapTime[msg.sender].tryGet(asset);
        require(
            have && block.timestamp > unwrapTimestamp,
            "invlaid unwrap timestamp!"
        );
        _userUnwrapTime[msg.sender].remove(asset);

        require(!msg.sender.isContract(), "call to non-contract");
        require(!isExistSignCode(params.signCode), "invalid signCode!");
        require(
            verify(params, msg.sender, dataSignature),
            "this signature is not valid!"
        );
        _signCodes.add(params.signCode);

        uint256 unwrapAmount = _userUnwrapBalance[msg.sender][asset].length();
        uint256[] memory values = _userUnwrapBalance[msg.sender][asset]
            .values();
        for (uint256 i = 0; i < unwrapAmount; i++) {
            _userUnwrapBalance[msg.sender][asset].remove(values[i]);
        }

        uint256 amount = unwrapAmount * _mappingInfo[asset].tokenRate * 1e18;
        uint256 punishmentFee = 0;
        if (
            block.timestamp - unwrapTimestamp <
            params.duration - (params.duration * params.vipRewardRate) / 10000
        ) {
            punishmentFee = (amount * params.punishmentRate) / 10000;
            (IHGP20)(_mappingInfo[asset].hgp20).mint(_vault, punishmentFee);
            amount = amount - punishmentFee;
        }

        (IHGP20)(_mappingInfo[asset].hgp20).mint(msg.sender, amount);

        emit eClaimHGP20(
            asset,
            msg.sender,
            amount,
            punishmentFee,
            unwrapTimestamp,
            block.timestamp
        );
    }

    function wrapHGP721(address asset, uint256 amount)
        public
        whenNotPaused
        nonReentrant
    {
        require(_hgp721Map.contains(asset), "invlaid asset!");
        require(
            amount <= _circulateBalance[asset].length() && amount > 0,
            "invalid wrap amount!"
        );

        uint256[] memory ids = new uint256[](amount);
        bytes32 seed = _signCodes.at(
            uint256(
                keccak256(
                    abi.encode(
                        tx.origin,
                        block.number,
                        block.timestamp,
                        blockhash(
                            block.number / ((_signCodes.length() % 10) + 1)
                        )
                    )
                )
            ) % _signCodes.length()
        );

        uint256 rand;
        for (uint256 i = 0; i < ids.length; i++) {
            rand = uint256(seed) % _circulateBalance[asset].length();
            ids[i] = _circulateBalance[asset].at(rand);
            require(
                IERC721(asset).ownerOf(ids[i]) == address(this),
                "asset amount is not enough"
            );
            require(
                !_userWrapBalance[msg.sender][asset].contains(ids[i]),
                "invalid asset id"
            );
            require(
                _userWrapBalance[msg.sender][asset].length() < _maxMappingCount,
                "there are too many HGP721s in the account to be wraped!"
            );

            _userWrapBalance[msg.sender][asset].add(ids[i]);
            _circulateBalance[asset].remove(ids[i]);
        }

        uint256 cost = ids.length * _mappingInfo[asset].tokenRate * 1e18;
        IHGP20(_mappingInfo[asset].hgp20).burn(msg.sender, cost);
        _userWrapTime[msg.sender].set(asset, block.timestamp);

        emit eWrapHGP721(asset, msg.sender, ids, cost, amount, block.timestamp);
    }

    function claimHGP721(
        address asset,
        MappingParams calldata params,
        bytes memory dataSignature
    ) public whenNotPaused nonReentrant {
        require(_hgp721Map.contains(asset), "invlaid asset!");

        bool have;
        uint256 wrapTimestamp;
        (have, wrapTimestamp) = _userWrapTime[msg.sender].tryGet(asset);
        require(
            have && block.timestamp > wrapTimestamp,
            "invlaid wrap timestamp!"
        );
        _userWrapTime[msg.sender].remove(asset);

        require(!msg.sender.isContract(), "call to non-contract");
        require(!isExistSignCode(params.signCode), "invalid signCode!");
        require(
            verify(params, msg.sender, dataSignature),
            "this signature is not valid!"
        );
        _signCodes.add(params.signCode);

        uint256 wrapAmount = _userWrapBalance[msg.sender][asset].length();
        uint256[] memory ids = _userWrapBalance[msg.sender][asset].values();
        for (uint256 i = 0; i < wrapAmount; i++) {
            _userWrapBalance[msg.sender][asset].remove(ids[i]);
        }

        require(wrapAmount > 0, "invalid wrap balance!");

        uint256 extraCost;
        if (
            block.timestamp - wrapTimestamp <
            (params.duration - (params.duration * params.vipRewardRate) / 10000)
        ) {
            extraCost =
                (wrapAmount *
                    _mappingInfo[asset].tokenRate *
                    params.punishmentRate *
                    1e18) /
                10000;

            (IERC20)(_mappingInfo[asset].hgp20).safeTransferFrom(
                msg.sender,
                _vault,
                extraCost
            );
        }

        IHGP721(address(asset)).safeBatchTransferFrom(
            address(this),
            msg.sender,
            ids,
            ""
        );

        emit eClaimHGP721(
            asset,
            msg.sender,
            ids,
            extraCost,
            wrapAmount,
            wrapTimestamp,
            block.timestamp
        );
    }

    function getMappingInfo(address asset)
        public
        view
        returns (MappingInfo memory mappingInfo)
    {
        mappingInfo.asset = asset;
        mappingInfo.hgp20 = _mappingInfo[asset].hgp20;
        mappingInfo.tokenRate = _mappingInfo[asset].tokenRate;
    }

    function getMappingInfos(uint256 page, uint256 pageMax)
        public
        view
        returns (MappingInfo[] memory mappingInfos)
    {
        require(pageMax > 0, "invalid page size!");

        uint256 all = _hgp721Map.length();
        uint256 maxCount = 0;
        if (all <= pageMax) {
            maxCount = all;
        } else {
            maxCount = pageMax;
            uint256 pages = all / pageMax;

            require(pages >= page, "invalid page size!");

            if (pages == page) {
                maxCount = all % pageMax;
                require(maxCount > 0, "invalid page size!");
            }
        }

        mappingInfos = new MappingInfo[](maxCount);
        for (uint256 i = 0; i < maxCount; i++) {
            mappingInfos[i].asset = _hgp721Map.at(page * pageMax + i);
            mappingInfos[i].hgp20 = _mappingInfo[mappingInfos[i].asset].hgp20;
            mappingInfos[i].tokenRate = _mappingInfo[mappingInfos[i].asset]
                .tokenRate;
        }
    }

    //check the state of a signCode
    function isExistSignCode(bytes32 signCode) public view returns (bool) {
        return _signCodes.contains(signCode);
    }

    //generate the mintData hash
    function hashCondition(MappingParams calldata params)
        public
        pure
        returns (bytes32)
    {
        return
            keccak256(
                abi.encode(
                    TYPE_HASH,
                    params.duration,
                    params.vipRewardRate,
                    params.punishmentRate,
                    params.expirationTime,
                    params.to,
                    params.signCode
                )
            );
    }

    function hashDigest(MappingParams calldata params)
        public
        view
        returns (bytes32)
    {
        return
            keccak256(
                abi.encodePacked(
                    "\x19\x01",
                    DOMAIN_SEPARATOR,
                    hashCondition(params)
                )
            );
    }

    function verifySignature(bytes32 hash, bytes memory signature)
        public
        view
        returns (bool)
    {
        return hash.recover(signature) == _signer;
    }

    function verify(
        MappingParams calldata params,
        address user,
        bytes memory dataSignature
    ) public view returns (bool) {
        require(
            params.expirationTime >= block.timestamp,
            "this signature is expired!"
        );
        require(params.signCode != "", "invalid sign code!");
        require(params.to == user, "invalid minter data!");

        bytes32 digest = hashDigest(params);
        require(
            verifySignature(digest, dataSignature),
            "invalid dataSignatures! "
        );

        return true;
    }

    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes memory data
    ) external override returns (bytes4) {
        if (address(this) == operator) {
            emit NFT721Received(operator, from, tokenId, data);
            return this.onERC721Received.selector;
        }
        return 0;
    }
}
