    "src/ReplicanFactory.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "openzeppelin-contracts/contracts/security/ReentrancyGuard.sol";
import "openzeppelin-contracts/contracts/token/ERC721/IERC721.sol";
import "openzeppelin-contracts/contracts/token/ERC20/IERC20.sol";
import "openzeppelin-contracts/contracts/utils/introspection/ERC165Checker.sol";
import "openzeppelin-contracts/contracts/proxy/Clones.sol";

import "./AccessControlled.sol";
import "./Errors.sol";
import "./IAddressProvider.sol";
import "./IPaymentTokenRegistry.sol";
import "./Replican.sol";

interface IReplican {
    function initialize(address _masterContract, string memory _name, string memory _symbol, address addressProvider_) external;
}

contract ReplicanFactory is AccessControlled, ReentrancyGuard {
    event ReplicanDeployed(
        address indexed replican,
        address indexed collection,
        address deployer,
        address paymentToken,
        uint256 deployCost
    );

    struct CollectionConfig {
        address deployer;
        string name;
        string symbol;
        address replican;
        address collection;
        bool valid;
        PaymentConfig deployPayment;
        PaymentConfig mintPayment;
    }

    mapping(address => uint256) public configsIndexByCollection; // keyed by collection address
    CollectionConfig[] public configs;

    IAddressProvider private addressProvider;

    address immutable replicanImplementation;

    constructor(address accessController_, address addressProvider_)
        AccessControlled(accessController_)
    {
        addressProvider = IAddressProvider(addressProvider_);
        Replican replican = new Replican();
        replican.initialize(address(0), '', '', address(0));
        replicanImplementation = address(replican);
    }

    function deployReplicans(address collection) public payable nonReentrant returns (address replicanAddress) {
        uint256 configIndex = configsIndexByCollection[collection];
        CollectionConfig storage config = configs[configIndex];

        if (config.valid == false) revert Errors.InvalidatedDeployment();
        if (config.replican != address(0)) revert Errors.ReplicanContractAlreadyDeployed();
        if (config.deployer == address(0)) revert Errors.CollectionNotFound();
        if (config.deployer != msg.sender) revert Errors.IncorrectContractDeployer();

        if (config.deployPayment.cost > 0) {
            if (config.deployPayment.token == address(0) && msg.value != config.deployPayment.cost) {
                revert Errors.InvalidDeploymentPayment();
            }
            if (config.deployPayment.token != address(0)) {
                IERC20 tokenContract = IERC20(config.deployPayment.token);
                tokenContract.transferFrom(msg.sender, addressProvider.treasury(), config.deployPayment.cost);
            } else {
                (bool success, ) = payable(addressProvider.treasury()).call{value: msg.value}("");
                if (!success) revert Errors.TransferFailed();
            }
        }

        bytes32 salt = keccak256(abi.encodePacked(collection));
        replicanAddress = Clones.cloneDeterministic(replicanImplementation, salt);
        IReplican(replicanAddress).initialize(collection, config.name, config.symbol, address(addressProvider));

        config.replican = replicanAddress;

        IPaymentTokenRegistry tokenRegistry = IPaymentTokenRegistry(addressProvider.paymentTokenRegistry());
        tokenRegistry.setCostConfig(replicanAddress, config.mintPayment);

        emit ReplicanDeployed(
                replicanAddress, collection, config.deployer, config.deployPayment.token, config.deployPayment.cost
            );
    }

    function onboardCollection(
        address collection,
        address deployer,
        address deployPaymentToken,
        uint256 deployCost,
        address mintPaymentToken,
        uint256 mintCost,
        address mintPayoutAddress,
        string calldata name,
        string calldata symbol
    ) public onlyOnboardingManager {
        if (deployer == address(0) || collection == address(0)) revert Errors.NullAddressNotAllowed();
        if (!ERC165Checker.supportsERC165(collection)) revert Errors.IERC165SupportCheckFailed();
        if (!ERC165Checker.supportsInterface(collection, type(IERC721).interfaceId)) {
            revert Errors.IERC721SupportCheckFailed();
        }

        IPaymentTokenRegistry tokenRegistry = IPaymentTokenRegistry(addressProvider.paymentTokenRegistry());
        if (!tokenRegistry.isTokenSupported(deployPaymentToken)) revert Errors.PaymentTokenNotSupported();
        if (!tokenRegistry.isTokenSupported(mintPaymentToken)) revert Errors.PaymentTokenNotSupported();

        uint256 configIndex = configs.length;
        configsIndexByCollection[collection] = configIndex;
        configs.push(CollectionConfig({
            deployer: deployer,
            name: name,
            symbol: symbol,
            deployPayment: PaymentConfig({token: deployPaymentToken, cost: deployCost, payout: address(0)}), // always treasury
            mintPayment: PaymentConfig({token: mintPaymentToken, cost: mintCost, payout: mintPayoutAddress}),
            valid: true,
            replican: address(0),
            collection: collection
        }));
    }

    function revokeCollectionConfig(address collection) public onlyOnboardingManager {
        uint256 index = configsIndexByCollection[collection];
        CollectionConfig storage config = configs[index];
        if (config.deployer == address(0)) revert Errors.CollectionNotFound();
        if (config.replican != address(0)) revert Errors.ReplicanContractAlreadyDeployed();
        config.valid = false;
    }

    function setAddressProvider(address addressProvider_) public onlyAdmin {
        if (addressProvider_ == address(0)) revert Errors.NullAddressNotAllowed();
        addressProvider = IAddressProvider(addressProvider_);
    }

    function numberOfConfigs() public view returns (uint256) {
        return configs.length;
    }

}
"
