      "content": "

// SPDX-License-Identifier: MIT
pragma solidity 0.8.16;

import "@openzeppelin/contracts@v4.9.3/access/Ownable.sol";
import "@openzeppelin/contracts@v4.9.3/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts@v4.9.3/utils/Address.sol";
import "@openzeppelin/contracts@v4.9.3/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts@v4.9.3/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts@v4.9.3/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts@v4.9.3/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts@v4.9.3/security/Pausable.sol";

import "./interface/IHGP.sol";

contract HGPFactory is IHGPFactory, Ownable, ReentrancyGuard, Pausable  {


    using ECDSA for bytes32;
    using Address for address;
    using SafeERC20 for IERC20;

    using EnumerableSet for EnumerableSet.AddressSet;
    using EnumerableSet for EnumerableSet.Bytes32Set;


    struct EIP712Domain {
        string  name;
        string  version;
        uint256 chainId;
        address verifyingContract;
    }

    bytes32 public immutable DOMAIN_SEPARATOR;

    bytes32 public constant EIP712DOMAIN_TYPEHASH = keccak256(
        "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
    );

    bytes32 public constant TYPE_HASH = keccak256(
        "MintRule(address mintRule,address asset,address to,bytes32 signCode,uint256 expirationTime,uint256 stage,uint256 mintFee,uint256 userMaxMintedAmount,uint256 maxMintedAmount,uint256 startTime,uint256 endTime)"
    );

    //signCodes table
    EnumerableSet.Bytes32Set private _signCodes;
    //mint rule map 
    EnumerableSet.AddressSet private _mintRules;

    address public _signer;
    address public _vault;

    constructor(address signer, address vault)
    {
        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                EIP712DOMAIN_TYPEHASH,
                keccak256("HGPFactory"),
                keccak256("1"),
                block.chainid,
                address(this)
            )
        );

        require(signer != address(0x0), "signer is zero address!");
        require(vault != address(0x0), "vault is zero address!");
        _signer = signer;
        _vault = vault;
    }

    function addMintRule(address mintRule) public onlyOwner {
        if(!_mintRules.contains(mintRule)){
            _mintRules.add(mintRule);
        }
    }

    function removeMintRule(address mintRule) public onlyOwner {
         if(_mintRules.contains(mintRule)){
            _mintRules.remove(mintRule);
         }
    }

    function updateSigner( address signer) external onlyOwner {
        require(signer != address(0),"the signer address is zero!");
        _signer = signer;
    }

    function updateVault( address vault) external onlyOwner {
        require(vault != address(0),"the vault address is zero!");
        _vault = vault;
    }

    function getMintRules() public view returns(address[] memory){
        return _mintRules.values();
    }

    function mint(IMintRule.MintRule calldata mintData, bytes memory dataSignature) 
    external payable override whenNotPaused nonReentrant
    {
        require(!msg.sender.isContract(), "call to non-contract");
        require(_mintRules.contains(mintData.mintRule)," invalid inscription mint rule!" );
        require(block.timestamp > mintData.startTime && block.timestamp < mintData.endTime, "inalid inscription mint time!");
        require(!isExistSignCode(mintData.signCode),"invalid signCode!");
        require(verify(mintData, msg.sender, dataSignature), "this signature is not valid!");

        _signCodes.add(mintData.signCode);

        IMintRule rule = (IMintRule)(mintData.mintRule);
        rule.beforeMint{value: msg.value}(mintData);

        uint256 tokenId = (IHGP721)(mintData.asset).mint(mintData.to);

        emit eMint(mintData.mintRule, mintData.asset, msg.sender, tokenId, block.timestamp, mintData.stage);

    } 

    function burn(uint256[] calldata tokenIds, address asset) 
    external override whenNotPaused nonReentrant
    {
        require(!msg.sender.isContract(), "call to non-contract");

        for (uint32 i = 0; i < tokenIds.length; ++i) {
            (IHGP721)(asset).burn(tokenIds[i]);
        }

        emit eBurned(asset, msg.sender ,tokenIds, block.timestamp);

    } 

    //check the state of a signCode
    function isExistSignCode(bytes32 signCode) view public returns(bool) {
        return _signCodes.contains(signCode);
    }

    //generate the mintData hash
    function hashCondition(IMintRule.MintRule calldata mintData) public pure returns (bytes32) {

        return keccak256(
            abi.encode(
                TYPE_HASH,
                mintData.mintRule,
                mintData.asset,
                mintData.to,
                mintData.signCode,
                mintData.expirationTime,
                mintData.stage,
                mintData.mintFee,
                mintData.userMaxMintedAmount,
                mintData.maxMintedAmount,
                mintData.startTime,
                mintData.endTime
                )
        );
    }

    function hashDigest(IMintRule.MintRule calldata mintData) public view returns (bytes32) {
        return keccak256(abi.encodePacked(
            "\x19\x01",
            DOMAIN_SEPARATOR,
            hashCondition(mintData)
        ));
    }

    function verifySignature(bytes32 hash, bytes memory  signature) public view returns (bool) {
        //hash must be a soliditySha3 with accounts.sign
        return hash.recover(signature) == _signer;
    }

    function verify( IMintRule.MintRule calldata mintData, address user, bytes memory dataSignature ) public view returns (bool) {

        require(
            mintData.expirationTime >= block.timestamp,
            "this signature is expired!"
        );
        require(mintData.signCode != "","invalid sign code!");
        require(mintData.to == user,"invalid minter data!");

        bytes32 digest = hashDigest(mintData);
        require(verifySignature(digest,dataSignature),"invalid dataSignatures! ");

        return true;
    }

    function pause() external onlyOwner{
        super._pause();
    }

    function unpause() external onlyOwner{
        super._unpause();
    }

    function withdraw(address target) public  onlyOwner {
        payable(target).transfer(address(this).balance);
    }

}
"
