    "FinanceClaim/src/FinanceClaim.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.20;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "fixedswap/FixedSwap.sol";
import "./libs/ClaimErrors.sol";
import "./interfaces/IFinanceClaim.sol";

contract FinanceClaim is IFinanceClaim, Ownable {
    using SafeERC20 for IERC20;

    mapping(uint256 => IFinanceClaim.Pool) public pools;

    mapping(address => bool) public finOpsWhitelisted;

    FixedSwap public fixedSwapContract;

    modifier onlyFinOps() {
        if (!finOpsWhitelisted[msg.sender]) {
            revert ClaimErrors.FinanceClaim__CallerNotAuthorized(msg.sender);
        }
        _;
    }

    modifier isValidPoolIndex(uint256 poolIndex) {
        uint256 poolsCount = fixedSwapContract.poolsCount();
        if (poolIndex >= poolsCount) {
            revert ClaimErrors.FinanceClaim__PoolIndexNonExistent(poolIndex, poolsCount);
        }
        _;
    }

    /**
     * @dev Initializes the contract by setting a `fixedSwapContractAddress` to instantiate the FixedSwap contract
     */
    constructor(address fixedSwapContractAddress) {
        if (fixedSwapContractAddress == address(0)) {
            revert ClaimErrors.FinanceClaim__NewContractCannotBeZeroAddress();
        }
        finOpsWhitelisted[msg.sender] = true;
        fixedSwapContract = FixedSwap(fixedSwapContractAddress);
    }

    /// @notice Deposit ERC-20 funds in the pool that will later be claimable by a user
    /// @dev Only triggerable by FinOps
    /// @param poolIndex Index of the pool in FixedSwap contract
    /// @param amount Amount to deposit in the pool
    function depositFunds(uint256 poolIndex, uint256 amount) external onlyFinOps isValidPoolIndex(poolIndex) {
        if (amount == 0) {
            revert ClaimErrors.FinanceClaim__ZeroDepositFunds();
        }
        if (pools[poolIndex].reward == address(0)) {
            revert ClaimErrors.FinanceClaim__RewardNotInitialized(poolIndex);
        }
        pools[poolIndex].fundsDeposited += amount;
        IERC20(pools[poolIndex].reward).safeTransferFrom(msg.sender, address(this), amount);
        emit FundsDeposited(poolIndex, msg.sender, amount);
    }

    /// @notice List rewards to their corresponding pool indices
    /// @dev Only triggerable by FinOps
    /// @param poolsIdxs Array of pool indices
    /// @param rewards Array of reward addresses respectively corresponding to the pool indices
    function addPoolRewards(uint256[] calldata poolsIdxs, address[] calldata rewards) external onlyFinOps {
        uint256 poolsIdxsLen = poolsIdxs.length;
        if (poolsIdxsLen != rewards.length) {
            revert ClaimErrors.FinanceClaim__ArrayLengthsMismatch(poolsIdxsLen, rewards.length);
        }
        for (uint256 i; i < poolsIdxsLen;) {
            IFinanceClaim.Pool storage claimDataPool = pools[poolsIdxs[i]];
            if (
                claimDataPool.fundsDeposited > 0 || claimDataPool.totalClaimableFundsSet > 0
                    || claimDataPool.latestWithdrawalTs > 0
            ) {
                revert ClaimErrors.FinanceClaim__CannotChangeRewardOfAlreadyInitializedPool(poolsIdxs[i]);
            }
            claimDataPool.reward = rewards[i];
            emit PoolRewardAdded(poolsIdxs[i], rewards[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @notice Whitelist an address to be able to authorize as a FinOps
    /// @dev Can only be called by the owner
    /// @param finOpsAddresses Array of addresses to be whitelisted
    function whitelistFinOpsAddresses(address[] calldata finOpsAddresses) external onlyOwner {
        uint256 finOpsAddressesLen = finOpsAddresses.length;
        for (uint256 i; i < finOpsAddressesLen;) {
            finOpsWhitelisted[finOpsAddresses[i]] = true;
            emit FinOpsWhitelisted(msg.sender, finOpsAddresses[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @notice Remove an address so that it can no longer be authorized as FinOps
    /// @dev Can only be called by the owner
    /// @param finOpsAddresses Array of addresses to be blacklisted
    function removeWhitelistedFinOpsAddresses(address[] calldata finOpsAddresses) external onlyOwner {
        uint256 finOpsAddressesLen = finOpsAddresses.length;
        for (uint256 i; i < finOpsAddressesLen;) {
            finOpsWhitelisted[finOpsAddresses[i]] = false;
            emit FinOpsBlacklisted(msg.sender, finOpsAddresses[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @notice Marks the users that are eligible to claim funds from a pool
    /// @dev Can only be called by the owner
    /// @param poolIndex Index of the pool in FixedSwap contract
    /// @param userData calldata array of structs of type UserClaimData representing the user address and
    /// corresponding amount to set claimable
    function setFundsClaimable(
        uint256 poolIndex,
        IFinanceClaim.UserClaimData[] calldata userData
    )
        external
        onlyOwner
        isValidPoolIndex(poolIndex)
    {
        uint256 dataLen = userData.length;
        IFinanceClaim.Pool storage claimDataPool = pools[poolIndex];
        uint256 allowedFundsToDeposit = claimDataPool.fundsDeposited - claimDataPool.totalClaimableFundsSet;
        for (uint256 i; i < dataLen;) {
            address userAddress = userData[i].user;
            uint256 claimAmount = userData[i].amount;
            if (userAddress == address(0)) {
                revert ClaimErrors.FinanceClaim__InvalidClaimableUser(userAddress);
            }
            if (
                claimAmount > allowedFundsToDeposit
                    || claimDataPool.totalClaimableFundsSet >= claimDataPool.fundsDeposited
            ) {
                revert ClaimErrors.FinanceClaim__InsufficientDepositedFundsForClaiming();
            }
            claimDataPool.userClaimableFunds[userAddress] = claimAmount;
            claimDataPool.validUserClaims[userAddress] =
                keccak256(abi.encode(userAddress, poolIndex, claimDataPool.latestWithdrawalTs));
            allowedFundsToDeposit -= claimAmount;
            claimDataPool.totalClaimableFundsSet += claimAmount;
            unchecked {
                ++i;
            }
            emit FundsClaimableSet(poolIndex, claimAmount, userAddress);
        }
    }

    /// @notice Claims all the funds that are eligible to be claimed by the user
    /// @param poolIndex Index of the pool
    function claimFunds(uint256 poolIndex) external isValidPoolIndex(poolIndex) {
        IFinanceClaim.Pool storage poolData = pools[poolIndex];
        uint256 claimAmount = poolData.userClaimableFunds[msg.sender];
        if (claimAmount == 0) {
            revert ClaimErrors.FinanceClaim__NoFundsToClaim(poolIndex, msg.sender);
        }
        bytes32 userClaimHash = keccak256(abi.encode(msg.sender, poolIndex, poolData.latestWithdrawalTs));
        if (userClaimHash != poolData.validUserClaims[msg.sender]) {
            revert ClaimErrors.FinanceClaim__InvalidClaimHash(msg.sender, poolIndex);
        }
        poolData.userClaimableFunds[msg.sender] = 0;
        poolData.fundsDeposited -= claimAmount;
        poolData.totalClaimableFundsSet -= claimAmount;
        IERC20(pools[poolIndex].reward).safeTransfer(msg.sender, claimAmount);
        emit FundsClaimed(poolIndex, msg.sender, claimAmount);
    }

    /// @notice Claims all the left funds in a pool. This is to be called by FinOps in case users don't claim their
    /// funds on a long enough period of time
    /// @dev Resets the valid claim timestamp of the pool, so all users who have not claimed their funds until then,
    /// will not be able to claim them anymore
    /// @dev only triggerable by FinOps
    /// @param poolIndex Index of the pool
    function withDrawPoolUnclaimedFunds(uint256 poolIndex) external onlyFinOps isValidPoolIndex(poolIndex) {
        IFinanceClaim.Pool storage poolData = pools[poolIndex];
        uint256 amount = poolData.fundsDeposited;
        if (amount == 0) {
            revert ClaimErrors.FinanceClaim__ZeroWithdrawalFunds(poolIndex);
        }
        poolData.fundsDeposited = 0;
        poolData.latestWithdrawalTs = block.timestamp;
        poolData.totalClaimableFundsSet = 0;
        IERC20(poolData.reward).safeTransfer(msg.sender, amount);
        emit UnClaimedFundsWithdrawn(poolIndex, msg.sender, amount);
    }

    /// @notice Returns the total deposited funds in a pool (both ETH and non-ETH pools)
    /// @param poolIndex Index of the pool
    function getPoolFundsDeposited(uint256 poolIndex) external view returns (uint256) {
        return pools[poolIndex].fundsDeposited;
    }

    /// @notice Returns the total funds a user is allowed to claim
    /// @param poolIndex Index of the pool
    /// @param user wallet address
    function getUserClaimableFunds(uint256 poolIndex, address user) external view returns (uint256) {
        bytes32 currentHash = keccak256(abi.encode(user, poolIndex, pools[poolIndex].latestWithdrawalTs));
        bytes32 latestValidUserHash = pools[poolIndex].validUserClaims[user];
        if (currentHash != latestValidUserHash) {
            return 0;
        }
        return pools[poolIndex].userClaimableFunds[user];
    }
}
"
