    error FinanceClaim__PoolIndexNonExistent(uint256 poolIndex, uint256 poolsCount);
    error FinanceClaim__RewardNotInitialized(uint256 poolIndex);
    error FinanceClaim__CannotChangeRewardOfAlreadyInitializedPool(uint256 poolIndex);
    error FinanceClaim__ZeroClaimFunds(address user);
    error FinanceClaim__InvalidClaimHash(address user, uint256 poolIndex);
    error FinanceClaim__InvalidClaimableUser(address user);
    error FinanceClaim__ZeroDepositFunds();
