    "src/NFTBatchMinter.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {OilXNft} from "./OilXNft.sol";
import "./OilXEnumDeclaration.sol";
import "./WithdrawContracts.sol";


contract NFTBatchMinter is WidthdrawContractsOwnable {
    OilXNft nftContract;
    address internal _NFTContractAddress;
    bool public allowBatchMint;

    event MintOilXNFT(
        address indexed to, 
        uint256 indexed tokenId, 
        OilXNftTokenType tokenType, 
        uint256 indexed amountOilX
    );

    /**
     * for ABI decode event from NFT
     */
    event Transfer(
        address indexed from, 
        address indexed to, 
        uint256 indexed tokenId
    );

    struct Basket {
        OilXNftTokenType purchaseNFTtype;
        uint32 quantity;
        uint32 amountOilX;
    }

    constructor(address _addressOilXNft) WidthdrawContractsOwnable(msg.sender) {
        nftContract = OilXNft(_addressOilXNft);
        _NFTContractAddress = _addressOilXNft;
        allowBatchMint = true;
    }


    function getNFTAddress() public view returns (address) {
        return _NFTContractAddress;
    }

    function setOnOff(bool _allowBatchMint) public onlyOwner {
        allowBatchMint = _allowBatchMint;
    }

    function batchmint(bool checkonly, address to, Basket[] memory basket) public onlyOwner {
        require(allowBatchMint, "Error: batchmint is off");
        require(to != address(0), "Error: address is zero");

        for (uint8 i = 0; i < basket.length; i++) {


            /* mint NFTs for each basket position */
            for (uint8 amount = 0; amount < basket[i].quantity; amount++) {
                if (checkonly) {
                    emit MintOilXNFT(to, 0, basket[i].purchaseNFTtype, basket[i].amountOilX);
                } else {
                    nftContract.safeMint(to, basket[i].purchaseNFTtype, basket[i].amountOilX);
                }
            }
        }
    }
}
"
