// File: BurnMetadataContracts/BurnStatus.sol



pragma solidity ^0.8.0;

//=====================================================================================================================
//=====================================================================================================================
interface ForgeToken {
    struct TokenAttributes { uint16 xp; bool isSpecialVariant; }
    function tokenAttributes(uint _tokenId) external view returns(TokenAttributes memory);
    function ownerOf(uint _tokenId) external view returns(address);
}
//=====================================================================================================================
/// @title Forge Burn Status
//=====================================================================================================================
contract Forge_BurnStatus is Ownable {
    constructor() Ownable(msg.sender) {
        // Your constructor code
    }
    ForgeToken public forgeTokenContract = ForgeToken(0x0f2c03D7240346c17acE551715C95210E7418d9e);
    uint public currentSeason = 0;
    mapping(uint => mapping(uint => bool)) public isBurned;
    //-----------------------------------------------------------------------------------------------------------------
    /// @dev Emits when a Cold Fire Badge has been cold burned. Can only happen once per season.
    //-----------------------------------------------------------------------------------------------------------------
    event ColdBurn(uint _currentSeason, uint _tokenId);
    //-----------------------------------------------------------------------------------------------------------------
    /// @dev Remember to set this each time there is a season change
    //-----------------------------------------------------------------------------------------------------------------
    function setCurrentSeason(uint _currentSeason) external onlyOwner {
        currentSeason = _currentSeason;
    }
    //-----------------------------------------------------------------------------------------------------------------
    /// @notice Does NOT burn your NFT. Extracts the XP from a Cold Fire Badge and applies it to your Forge Season
    ///  Progress. Do not call this function directly, rather, visit https://forge.gg to apply it via the website.
    /// @dev Cold burning something can only happen once per season and is irreversible. Make sure that the resulting
    ///  event emission is processed correctly.
    //-----------------------------------------------------------------------------------------------------------------
    function coldBurn(uint _tokenId) external {
        require (msg.sender == forgeTokenContract.ownerOf(_tokenId), "Sender does not own this NFT");
        ForgeToken.TokenAttributes memory attributes = forgeTokenContract.tokenAttributes(_tokenId);
        require (attributes.isSpecialVariant, "Can only cold burn Cold Fire Badges");
        require (isBurned[currentSeason][_tokenId] == false, "This NFT has already been cold burned this season");
        isBurned[currentSeason][_tokenId] = true;
        emit ColdBurn(currentSeason, _tokenId);
    }
}
