    "contracts/protocol/libraries/math/WadRayMath.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;


/**
 * @title WadRayMath library
 * @author Aave
 * @notice Provides functions to perform calculations with Wad and Ray units
 * @dev Provides mul and div function for wads (decimal numbers with 18 digits of precision) and rays (decimal numbers
 * with 27 digits of precision)
 * @dev Operations are rounded. If a value is >=.5, will be rounded up, otherwise rounded down.
 **/
library WadRayMath {
  // HALF_WAD and HALF_RAY expressed with extended notation as constant with operations are not supported in Yul assembly
  uint256 internal constant WAD = 1e18;
  uint256 internal constant HALF_WAD = 0.5e18;

  uint256 internal constant RAY = 1e27;
  uint256 internal constant HALF_RAY = 0.5e27;

  uint256 internal constant WAD_RAY_RATIO = 1e9;

  /**
   * @dev Multiplies two wad, rounding half up to the nearest wad
   * @dev assembly optimized for improved gas savings, see https://twitter.com/transmissions11/status/1451131036377571328
   * @param a Wad
   * @param b Wad
   * @return c = a*b, in wad
   **/
  function wadMul(uint256 a, uint256 b) internal pure returns (uint256 c) {
    // to avoid overflow, a <= (type(uint256).max - HALF_WAD) / b
    assembly {
      if iszero(or(iszero(b), iszero(gt(a, div(sub(not(0), HALF_WAD), b))))) {
        revert(0, 0)
      }

      c := div(add(mul(a, b), HALF_WAD), WAD)
    }
  }

  /**
   * @dev Divides two wad, rounding half up to the nearest wad
   * @dev assembly optimized for improved gas savings, see https://twitter.com/transmissions11/status/1451131036377571328
   * @param a Wad
   * @param b Wad
   * @return c = a/b, in wad
   **/
  function wadDiv(uint256 a, uint256 b) internal pure returns (uint256 c) {
    // to avoid overflow, a <= (type(uint256).max - halfB) / WAD
    assembly {
      if or(iszero(b), iszero(iszero(gt(a, div(sub(not(0), div(b, 2)), WAD))))) {
        revert(0, 0)
      }

      c := div(add(mul(a, WAD), div(b, 2)), b)
    }
  }

  /**
   * @notice Multiplies two ray, rounding half up to the nearest ray
   * @dev assembly optimized for improved gas savings, see https://twitter.com/transmissions11/status/1451131036377571328
   * @param a Ray
   * @param b Ray
   * @return c = a raymul b
   **/
  function rayMul(uint256 a, uint256 b) internal pure returns (uint256 c) {
    // to avoid overflow, a <= (type(uint256).max - HALF_RAY) / b
    assembly {
      if iszero(or(iszero(b), iszero(gt(a, div(sub(not(0), HALF_RAY), b))))) {
        revert(0, 0)
      }

      c := div(add(mul(a, b), HALF_RAY), RAY)
    }
  }

  /**
   * @notice Divides two ray, rounding half up to the nearest ray
   * @dev assembly optimized for improved gas savings, see https://twitter.com/transmissions11/status/1451131036377571328
   * @param a Ray
   * @param b Ray
   * @return c = a raydiv b
   **/
  function rayDiv(uint256 a, uint256 b) internal pure returns (uint256 c) {
    // to avoid overflow, a <= (type(uint256).max - halfB) / RAY
    assembly {
      if or(iszero(b), iszero(iszero(gt(a, div(sub(not(0), div(b, 2)), RAY))))) {
        revert(0, 0)
      }

      c := div(add(mul(a, RAY), div(b, 2)), b)
    }
  }

  /**
   * @dev Casts ray down to wad
   * @dev assembly optimized for improved gas savings, see https://twitter.com/transmissions11/status/1451131036377571328
   * @param a Ray
   * @return b = a converted to wad, rounded half up to the nearest wad
   **/
  function rayToWad(uint256 a) internal pure returns (uint256 b) {
    assembly {
      b := div(a, WAD_RAY_RATIO)
      let remainder := mod(a, WAD_RAY_RATIO)
      if iszero(lt(remainder, div(WAD_RAY_RATIO, 2))) {
        b := add(b, 1)
      }
    }
  }

  /**
   * @dev Converts wad up to ray
   * @dev assembly optimized for improved gas savings, see https://twitter.com/transmissions11/status/1451131036377571328
   * @param a Wad
   * @return b = a converted in ray
   **/
  function wadToRay(uint256 a) internal pure returns (uint256 b) {
    // to avoid overflow, b/WAD_RAY_RATIO == a
    assembly {
      b := mul(a, WAD_RAY_RATIO)

      if iszero(eq(div(b, WAD_RAY_RATIO), a)) {
        revert(0, 0)
      }
    }
  }
}"
    },
    "contracts/protocol/libraries/types/Types.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

library Types {
  struct Exchequer {
    uint128 supplyIndex;
    uint128 supplyRate;
    uint40 lastUpdateTimestamp;
    uint16 id;
    uint8 decimals;
    bool active;
    bool borrowingEnabled;
    uint256 protocolBorrowFee;
    uint256 totalDebt;
    uint256 borrowCap;
    uint256 supplyCap;
    uint256 collateralFactor; // expressed in ray
    address sTokenAddress;
    address dTokenAddress;
    address gTokenAddress;
    uint128 accruedToExchequerSafe;
  }

  // a user can only have one open line of credit and LoCs are closed when the balance is zero
  // the balance can be tracked through dToken state
  struct LineOfCredit {
    uint256 borrowMax;
    address underlyingAsset;
    // uint256 rate; // interest should accrue per second => rate based on per second accrual
    uint40 lastRepaymentTimestamp;
    uint40 creationTimestamp;
    uint40 expirationTimestamp;
    uint128 id;
    bool deliquent;
  }
}"
    },
    "contracts/protocol/tokenization/ERC20Base.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

import {ERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import {SafeCast} from "@openzeppelin/contracts/utils/math/SafeCast.sol";
import {WadRayMath} from "../libraries/math/WadRayMath.sol";
import {IPolemarch} from "../../interfaces/IPolemarch.sol";

abstract contract ERC20Base is ERC20Upgradeable {
	using WadRayMath for uint256;
	using SafeCast for uint256;

	modifier onlyPolemarch() {
		require(_msgSender() == address(POLEMARCH), "CALLER_MUST_BE_POLEMARCH");
		_;
	}
	// additional user data holds the rate for debt and the lastUpdateTimeStamp for supply
	mapping(address => uint128) internal _additionalUserData;
	uint internal _totalSupply;
	uint8 private _decimals;
	IPolemarch public POLEMARCH;

	function initialize(
		IPolemarch polemarch,
		string memory name,
		string memory symbol,
		uint8 sTokenDecimals
	) internal virtual onlyInitializing {
		__ERC20_init(name, symbol);
		POLEMARCH = polemarch;
		_setDecimals(sTokenDecimals);
	}

	function decimals() public view virtual override returns (uint8) {
		return _decimals;
	}

	function _setDecimals(uint8 newDecimals) internal {
		_decimals = newDecimals;
	}
}"
    },
    "contracts/protocol/tokenization/ScaledBalanceTokenBase.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

import {SafeCast} from "@openzeppelin/contracts/utils/math/SafeCast.sol";
import {WadRayMath} from "../libraries/math/WadRayMath.sol";
import {IScaledBalanceToken} from "../../interfaces/IScaledBalanceToken.sol";
import {IPolemarch} from "../../interfaces/IPolemarch.sol";
import {ERC20Base} from "./ERC20Base.sol";

abstract contract ScaledBalanceTokenBase is ERC20Base, IScaledBalanceToken {
	using WadRayMath for uint256;
	using SafeCast for uint256;

	function initialize(
		IPolemarch polemarch,
		string memory name,
		string memory symbol,
		uint8 decimals
	) internal virtual override onlyInitializing {
		ERC20Base.initialize(polemarch, name, symbol, decimals);
	}

	function scaledBalanceOf(address user) external view returns(uint256) {
		return super.balanceOf(user);
	}

	function scaledTotalSupply() public view virtual override returns (uint256) {
		return super.totalSupply();
	}

	function getPreviousIndex(address user) external view returns (uint256) {
		return _additionalUserData[user];
	}

	function _mintScaled(
		address caller,
		uint256 amount,
		uint256 index
	) internal {
		uint256 amountScaled = amount.rayDiv(index);
		require(amountScaled != 0, "INVALID_MINT_AMOUNT");

		uint256 scaledBalance = super.balanceOf(caller);
		uint256 balanceIncrease = scaledBalance.rayMul(index) -
			scaledBalance.rayMul(_additionalUserData[caller]);

		_additionalUserData[caller] = index.toUint128();
		_mint(caller, amountScaled.toUint128());
		uint256 amountToMint = amount + balanceIncrease;
		emit Mint(caller, amountToMint, balanceIncrease, index);
	}

	function _burnScaled(
		address caller,
		uint256 amount,
		uint256 index
	) internal {
		uint256 amountScaled = amount.rayDiv(index);
		require(amountScaled != 0, "INVALID_BURN_AMOUNT");

		uint256 scaledBalance = super.balanceOf(caller);
		uint balanceIncrease = scaledBalance.rayMul(index) -
			scaledBalance.rayMul(_additionalUserData[caller]);

		_additionalUserData[caller] = index.toUint128();
		_burn(caller, amountScaled.toUint128());
		if (amount < balanceIncrease) {
			uint256 amountToMint = balanceIncrease - amount;
      		emit Mint(caller, amountToMint, balanceIncrease, index);
		} else {
			uint256 amountToBurn = amount - balanceIncrease;
			emit Burn(caller, amountToBurn, balanceIncrease, index);
		}
	}
}"
