      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract airdropFarmingSplitter {
    using SafeMath for uint256;

    address payable wallet1 = payable(0xdAc6eCa5756D3De4d7fd72A752A306Ecbb52c2D0);
    address payable wallet2 = payable(0xFF7aAe026615939c7F133Da944090720e0d564c4);
    address payable wallet3 = payable(0xd4d751452CCc7cC37A1B8116cd2a9e828983bf8B);

    constructor() {}

    /**
    * Standard fallback function to allow contract to receive payments.
    */
    fallback() external payable {}

    /**
     * @dev Withdraw all ether from this contract and send to predefined 
     * wallets with fixed percentages (Callable by anyone)
     */
    function withdraw() external {
        uint256 totalBalance = address(this).balance;
        uint256 shareForFirstAndSecond = totalBalance.mul(40).div(100); // 40% share for the first and second wallets

        payable(wallet1).transfer(shareForFirstAndSecond);
        payable(wallet2).transfer(shareForFirstAndSecond);
        
        // After transferring to the first and second wallets, the remaining balance is for the third wallet.
        // This eliminates the need for explicit calculation of the third share.
        uint256 remainingBalance = address(this).balance;
        payable(wallet3).transfer(remainingBalance);
    }

    /**
     * @dev Withdraw all ERC20 tokens of the specified address from this contract
     * and send to predefined wallets with fixed percentages (Callable by anyone)
     */
    function withdrawERC20(address _token) external {
        IERC20 targetToken = IERC20(_token);
        uint256 totalBalance = targetToken.balanceOf(address(this));
        uint256 shareForFirstAndSecond = totalBalance.mul(40).div(100); // 40% share for the first and second wallets

        targetToken.transferFrom(address(this), wallet1, shareForFirstAndSecond);
        targetToken.transferFrom(address(this), wallet2, shareForFirstAndSecond);
    
        uint256 remainingBalance = targetToken.balanceOf(address(this));
        targetToken.transferFrom(address(this), wallet3, remainingBalance);
    }
}
