      "content": "//SPDX-License-Identifier: MIT

// SimpleSwap by Bitiocracy, Inc.

pragma solidity ^0.8.0;
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import {ERC721HolderUpgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/utils/ERC721HolderUpgradeable.sol";

interface IERC721 {
    function ownerOf(uint256) external view returns (address);
    function safeTransferFrom( address, address, uint256) external;
    function transferFrom( address, address, uint256) external;
}

contract SimpleSwap is OwnableUpgradeable, ERC721HolderUpgradeable {

    IERC721 public ATS;
    IERC721 public GRAPES;
    address public ATS_VAULT_ADDRESS;

    // Variables for external contract managers
    mapping (address => bool ) private managerStatus;

    event AddManager(address manager);
    event RemoveManager(address manager);

    event SwapTokens(uint256 numberStaked);
    event SwapManual(uint256 numberStaked, address targetWallet);

    event SetVaultAddress(address vaultAddress);
    event SetGrapesContract(address grapesAddress);

    event ToggleSwap(bool swapState);

    bool public swapOn;

    function initialize(address _vaultAddress, address _atsAddress, address _grapesAddress) public initializer {

        __Ownable_init(msg.sender);
        __ERC721Holder_init();

        ATS_VAULT_ADDRESS = _vaultAddress;

        ATS = IERC721(_atsAddress);
        GRAPES = IERC721(_grapesAddress);

    }


    // Managers allow external mint contracts to stake tokens directly
    modifier onlyOwnerOrManager() {
        require(
            managerStatus[msg.sender] == true || owner() == msg.sender,
            "caller is neither owner nor manager"
        );
        _;
    }

    function setVaultAddress(address vaultAddress) external onlyOwnerOrManager {
        ATS_VAULT_ADDRESS = vaultAddress;
        emit SetVaultAddress(ATS_VAULT_ADDRESS);
    }

    function setGrapesContract(address grapesAddress) external onlyOwnerOrManager {
        GRAPES = IERC721(grapesAddress);
        emit SetGrapesContract(ATS_VAULT_ADDRESS);
    }

    function addManager(address newManager) external onlyOwner {
        managerStatus[newManager] = true;
        emit AddManager(newManager);
    }

    function removeManager(address newManager) external onlyOwner {
        managerStatus[newManager] = false;
        emit RemoveManager(newManager);
    }

    function isManager(address checkAddress) external view returns (bool manager) {
        if (managerStatus[checkAddress] == true) {
            manager = true;
        } else {
            manager = false;
        }
    }


    function toggleSwap() external onlyOwnerOrManager {
        swapOn = !swapOn;
        emit ToggleSwap(swapOn);
    }


    /**
        Main function a user calls to send their ATS to this contract, and receive a new ATSROTA. The code assumes all the ATSROTA from 0-4443 have been minted, and placed in this contract.
     */
    function swapTokens(uint256[] memory tokens) external {
        uint256[] memory localTokens = tokens;
        uint256 arrayLength = localTokens.length;

        require(swapOn, "Swap Not Live");

        for (uint256 i =0; i < arrayLength;) {

            ATS.safeTransferFrom(msg.sender, ATS_VAULT_ADDRESS, localTokens[i]);
            GRAPES.safeTransferFrom( ATS_VAULT_ADDRESS, msg.sender, localTokens[i]);

            unchecked {
                i++;
            }

            emit SwapTokens(arrayLength);
        }
    }


    /**
        Manual swap for Manager. Assumes ATS NFT present. Allows to send to a target.
     */
    function swapManual(uint256[] memory tokens, address targetWallet) external onlyOwnerOrManager {
        uint256[] memory localTokens = tokens;
        uint256 arrayLength = localTokens.length;

        for (uint256 i =0; i<arrayLength;) {

            require(ATS.ownerOf(localTokens[i]) == ATS_VAULT_ADDRESS, "Send Token First");

            GRAPES.safeTransferFrom( ATS_VAULT_ADDRESS, targetWallet, localTokens[i]);

            unchecked {
                i++;
            }
        }

        emit SwapManual(arrayLength, targetWallet);

    }


}
