contract BOTGENTOKEN is ERC20 , ERC20Burnable, ERC20Permit{
    //deflationary taxed token - to toggle deflation on/off set tax/fee to more thn 0 / equal to 0 
    //auto-burn 50% of fee
    address public owner;
    uint256 public tax;

    event OwnerTransfer(address indexed oldOwner , address indexed newOwner);    
    constructor(
        string memory name,
        string memory symbol,
        address initialHolder,
        uint256 initialSupply
    ) ERC20(name, symbol) ERC20Permit(name) {
        // Mint initial supply to the contract deployer
        _mint(initialHolder, initialSupply);
        owner = initialHolder;
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == owner,"not owner");
        owner = newOwner;
        emit OwnerTransfer(msg.sender , newOwner);
    }

    function _transfer(address from, address to, uint256 amount) internal virtual override(ERC20) {
         uint256 amt;
         if(tax > 0){
            uint256 fee = amount * tax / 100;
            amt = amount - fee;
            super._transfer(from , owner , fee/2);
            burn(fee/2);
         }
         if(tax == 0) {
            amt = amount;
         }
         super._transfer(from , to , amt);
    }
    
    function changeFee(uint256 percent) public {
        require(msg.sender == owner, "owner function");
        require(percent < 16,"fee cannot exceed 15% max");
        tax = percent;
    }
}

contract BOTBRAIN is ReentrancyGuard, IERC1155Receiver, IERC721Receiver, ERC165 {
    uint256 internal day_ = 86400;
    using SafeMath for uint256;
    uint256 internal nonce;
    uint256 internal fee = 0.001 ether;
    uint256 internal revenue; // ether revenue

    uint256 public tokenN;
    address public owner;

    struct LockERC721{
        address token;
        address lockee;
        uint256 id;
        uint256 unlockTime;
        uint256 lockTimestamp;
        uint256 nonce;
        bytes32 key;
    }

    struct LockERC1155{
        address token;
        address lockee;
        uint256 id;
        uint256 amounts;
        uint256 unlockTime;
        uint256 lockTimestamp;
        uint256 nonce;
        bytes32 key;
    }

    struct LockERC20{
        address token;
        address lockee;
        uint256 amounts;
        uint256 unlockTime;
        uint256 lockTimestamp;
        uint256 nonce;
        bytes32 key;
    }

    struct LockEther{
        uint256 amounts;
        address lockee;
        uint256 unlockTime;
        uint256 lockTimestamp;
        uint256 nonce;
        bytes32 key;
    }

    struct TOKEN{
        address tokenAddress;
        string  name;
        string symbol;
        uint256 supply;
        address minter;
        uint256 time;
    }

    event LOCK(address indexed locker , bytes indexed lockData , bytes32 key ,uint256 lockTime);
    event UNLOCK(address indexed unlocker , bytes indexed unlockData , uint256 Time);
    event MINT (address indexed token , uint256 supply , string Name , string Symbol , address indexed owner, uint256 createtime);

    mapping (address => mapping(uint256=>bytes32)) public activeLocks; //lockee address=>nonce => active keys // all locks
    mapping (address =>mapping(bytes32=> LockERC721)) public ERC721Locks; // lokee address=> bytes32Key => 721 locks
    mapping (address => mapping(bytes32=> LockERC1155)) public ERC1155Locks; // same
    mapping (address => mapping(bytes32=> LockERC20)) public ERC20Locks; // same
    mapping (address => mapping(bytes32 => LockEther)) public EtherLocks; // same
    mapping (uint256 => TOKEN) public allCreations; // info of created tokens

    constructor() {
        // Register supported interfaces
        nonce = 1;
        _registerInterface(type(IERC1155Receiver).interfaceId);
        _registerInterface(type(IERC721Receiver).interfaceId);
        owner = msg.sender;
    }

    modifier isOwner {
        require(msg.sender == owner,"not owner");
        _;
    }
    function getFee() public view returns(uint256){
        //retrunns wei
        return fee;
    }

    function getRevenue() public view returns(uint256){
        return revenue;
    }

    function getUserLocks(address user) public view returns (bytes32[] memory) {
        uint256 numLocks = 0;

        // Count the number of locks for the user
        for (uint256 i = 1; i < nonce; i++) {
            bytes32 lockerKey = activeLocks[user][i];
            if (lockerKey != bytes32(0)) {
                numLocks++;
            }
        }

        bytes32[] memory locks = new bytes32[](numLocks);
        uint256 currentIndex = 0;

        // Collect lock keys for the user
        for (uint256 i = 1; i < nonce; i++) {
            bytes32 lockerKey = activeLocks[user][i];
            if (lockerKey != bytes32(0)) {
                locks[currentIndex] = lockerKey;
                currentIndex++;
            }
        }

        return locks;
    }

    function getIndex(address user , bytes32 lock) public view returns(uint256){
        uint256 rVal;
        uint256 a = ERC721Locks[user][lock].nonce;
        uint256 b = ERC20Locks[user][lock].nonce;
        uint256 c = EtherLocks[user][lock].nonce;
        uint256 d = ERC1155Locks[user][lock].nonce;

        if(a>0) {rVal = a;}
        if(b>0) {rVal = b;} 
        if(c>0) {rVal = c;} 
        if(d>0) {rVal = d;}
        return rVal;     
    }

    function getUnlockTime(address user , bytes32 lockerKey) public view returns(uint256){
        LockERC721 storage lock721_ = ERC721Locks[user][lockerKey];
        LockERC1155 storage lock1155_ = ERC1155Locks[user][lockerKey];
        LockERC20 storage lock20_ = ERC20Locks[user][lockerKey];
        LockEther storage lockEther_ = EtherLocks[user][lockerKey];

        if (lock721_.nonce > 0) {
            return lock721_.unlockTime;
        }
        if (lock1155_.nonce > 0) {
            return lock1155_.unlockTime;
        } 
        if (lock20_.nonce > 0) {
            return lock20_.unlockTime;
        }
        if (lockEther_.nonce > 0) {
            return lockEther_.unlockTime;
        }
        revert("Invalid lock type");
    }

    function getLockerType( address user , bytes32 lockerKey) public view returns(string memory){
        LockERC721 storage lock721_ = ERC721Locks[user][lockerKey];
        LockERC1155 storage lock1155_ = ERC1155Locks[user][lockerKey];
        LockERC20 storage lock20_ = ERC20Locks[user][lockerKey];
        LockEther storage lockEther_ = EtherLocks[user][lockerKey];

        if (lock721_.nonce > 0) {
            return "ERC721";
        }  
        if (lock1155_.nonce > 0) {
            return "ERC1155";
        } 
        if (lock20_.nonce > 0) {
            return "ERC20";
        } 
        if (lockEther_.nonce > 0) {
            return "NATIVE";
        } 
        return "INVALID LOCK TYPE";
    }

    function getLock(address user , uint256 index) public view returns(bytes32){
        return activeLocks[user][index];
    }

    function setFee(uint256 newfee) public isOwner{
        fee = newfee;
    }

    function transferOwnership(address to) public isOwner{
        owner = to;
    }

    function withdrawRevenue(address to , uint256 amount) public isOwner{
        require(revenue >= amount,"not enough revenue");
        revenue = revenue - amount;
        payable(to).transfer(amount);
    }

    function lockEth(uint256 amount , uint256 durationInDays) public payable nonReentrant returns(bytes32) { // returns lock
        require(msg.value >= amount + fee ,"send amount + fee");
        uint256 _fee = msg.value - amount;
        revenue = revenue + _fee;
        uint256 currNonce = nonce;
        nonce++;
        uint256 duration = durationInDays * day_;
        uint256 unlockT = block.timestamp.add(duration);
        bytes32 KEY = bytes32(keccak256(abi.encode(msg.sender , currNonce, nonce ,unlockT, amount)));
        EtherLocks[msg.sender][KEY].amounts =EtherLocks[msg.sender][KEY].amounts.add(amount);
        EtherLocks[msg.sender][KEY].unlockTime =EtherLocks[msg.sender][KEY].unlockTime.add(unlockT);
        EtherLocks[msg.sender][KEY].lockTimestamp = block.timestamp;
        EtherLocks[msg.sender][KEY].nonce = currNonce;
        EtherLocks[msg.sender][KEY].key = KEY;
        EtherLocks[msg.sender][KEY].lockee= msg.sender;
        activeLocks[msg.sender][currNonce] = KEY;
        emit LOCK(msg.sender , abi.encode("ETH" , amount,duration ), KEY , block.timestamp);
        return KEY; 
    }

    function lockERC20(address token  , uint256 amount , uint256 durationInDays) public payable nonReentrant returns (bytes32){
        require(IERC20(token).balanceOf(msg.sender)>= amount,"not enough balances");
        uint256 currNonce = nonce;
        require(msg.value >= fee ,"send fee");
        uint256 _fee = msg.value;
        revenue = revenue + _fee;
        nonce++;
        uint256 duration = durationInDays * day_;
        uint256 unlockT = block.timestamp.add(duration);
        bytes32 KEY = bytes32(keccak256(abi.encode(msg.sender , token , amount, currNonce, unlockT , nonce)));
        IERC20(token).transferFrom(msg.sender, address(this) , amount);
        ERC20Locks[msg.sender][KEY].token = token;
        ERC20Locks[msg.sender][KEY].amounts = ERC20Locks[msg.sender][KEY].amounts.add(amount);
        ERC20Locks[msg.sender][KEY].unlockTime = unlockT;
        ERC20Locks[msg.sender][KEY].lockTimestamp = block.timestamp;
        ERC20Locks[msg.sender][KEY].nonce = currNonce;
        ERC20Locks[msg.sender][KEY].key = KEY;
        ERC20Locks[msg.sender][KEY].lockee = msg.sender;
        activeLocks[msg.sender][currNonce] = KEY;
        emit LOCK(msg.sender, abi.encode("ERC20" , token , amount , duration), KEY , block.timestamp);
        return KEY;
    }

    function lock721(address token , uint256 id , uint256 durationInDays) public payable nonReentrant returns(bytes32){
        require(IERC721(token).ownerOf(id)== msg.sender,"not owner of id");
        require(msg.value >= fee ,"send fee");
        revenue = revenue + msg.value;
        uint256 currNonce= nonce;
        nonce ++;
        uint256 duration = durationInDays * day_;
        uint256 unlockT = block.timestamp.add(duration);
        bytes32 KEY = bytes32(keccak256(abi.encode(msg.sender , token , id , unlockT , currNonce)));
        IERC721(token).transferFrom(msg.sender , address(this), id);
        ERC721Locks[msg.sender][KEY].token = token;
        ERC721Locks[msg.sender][KEY].id = id;
        ERC721Locks[msg.sender][KEY].unlockTime = unlockT;
        ERC721Locks[msg.sender][KEY].lockTimestamp = block.timestamp;
        ERC721Locks[msg.sender][KEY].key = KEY;
        ERC721Locks[msg.sender][KEY].nonce = currNonce;
        ERC721Locks[msg.sender][KEY].lockee=msg.sender;
        activeLocks[msg.sender][currNonce] = KEY;
        emit LOCK(msg.sender , abi.encode("ERC721" , token , id , duration), KEY,block.timestamp);
        return KEY;
    }

    function lock1155(address token , uint256 id, uint256 amount , uint256 durationInDays) public payable nonReentrant returns(bytes32){
        require(IERC1155(token).balanceOf(msg.sender , id) >= amount,"not enough balance");
        require(msg.value >= fee,"send fee");
        uint256 _fee = msg.value;
        revenue = revenue + _fee;
        uint256 currNonce= nonce;
        nonce ++;
        uint256 duration = durationInDays * day_;
        uint256 unlockT = block.timestamp.add(duration);
        bytes memory enc = abi.encode(msg.sender , token , id, amount , unlockT , currNonce);
        bytes32 KEY = bytes32(keccak256(enc));
        
        IERC1155(token).safeTransferFrom(msg.sender, address(this) , id , amount ,enc);
            
        ERC1155Locks[msg.sender][KEY].token = token;
        ERC1155Locks[msg.sender][KEY].amounts = amount;
        ERC1155Locks[msg.sender][KEY].id = id;
        ERC1155Locks[msg.sender][KEY].unlockTime = unlockT;
        ERC1155Locks[msg.sender][KEY].lockTimestamp = block.timestamp;
        ERC1155Locks[msg.sender][KEY].nonce = currNonce;
        ERC1155Locks[msg.sender][KEY].key=KEY;
        ERC1155Locks[msg.sender][KEY].lockee = msg.sender;
        activeLocks[msg.sender][currNonce] = KEY;
        emit LOCK(msg.sender , abi.encode("ERC1155" , token , id, amount , duration) ,KEY, block.timestamp);
        
        return KEY;
    }

    function withdraw721Lock(bytes32 lockerKey ) public payable nonReentrant returns(bool){
        require(msg.value >= fee,"send fee");
        address user = msg.sender;
        uint256 index = ERC721Locks[user][lockerKey].nonce;
        if (index == 0){
            require(index>0 ,"not found index of key");
        }
        require(activeLocks[user][index].length > 0 ,"no lock found at key");
        require(block.timestamp>ERC721Locks[user][lockerKey].unlockTime,"Unlock Time not reached");
        (uint256 id, address token, address _lockee) = (ERC721Locks[user][lockerKey].id, ERC721Locks[user][lockerKey].token ,ERC721Locks[user][lockerKey].lockee );
        require(msg.sender == _lockee,"this addr is not owner of locker");
        revenue = revenue + msg.value;
        if(ERC721Locks[user][lockerKey].nonce >0 && IERC721(token).ownerOf(id)==address(this)){
        IERC721(token).transferFrom(address(this), user , id);
        emit UNLOCK(user , abi.encode("ERC721-UNLOCK" , token , id ), block.timestamp);
        }
        delete activeLocks[user][ERC721Locks[user][lockerKey].nonce];
        delete ERC721Locks[user][lockerKey];
        return true;
    }

    function withdraw1155Lock(bytes32 lockerKey) public payable nonReentrant returns(bool){
        address user = msg.sender;
        uint256 index = ERC1155Locks[user][lockerKey].nonce;
        if (index == 0){
            require(index>0 ,"not found index of key");
        }
        require(activeLocks[user][index].length > 0 ,"no lock found at key");
        require(block.timestamp>ERC1155Locks[user][lockerKey].unlockTime,"Unlock Time not reached");
        (uint256 id, address token, uint256 amt, address _lockee) = (ERC1155Locks[user][lockerKey].id, ERC1155Locks[user][lockerKey].token , ERC1155Locks[user][lockerKey].amounts,ERC1155Locks[user][lockerKey].lockee);
        require(msg.sender == _lockee,"not owner of locker");
        require(msg.value >=fee ,"send fee");
        revenue = revenue + msg.value;
        if(ERC1155Locks[user][lockerKey].nonce > 0 && IERC1155(token).balanceOf(address(this) , id) >= amt){
        IERC1155(token).safeTransferFrom(address(this), user , id, amt ,"Unlock");
        emit UNLOCK(user , abi.encode("ERC1155-UNLOCK" , token , id, amt ), block.timestamp);
        }
        delete activeLocks[user][ERC1155Locks[user][lockerKey].nonce];
        delete ERC1155Locks[user][lockerKey];
        return true;
    }

    function withdraw20Lock(bytes32 lockerKey) public payable nonReentrant returns(bool){
        address user = msg.sender;
        uint256 i = ERC20Locks[user][lockerKey].nonce;
        if (i==0){
            require(i>0);
        }
        require(activeLocks[user][i].length>0,"no lock found");
        require(block.timestamp > ERC20Locks[user][lockerKey].unlockTime,"Unlock time not reached");
        (address token , uint256 amount, address _lockee) = (ERC20Locks[user][lockerKey].token ,ERC20Locks[user][lockerKey].amounts,ERC20Locks[user][lockerKey].lockee);
        require (msg.sender == _lockee,"Not Owner of lock");
        require(msg.value >=fee ,"send fee");
        revenue = revenue + msg.value;
        if(amount >0 && IERC20(token).balanceOf(address(this))>= amount){
            IERC20(token).transfer(user , amount);
            emit UNLOCK(user , abi.encode("ERC20-UNLOCK" , token , amount), block.timestamp);
        }
        delete activeLocks[user][ERC20Locks[user][lockerKey].nonce];
        delete ERC20Locks[user][lockerKey];
        return true;
    }

    function withdrawNativeLock(bytes32 lockerKey ) public payable nonReentrant returns(bool){
        address user = msg.sender;
        uint256 i = EtherLocks[user][lockerKey].nonce;
        if(i==0){
            require(i>0);
        }
        require(activeLocks[user][i].length >0,"no lock found");
        require(block.timestamp > EtherLocks[user][lockerKey].unlockTime,"Unlock time not reached");
        require(msg.sender == EtherLocks[user][lockerKey].lockee,"not lockee");
        require(msg.value >=fee ,"send fee");
        uint256 amt = EtherLocks[user][lockerKey].amounts;
        uint256 _fee = msg.value; 
        revenue = revenue + _fee;
        if(amt > 0 && address(this).balance >=amt){
            payable(user).transfer(amt);
            emit UNLOCK(user , abi.encode("ETH-UNLOCK" , amt), block.timestamp);
        }
        delete activeLocks[user][EtherLocks[user][lockerKey].nonce];
        delete EtherLocks[user][lockerKey];
        return true;
    }

    //mint erc20 tokens
    function mintERC(string memory name , string memory symbol , uint256 supply) public payable nonReentrant returns(address){
        address _owner = msg.sender;
        require(msg.value >=fee ,"send fee");
        revenue = revenue + msg.value;
        BOTGENTOKEN newToken = new BOTGENTOKEN(name , symbol, _owner , supply);
        uint256 n = tokenN;
        tokenN++;
        allCreations[n].tokenAddress = address(newToken);
        allCreations[n].name = name;
        allCreations[n].symbol = symbol;
        allCreations[n].supply = supply;
        allCreations[n].minter = _owner;
        allCreations[n].time = block.timestamp;
        //crates[_owner][n] = address(newToken);
        emit MINT(address(newToken) , supply,name, symbol , _owner , block.timestamp);
        return address(newToken);
    }

    // function to unlock all locktypes using just key 

    function withdrawLock( bytes32 lockerKey) public payable{
        address user = msg.sender;
        LockERC721 storage lock721_ = ERC721Locks[user][lockerKey];
        LockERC1155 storage lock1155_ = ERC1155Locks[user][lockerKey];
        LockERC20 storage lock20_ = ERC20Locks[user][lockerKey];
        LockEther storage lockEther_ = EtherLocks[user][lockerKey];
        if (lock721_.nonce > 0) {
            withdraw721Lock(lockerKey);
        }  
        if (lock1155_.nonce > 0) {
            withdraw1155Lock(lockerKey);
        } 
        if (lock20_.nonce > 0) {
            withdraw20Lock(lockerKey);
        } 
        if (lockEther_.nonce > 0) {
            withdrawNativeLock(lockerKey);
        } 
        //return "INVALID LOCK TYPE";
    }

