pragma solidity ^0.8.0;

interface liquidForgeStaking {
    // get the forgeblocks for the forged liquid key
    function _liquidKeyForgeBlocks(address) external view returns (uint256);

    // Retrieve the Token IDs of the Forged Liquid Key address
    function forgedKeyOwnerAddress(address account)
        external
        view
        returns (uint256[] memory);
}

contract LiquidKeysForge is Ownable, ReentrancyGuard, Pausable {
    using EnumerableSet for EnumerableSet.UintSet;

    mapping(uint256 => uint256) public nftForgeBlocks;
    mapping(address => EnumerableSet.UintSet) private nftForges; // private
    mapping(uint256 => address) public _nftTokenForges;

    // NFT Collection, Forge, and METL token addresses
    address public nftAddress = 0x753412F4FB7245BCF1c0714fDf59ba89110f39b8; // Liquid Keys
    address public forgeAddress = 0x346e31ddE260c6BEEA84186ad99878C1D8C7D90F; // Liquid Key Forge
    address public metlTokenAddress = 0xFcbE615dEf610E806BB64427574A2c5c1fB55510; // METL Token

    // rate governs how often you receive your token
    uint256 public nftRate = 9324596774194; // nft earning rate

    // how many blocks before a claim (30 days)
    uint256 public minBlockToClaim = 222208;
    // expiration time
    uint256 public expiration = 1000000000;

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    // Set this to a expiration block to disable the ability to continue accruing tokens past that block number.
    // which is calculated as current block plus the parameters
    //
    // Set a multiplier for how many tokens to earn each time a block passes
    // and the min number of blocks needed to pass to claim rewards
    function setRates(
        uint256 _nftRate,
        uint256 _minBlockToClaim,
        uint256 _expiration
    ) public onlyOwner {
        nftRate = _nftRate;
        minBlockToClaim = _minBlockToClaim;
        expiration = block.number + _expiration;
    }

    // Set addresses
    function setAddresses(
        address _nftAddress, 
        address _forgeAddress, 
        address _metlTokenAddress
    ) public onlyOwner {
        nftAddress = _nftAddress;
        forgeAddress = _forgeAddress;
        metlTokenAddress = _metlTokenAddress;
    }

    // Reward Claimable information
    function rewardClaimable(uint256 tokenId) public view returns (bool) {
        uint256 blockCur = Math.min(block.number, expiration);
        if (nftForgeBlocks[tokenId] > 0) {
            return (blockCur - nftForgeBlocks[tokenId] > minBlockToClaim);
        }
        return false;
    }

    // reward amount by address/tokenIds[]
    function CalculateEarnedMETL(address account, uint256 tokenId)
        public
        view
        returns (uint256)
    {
        require(
            Math.min(block.number, expiration) > nftForgeBlocks[tokenId],
            "Invalid blocks"
        );
        return
            nftRate *
            (nftForges[msg.sender].contains(tokenId) ? 1 : 0) *
            (Math.min(block.number, expiration) -
                Math.max(
                    liquidForgeStaking(forgeAddress)._liquidKeyForgeBlocks(
                        account
                    ),
                    nftForgeBlocks[tokenId]
                ));
    }

    // claim rewards (earned METL)
    function ClaimEarnedMETL(uint256[] calldata tokenIds) public whenNotPaused {
        uint256 reward;
        uint256 blockCur = Math.min(block.number, expiration);

        require(
            liquidForgeStaking(forgeAddress)
                .forgedKeyOwnerAddress(msg.sender)
                .length > 0,
            "No Liquid Key Forged"
        );

        for (uint256 i; i < tokenIds.length; i++) {
            require(
                nftForges[msg.sender].contains(tokenIds[i]),
                "Staking: token not forged"
            );
            require(blockCur - nftForgeBlocks[tokenIds[i]] > minBlockToClaim);
        }

        for (uint256 i; i < tokenIds.length; i++) {
            reward += CalculateEarnedMETL(msg.sender, tokenIds[i]);
            nftForgeBlocks[tokenIds[i]] = blockCur;
        }

        if (reward > 0) {
            IERC20(metlTokenAddress).transfer(msg.sender, reward);
        }
    }

    // Forge NFT
    function ForgeNFT(uint256[] calldata tokenIds)
        external
        whenNotPaused
    {
        uint256 blockCur = block.number;
        require(msg.sender != nftAddress, "Invalid NFT contract address");

        for (uint256 i; i < tokenIds.length; i++) {
            IERC721(nftAddress).safeTransferFrom(
                msg.sender,
                address(this),
                tokenIds[i],
                "Transfer from Member to Forge"
            );
            nftForges[msg.sender].add(tokenIds[i]);
            nftForgeBlocks[tokenIds[i]] = blockCur;
            // write the entry for the ETH address
            _nftTokenForges[tokenIds[i]] = msg.sender;
        }
    }

    // withdrawal function
    function WithdrawNFT(uint256[] calldata tokenIds)
        external
        whenNotPaused
        nonReentrant
    {
        for (uint256 i; i < tokenIds.length; i++) {
            require(
                nftForges[msg.sender].contains(tokenIds[i]),
                "Staking: token not forged"
            );

            nftForges[msg.sender].remove(tokenIds[i]);
            _nftTokenForges[tokenIds[i]] = 0x0000000000000000000000000000000000000000;

            IERC721(nftAddress).safeTransferFrom(
                address(this),
                msg.sender,
                tokenIds[i],
                "Not withdrawn: Transfer failed"
            );
        }
    }

    // withdraw earned METL
    function WithdrawEarnedMETL() external onlyOwner {
        uint256 tokenSupply = IERC20(metlTokenAddress).balanceOf(address(this));
        IERC20(metlTokenAddress).transfer(msg.sender, tokenSupply);
    }
    
    // ERC721 token receiver for transferred NFT to Forge
    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure  returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }
}
