  function setMaxSupply(uint256 id_, uint256 amount_) external;
  function grantMinterRole(address addr_) external;
  function mint(
    address to_,
    uint256 id_,
    uint256 amount_,
    bytes memory data_
  ) external;

  function burn(
    address account,
    uint256 id,
    uint256 amount
  ) external;
