contract ETFApplicationGame is VRFConsumerBaseV2, Ownable {
  address public gameToken = 0x667210a731447F8B385e068205759BE2311b86d4; // ETF The Token
  VRFCoordinatorV2Interface vrfCoord;
  LinkTokenInterface link;
  uint64 private _vrfSubscriptionId;
  bytes32 private _vrfKeyHash;
  uint16 private _vrfNumBlocks = 3;
  uint32 private _vrfCallbackGasLimit = 600000;
  mapping(uint256 => address) private _applicationWagerInitUser;
  mapping(uint256 => uint256) private _applicationWagerInitAmount;
  mapping(uint256 => uint256) private _applicationWagerInitNonce;
  mapping(uint256 => bool) private _applicationWagerInitSettled;
  mapping(address => uint256) public userWagerNonce;

  uint256 public poolSize;
  uint256 public maxWagerPercentage;
  uint256 private constant PERCENT_DENOMENATOR = 1000;
  uint256 public minBetAmount;
  uint256 public payoutPercentage = (PERCENT_DENOMENATOR * 92) / 100;
  uint256 public applicationsApproved;
  uint256 public applicationsDenied;
  uint256 public applicationAmountWon;
  uint256 public applicationAmountLost;
  mapping(address => uint256) public applicationsUserWon;
  mapping(address => uint256) public applicationsUserLost;
  mapping(address => uint256) public applicationUserAmountWon;
  mapping(address => uint256) public applicationUserAmountLost;
  mapping(address => bool) public lastApplicationWon;

  event InitiatedApplication(
    address indexed wagerer,
    uint256 indexed nonce,
    uint256 requestId,
    uint256 amountWagered
  );
  event SettledApplication(
    address indexed wagerer,
    uint256 indexed nonce,
    uint256 requestId,
    uint256 amountWagered,
    bool isWinner,
    uint256 amountWon
  );

  constructor(
    address _vrfCoordinator,
    uint64 _subscriptionId,
    address _linkToken,
    bytes32 _keyHash
  ) VRFConsumerBaseV2(_vrfCoordinator) {
    vrfCoord = VRFCoordinatorV2Interface(_vrfCoordinator);
    link = LinkTokenInterface(_linkToken);
    _vrfSubscriptionId = _subscriptionId;
    _vrfKeyHash = _keyHash;
  }

  function setMaxWagerPercentage(uint256 _maxWagerPercentage) external onlyOwner {
    maxWagerPercentage = _maxWagerPercentage;
  }

  function resetPoolSize() external onlyOwner {
    poolSize = IERC20(gameToken).balanceOf(address(this));
  }

  function setMinBetAmount(uint256 _minBetAmount) external onlyOwner {
    minBetAmount = _minBetAmount;
  }

  function submitEtfApplication(uint256 amountBet) external payable {
    require(IERC20(gameToken).balanceOf(msg.sender) > 0, 'must have a bag to wager');
    require(
      amountBet <= (poolSize * maxWagerPercentage) / 100,
      'cannot exceed max wager'
    );
    require(amountBet >= minBetAmount, 'bet more');

    IERC20(gameToken).transferFrom(msg.sender, address(this), amountBet);

    uint256 requestId = vrfCoord.requestRandomWords(
      _vrfKeyHash,
      _vrfSubscriptionId,
      _vrfNumBlocks,
      _vrfCallbackGasLimit,
      uint16(1)
    );

    _applicationWagerInitUser[requestId] = msg.sender;
    _applicationWagerInitAmount[requestId] = amountBet;
    _applicationWagerInitNonce[requestId] = userWagerNonce[msg.sender];
    userWagerNonce[msg.sender]++;

    emit InitiatedApplication(
      msg.sender,
      _applicationWagerInitNonce[requestId],
      requestId,
      amountBet
    );
  }

  function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords)
    internal
    override
  {
    _settleEtfApplication(requestId, randomWords[0]);
  }

  function manualFulfillRandomWords(
    uint256 requestId,
    uint256[] memory randomWords
  ) external onlyOwner {
    _settleEtfApplication(requestId, randomWords[0]);
  }

  function _settleEtfApplication(uint256 requestId, uint256 randomNumber) internal {
    address _user = _applicationWagerInitUser[requestId];
    require(_user != address(0), 'ETF application record does not exist');
    require(!_applicationWagerInitSettled[requestId], 'already settled');
    _applicationWagerInitSettled[requestId] = true;

    uint256 amountBet = _applicationWagerInitAmount[requestId];
    uint256 _nonce = _applicationWagerInitNonce[requestId];
    uint256 _amountToWin = (amountBet * payoutPercentage) /
      PERCENT_DENOMENATOR;
    uint256 amountPayout = amountBet + _amountToWin;
    bool _didUserWin = randomNumber % 2 == 0;

    if (_didUserWin) {
      IERC20(gameToken).transfer(_user, amountPayout);
      applicationsApproved++;
      applicationAmountWon += _amountToWin;
      applicationsUserWon[_user]++;
      applicationUserAmountWon[_user] += _amountToWin;
      lastApplicationWon[_user] = true;
      poolSize = IERC20(gameToken).balanceOf(address(this));

    } else {
      applicationsDenied++;
      applicationAmountLost += amountBet;
      applicationsUserLost[_user]++;
      applicationUserAmountLost[_user] += amountBet;
      lastApplicationWon[_user] = false;
      poolSize = IERC20(gameToken).balanceOf(address(this));      
    }
    emit SettledApplication(
      _user,
      _nonce,
      requestId,
      amountBet,
      _didUserWin,
      _amountToWin
    );
  }

  function setPayoutPercentage(uint256 _percentage) external onlyOwner {
    require(_percentage <= PERCENT_DENOMENATOR, 'cannot exceed 100%');
    payoutPercentage = _percentage;
  }

  function getMaxWager() external view returns (uint256) {
    return (poolSize * maxWagerPercentage) / 100;
  }

  function setGameToken(address _token) external onlyOwner {
    gameToken = _token;
  }

  function setVrfSubscriptionId(uint64 _subId) external onlyOwner {
    _vrfSubscriptionId = _subId;
  }

  function setVrfNumBlocks(uint16 _numBlocks) external onlyOwner {
    _vrfNumBlocks = _numBlocks;
  }

  function setVrfCallbackGasLimit(uint32 _gas) external onlyOwner {
    _vrfCallbackGasLimit = _gas;
  }

  function withdrawGameTokens(uint256 amountToWithdraw) external onlyOwner {
    IERC20(gameToken).transfer(msg.sender, amountToWithdraw);
    poolSize = IERC20(gameToken).balanceOf(address(this));
  }

  function withdrawEth() external onlyOwner {
    uint256 ethBalance = address(this).balance;
    if (ethBalance > 0) {
        payable(msg.sender).transfer(ethBalance);
    }
  }
}
