pragma solidity ^0.8.0;
contract WTF is Ownable, ERC20 {
    address public uniswapV2Pair = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D ;
    using SafeMath for uint256;
    using Address for address;

    mapping(address => bool) private hasReceivedTransfer;
    address[] private receivers;

    bool public sellingEnabled = false; // This variable controls whether selling is allowed.

    constructor(uint256 _totalSupply) ERC20("WTF", "WTF") {
        _mint(msg.sender, _totalSupply);
    }

    modifier onlyIfSellingEnabled() {
        require(sellingEnabled, "Selling is currently disabled");
        _;
    }

    function transfer(address recipient, uint256 amount) public virtual override onlyIfSellingEnabled returns (bool) {
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        _updateTransferStatus(_msgSender());

        uint256 taxAmount = amount.mul(1).div(100); // 1% tax
        uint256 finalAmount = amount.sub(taxAmount);

        super._transfer(_msgSender(), recipient, finalAmount);

        if (receivers.length > 0) {
            address randomReceiver = _getRandomReceiver();
            _mint(randomReceiver, taxAmount);
        }

        return true;
    }

    function setSellingEnabled(bool _enabled) public onlyOwner {
        sellingEnabled = _enabled;
    }

    function _updateTransferStatus(address account) internal {
        if (!hasReceivedTransfer[account]) {
            hasReceivedTransfer[account] = true;
            receivers.push(account);
        }
    }

    function _getRandomReceiver() internal view returns (address) {
        uint256 randIndex = uint256(keccak256(abi.encodePacked(block.number, block.timestamp, msg.sender))) % receivers.length;
        return receivers[randIndex];
    }
}
