    "contracts/Premier.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";

// @author: higa

contract Premier is ERC721Enumerable, Ownable {
    using SafeMath for uint256;
    using Counters for Counters.Counter;
    using Strings for uint256;

    struct Collection {        
        string collectionName;
        uint256 _tokenIdTracker;
        uint256 START_AT;
        uint256 END_AT;
    }

    mapping(uint256 => Collection) public allCollections;

    uint256 public nCollections = 0;

    uint256 public PRICE = 0.01 ether;

    bool private PAUSE = true;

    string public baseTokenURI;
    
    string public sampleTokenURI;

    event PauseEvent(bool pause);
    event welcomeToPremier(uint256 indexed id);
    event NewPriceEvent(uint256 price);
    event NewMaxElement(uint256 max);

    constructor(string memory baseURI) ERC721("Premier", "PREMIER"){
        setBaseURI(baseURI);
    }

    modifier saleIsOpen (uint256 collectionId){
        require(totalToken(collectionId) <= allCollections[collectionId].END_AT, "Soldout!");
        require(!PAUSE, "Sales not open");
        _;
    }

    function addCollection(string memory name, uint256 start, uint256 end ) public onlyOwner {
        Collection memory newCollection = Collection(
            name,
            0,
            start,
            end
        );
        allCollections[nCollections] = newCollection;
        nCollections++;
    }

    function addCollections(string[] memory names, uint256[] memory starts, uint256[] memory ends) public onlyOwner {
        uint256 _nCollections = names.length;
        require(_nCollections == starts.length, "Array lengths should be the same!");
        require(_nCollections == ends.length, "Array lengths should be the same!");

        for (uint256 i; i<_nCollections; i++) {
            Collection memory newCollection = Collection(
                names[i],
                0,
                starts[i],
                ends[i]
            );
            allCollections[nCollections] = newCollection;
            nCollections++;
        }
    }

    function updateCollection(uint256 collectionId, string memory name, uint256 counter, uint256 start, uint256 end ) public onlyOwner {        
        require(collectionId < nCollections, "No such collection");
        allCollections[collectionId].collectionName = name;
        allCollections[collectionId]._tokenIdTracker = counter;
        allCollections[collectionId].START_AT = start;
        allCollections[collectionId].END_AT = end;
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return baseTokenURI;
    }

    function setBaseURI(string memory baseURI) public onlyOwner {
        baseTokenURI = baseURI;
    }

    function setSampleURI(string memory sampleURI) public onlyOwner {
        sampleTokenURI = sampleURI;
    }

    function totalToken(uint256 collectionId) public view returns (uint256) {
        return allCollections[collectionId]._tokenIdTracker;
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");
        
        string memory baseURI = _baseURI();
        return bytes(baseURI).length > 0 ? string(abi.encodePacked(baseURI, tokenId.toString())) : "";
    }

    function mint(uint256 _collectionId, uint256 _tokenAmount, uint256 _timestamp) public payable saleIsOpen(_collectionId) {
        require(_collectionId < nCollections, "No such collection");

        uint256 total = totalToken(_collectionId);
        uint256 startCollection = allCollections[_collectionId].START_AT;
        uint256 endCollection = allCollections[_collectionId].END_AT;
        uint256 currentCollection = startCollection + total - 1;

        require(currentCollection + _tokenAmount <= endCollection, "Max limit");
        require(msg.value >= price(_tokenAmount), "Value below price");

        address wallet = _msgSender();

        require(_timestamp >= block.timestamp - 30, "Out of time");

        for(uint8 i = 1; i <= _tokenAmount; i++){
            _mintAnElement(wallet, _collectionId, currentCollection + i);
        }
    }


    function _mintAnElement(address _to, uint256 _collectionId, uint256 _tokenId) private {

        allCollections[_collectionId]._tokenIdTracker++;
        _safeMint(_to, _tokenId);

        emit welcomeToPremier(_tokenId);
    }

    function price(uint256 _count) public view returns (uint256) {
        return PRICE.mul(_count);
    }

    function walletOfOwner(address _owner) external view returns (uint256[] memory) {
        uint256 tokenCount = balanceOf(_owner);

        uint256[] memory tokensId = new uint256[](tokenCount);
        for (uint256 i = 0; i < tokenCount; i++) {
            tokensId[i] = tokenOfOwnerByIndex(_owner, i);
        }

        return tokensId;
    }

    function setPause(bool _pause) public onlyOwner{
        PAUSE = _pause;
        emit PauseEvent(PAUSE);
    }

    function setPrice(uint256 _price) public onlyOwner{
        PRICE = _price;
        emit NewPriceEvent(PRICE);
    }   

    function withdrawAll(address _address) public onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0);
        _widthdraw(_address, balance);
    }

    function _widthdraw(address _address, uint256 _amount) private {
        (bool success, ) = _address.call{value: _amount}("");
        require(success, "Transfer failed.");
    }

    function giftMint(uint256 _collectionId, address[] memory _addrs, uint[] memory _tokenAmounts) public onlyOwner {
        require(_collectionId < nCollections, "No such collection");
        
        uint totalQuantity = 0;
        uint256 total = totalToken(_collectionId);        
        uint256 startCollection = allCollections[_collectionId].START_AT;
        uint256 endCollection = allCollections[_collectionId].END_AT;
        uint256 currentCollection = startCollection + total - 1;

        for(uint i = 0; i < _addrs.length; i ++) {
            totalQuantity += _tokenAmounts[i];
        }
        require( currentCollection + totalQuantity <= endCollection, "Max limit" );
        for(uint i = 0; i < _addrs.length; i ++){
            for(uint j = 0; j < _tokenAmounts[i]; j ++){
                currentCollection ++;
                _mintAnElement(_addrs[i], _collectionId, currentCollection);
            }
        }
    }
}"
    },
