pragma solidity 0.8.17;

import "@openzeppelin/contracts/utils/Create2.sol";
import "./utils/Ownable.sol";

contract Factory is Ownable {

  address private operator;

  event Deployed(address addr, bytes32 salt);

  modifier onlyOperator() {
        require(operator == msg.sender, "no authorization");
        _;
  }

  constructor(address _owner) Ownable(_owner) {}

  function addOperator(address _operator) public onlyOwner {
    operator = _operator;
  }

  function removeOperator(address _operator) public onlyOwner {
    operator = address(0);
  }

  function deploy(bytes memory code, bytes32 salt) public onlyOperator {

    address addr = Create2.deploy(0, salt, code);

    emit Deployed(addr, salt);
  }

  function getAddress(bytes memory code, bytes32 salt) public view returns (address) {
    return Create2.computeAddress(salt, keccak256(code));
  }
}"
