    "contracts/vesting/KreatorhoodFeesDistributor.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import { IERC20, SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ECDSA } from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import { EIP712Base } from "../utils/EIP712Base.sol";
import { LibMerkleDistributor } from "../libraries/LibMerkleDistributor.sol";
import { LibMerkleProof } from "../libraries/LibMerkleProof.sol";

contract KreatorhoodFeesDistributor is Ownable, EIP712Base {
    using SafeERC20 for IERC20;

    // Emitted when a user claims
    event FeesClaimed(address indexed user, bytes32 indexed merkleRoot, address token, uint256 amount);

    // Emitted whenever a new round has started
    event FeesRoundStarted(bytes32[] merkleRoots, uint256 roundAmount, address token, uint256 roundExpiryBlock);

    // Mapping used to mark if a certain user has claimed the tokens for a corresponding round
    mapping(bytes32 => mapping(address => uint256)) public claimed;

    // Merkle trees roots for the current round
    bytes32[] public merkleRoots;

    // Block number at which the current round expires
    uint256 public roundExpiryBlock;

    constructor() {
        _initializeEIP712("KreatorhoodFeesDistributor");
    }

    function startNewRound(
        bytes32[] calldata _merkleRoots,
        address _token,
        uint256 _roundAmount,
        uint256 _roundExpiryBlock
    ) external payable onlyOwner {
        if (_roundExpiryBlock <= block.number) revert LibMerkleDistributor.InvalidExpiryBlock(_roundExpiryBlock);
        roundExpiryBlock = _roundExpiryBlock;
        merkleRoots = _merkleRoots;

        if (_token != address(0) && IERC20(_token).balanceOf(address(this)) < _roundAmount)
            revert LibMerkleDistributor.InvalidRoundTotalAmount(_token, _roundAmount);
        else if (_token == address(0) && address(this).balance < _roundAmount)
            revert LibMerkleDistributor.InvalidRoundTotalAmount(_token, _roundAmount);

        emit FeesRoundStarted(_merkleRoots, _roundAmount, _token, _roundExpiryBlock);
    }

    function claim(
        LibMerkleDistributor.Claim calldata _claim,
        LibMerkleProof.MerkleTree calldata _merkleTree,
        uint8 _merklePosition,
        bytes calldata _signature
    ) external {
        if (roundExpiryBlock < block.number)
            revert LibMerkleDistributor.RoundExpired(_merkleTree.root, roundExpiryBlock);
        if (_merkleTree.root != merkleRoots[_merklePosition])
            revert LibMerkleDistributor.InvalidRound(_merkleTree.root);
        if (_claim.user != _msgSender()) revert LibMerkleDistributor.InvalidUser(_msgSender(), _claim.user);
        if (claimed[_merkleTree.root][_msgSender()] != 0)
            revert LibMerkleDistributor.UsedAlreadyClaimed(_merkleTree.root, _msgSender());

        uint256 proofLength = _merkleTree.proof.length;
        bytes32 claimHash = LibMerkleDistributor.hashClaim(_claim);
        if (proofLength != 0) {
            if (proofLength > LibMerkleDistributor.MAX_PROOF_LENGTH) {
                revert LibMerkleDistributor.ProofTooLarge(proofLength);
            }
            if (!LibMerkleProof.verifyProof(_merkleTree.proof, _merkleTree.root, claimHash)) {
                revert LibMerkleDistributor.InvalidProof();
            }

            claimHash = LibMerkleDistributor.hashBatchClaim(_merkleTree.root, proofLength);
        } else {
            revert LibMerkleDistributor.InvalidProof();
        }
        (address signer, ECDSA.RecoverError err) = ECDSA.tryRecover(toTypedMessageHash(claimHash), _signature);
        if (err != ECDSA.RecoverError.NoError) revert LibMerkleDistributor.InvalidSignature(_signature);
        if (owner() != signer) revert LibMerkleDistributor.ProofNotSignedByOwner(signer);

        claimed[_merkleTree.root][_msgSender()] = 1;
        if (_claim.token == address(0)) {
            (bool success, ) = payable(_msgSender()).call{ value: _claim.amount }("");
            if (!success) revert LibMerkleDistributor.NativeTransfer(_merkleTree.root, _msgSender());
        } else {
            IERC20(_claim.token).safeTransfer(_msgSender(), _claim.amount);
        }

        emit FeesClaimed(_msgSender(), _merkleTree.root, _claim.token, _claim.amount);
    }

    receive() external payable {}
}
