pragma solidity ^0.8.9;

contract RangeBet is Ownable, Pausable {
    using SafeERC20 for IERC20Metadata; 

    uint256 public constant minBidAmount = 1000000000000000000;
    uint8 public constant minPercent = 5;
    uint8 public constant maxPercent = 95;
    uint64 public constant precision = 1000000000;
    IERC20Metadata public GameToken;
    address public commissionRecipient;

    struct Game {
        uint8 percent;
        bool isGreater;
        bool result;
        address player;
        uint256 possibleWin;
        uint256 targetBlockNumber;
        uint256 randomNumber;
    }

    mapping(address => bytes32) public lastHashes;
    mapping(bytes32 => Game) public allGames;
    bytes32[] private _allHashes;

    event HashRequested(
        address indexed player,
        bytes32 indexed gameHash
    );

    event HashUsed(
        address indexed player,
        bytes32 indexed gameHash,
        uint32 randomNumber
    );
    
    constructor() {
        GameToken = IERC20Metadata(0xD999B1dA916BC1e48F75e77288bbfCb67138CFBF);
        commissionRecipient = 0xe49C82163E5977c7f2992c59aA4eaF8b616C16d2;
    }

    function requestGameHash(uint256 _bidAmount, uint8 _percent, bool _isGreater) external whenNotPaused {
        address sender = tx.origin;
        require(sender == msg.sender, "only EOA");
        (uint256 possibleWin, uint256 commission) = _getPossibleWin(_percent, _bidAmount);
        require(_bidAmount >= minBidAmount && _bidAmount <= _getMaxBidAmount(), "invalid bet amount");
        require(_percent >= minPercent && _percent <= maxPercent, "invalid percent");
        GameToken.safeTransferFrom(sender, address(this), _bidAmount);
        bytes32 gameHash = keccak256(abi.encodePacked(sender, _percent, _bidAmount, _isGreater, block.timestamp));
        Game storage currentGame = allGames[gameHash];
        currentGame.percent = _percent;
        currentGame.isGreater = _isGreater;
        currentGame.player = sender;
        currentGame.possibleWin = possibleWin;
        GameToken.safeTransfer(commissionRecipient, commission);
        currentGame.targetBlockNumber = block.number;
        lastHashes[sender] = gameHash;
        emit HashRequested(sender, gameHash);
    }
    
    function play() external {
        address sender = msg.sender;
        bytes32 gameHash = lastHashes[sender];
        require(gameHash != bytes32(0), "first request a game hash");
        Game storage currentGame = allGames[gameHash];
        (uint32 D1, uint32 D2) = _getLimits(currentGame.percent);
        bytes32 targetBlockHash = blockhash(currentGame.targetBlockNumber);
        require(targetBlockHash != bytes32(0), "expired, request a new game hash");
        bytes32 targetRandomHash = keccak256(abi.encodePacked(targetBlockHash, sender));
        uint32 targetRandomNumber = uint32(uint256(targetRandomHash) % 1000000);
        currentGame.randomNumber = targetRandomNumber;
        if((currentGame.isGreater && targetRandomNumber >= D2) || (!currentGame.isGreater && targetRandomNumber <= D1)) {
            GameToken.safeTransfer(currentGame.player, currentGame.possibleWin);
            currentGame.result = true;
        }
        _allHashes.push(gameHash);
        lastHashes[sender] = bytes32(0);
        emit HashUsed(sender, gameHash, targetRandomNumber);
    }

    function getCurrentBlockNumber() external view returns(uint256 number) {
        number = block.number;
    }

    function getMaxBidAmount() external view returns(uint256) {
        return _getMaxBidAmount();
    } 

    function withdrawTokens(address _recipient, uint256 _amount) external onlyOwner {
        GameToken.safeTransfer(_recipient, _amount);
    }

    function setTokenAndRecipient(address _token, address _recipient) external onlyOwner {
        GameToken = IERC20Metadata(_token);
        commissionRecipient = _recipient;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function _getMaxBidAmount() private view returns(uint256) {
        uint256 balanceThis = GameToken.balanceOf(address(this));
        uint256 maxBid = (balanceThis * 70000) / 100000;
        return maxBid;
    }

    function getHashByIndex(uint256 _index) external view returns(bytes32) {
        return _allHashes[_index];
    }

    function getSliceAllHashes(uint _start, uint _end) external view returns(bytes32[] memory slice) {
        require(_end > _start, "Invalid input data");
        uint256 sliceLength = _end - _start; 
        slice = new bytes32[](sliceLength);
        for(uint256 i = 0; i < sliceLength; i++) {
            uint256 storagePosition = i + _start;
            slice[i] = _allHashes[storagePosition];
        }
    }

    function getLengthAllHashes() external view returns(uint256 L) {
        L = _allHashes.length;
    }

    function getRandomNumber(uint256 _targetBlockNumber, address _player) external view returns(uint32 targetRandomNumber) {
        bytes32 targetBlockHash = blockhash(_targetBlockNumber);
        bytes32 targetRandomHash = keccak256(abi.encodePacked(targetBlockHash, _player));
        targetRandomNumber = uint32(uint256(targetRandomHash) % 1000000);
    }

    function _getLimits(uint8 _percent) private pure returns(uint32 D1, uint32 D2) {
        D1 = (10000 * uint32(_percent)) - 1;
        D2 = 999999 - D1;
    }

    function _getPossibleWin(uint8 _percent, uint256 _bidAmount) private pure returns(uint256 possibleWin, uint256 commission) {
        uint256 aPercent = ((_bidAmount * precision) / _percent) / precision;
        possibleWin = aPercent * 98;
        commission = aPercent * 2;
    }
}
