    {
        return IStake(OLDSTAKE_CONTRACT).depositsOf(msg.sender);
    }
    
        function blocknumber()
        external
        view
        returns (uint256)
    {
        return block.number;
    }

    function initolduser(address account, uint256[] memory tokenIds) external {
        require(!userinit[account], "You Already initialize your address");
        for (uint256 i; i < tokenIds.length; i++) {
        _deposits[account].add(tokenIds[i]);
        depositBlocks[msg.sender][tokenIds[i]] = 14837207;
        userinit[account] = true;
        }
    }

    function claimRewards(uint256[] calldata tokenIds) public {
        uint256 reward;
        uint256 curblock = Math.min(block.number, EXPIRATION);

        uint256[] memory rewards = calculateRewards(msg.sender, tokenIds);

        for (uint256 i; i < tokenIds.length; i++) {
            reward += rewards[i];
            depositBlocks[msg.sender][tokenIds[i]] = curblock;
        }

        if (reward > 0) {
            ITOKEN(ERC20_CONTRACT).mint(msg.sender, reward);
        }
    }

    function deposit(uint256[] calldata tokenIds) external {
        require(started, ' Staking contract not started yet');

        claimRewards(tokenIds);
        

        for (uint256 i; i < tokenIds.length; i++) {
            IERC721(ERC721_CONTRACT).safeTransferFrom(
                msg.sender,
                address(this),
                tokenIds[i],
                ''
            );
            _deposits[msg.sender].add(tokenIds[i]);
        }
    }

    function admin_deposit(uint256[] calldata tokenIds) onlyOwner() external {
        claimRewards(tokenIds);
        

        for (uint256 i; i < tokenIds.length; i++) {
            IERC721(ERC721_CONTRACT).safeTransferFrom(
                msg.sender,
                address(this),
                tokenIds[i],
                ''
            );
            _deposits[msg.sender].add(tokenIds[i]);
        }
    }

    function withdraw(uint256[] calldata tokenIds) external {
        claimRewards(tokenIds);
        for (uint256 i; i < tokenIds.length; i++) {
            require(
                _deposits[msg.sender].contains(tokenIds[i]),
                ' Token not deposited'
            );

            _deposits[msg.sender].remove(tokenIds[i]);

            IERC721(ERC721_CONTRACT).safeTransferFrom(
                address(this),
                msg.sender,
                tokenIds[i],
                ''
            );
        }
    }
}
