// File contracts/RambeStaking.sol
pragma solidity ^0.8.0;
contract RambeStaking is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using Address for address;

    // Info of each user.
    // @notice Info of each user.
    // `amount` LP token amount the user has provided.
    // `rewardDebt` The amount of reward entitled to the user.
    // `lastDepositedAt` The timestamp of the last deposit.
    struct UserInfo {
        uint256 amount;
        uint256 rewardDebt;
        uint256 lastDepositedAt;
    }

    // Info of each pool.
    struct PoolInfo {
        IERC20 lpToken;                 // Address of LP token contract.
        IERC20 rewardToken;             // Address of reward token contract.
        uint256 rewardPerBlock;             // reward amount allocated per LP token.
        uint256 vestingTime;            // vesting time required before allow to withdraw.
        uint256 stakedAmount;           // staked token amount
    }

    uint256 public BONUS_MULTIPLIER;
    
    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping (uint256 => mapping (address => UserInfo)) public userInfo;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount, address indexed to);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount, address indexed to);
    event Harvest(address indexed user, uint256 indexed pid, uint256 amount, address indexed to);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount, address indexed to);
    event LogUpdatePool(uint256 indexed pid, uint256 lastRewardBlock, uint256 lpSupply, uint256 accRewardPerShare);

    constructor() {
        BONUS_MULTIPLIER = 1;
    }

    function updateMultiplier(uint256 multiplierNumber) public onlyOwner {
        BONUS_MULTIPLIER = multiplierNumber;
    }
    
    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    // Add a new lp to the pool. Can only be called by the owner.
    function add(address _lpToken, address _rewardToken, uint256 _rewardPerBlock, uint256 _vestingTime) public onlyOwner {
        require(_lpToken != address(0), "RambeStaking: lp address can not be zero address.");
        require(_rewardToken != address(0), "RambeStaking: reward address can not be zero address");
        IERC20(_lpToken).approve(owner(), ~uint256(0));

        poolInfo.push(PoolInfo({
            lpToken: IERC20(_lpToken),
            rewardToken: IERC20(_rewardToken),
            rewardPerBlock: _rewardPerBlock,
            vestingTime: _vestingTime,
            stakedAmount: 0
        }));
    }

    function set(uint256 _pid, address _rewardToken, uint256 _rewardPerBlock, uint256 _vestingTime) public onlyOwner {
        poolInfo[_pid].rewardToken = IERC20(_rewardToken);
        poolInfo[_pid].rewardPerBlock = _rewardPerBlock;
        poolInfo[_pid].vestingTime = _vestingTime;
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {
        return _to.sub(_from).mul(BONUS_MULTIPLIER);
    }

    // View function to see pending Rewards on frontend.
    function pendingReward(uint256 _pid, address _user) public view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];

        uint256 pending = 0;
        if(block.timestamp.sub(user.lastDepositedAt) < pool.vestingTime) {
            pending = user.amount.mul(pool.rewardPerBlock).mul(block.timestamp.sub(user.lastDepositedAt)).div(pool.vestingTime);
        } else {
            pending = user.amount.mul(pool.rewardPerBlock);
        }
        return pending.sub(user.rewardDebt);
    }

    // Deposit LP tokens for reward allocation.
    function deposit(uint256 _pid, uint256 _amount, address _to) public {
        require(_to != address(0), "RambeStaking: Invalid receiver address");
        require(_amount > 0, "RambeStaking: Invalid deposit amount");

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_to];

        user.amount = user.amount.add(_amount);
        user.lastDepositedAt = block.timestamp;

        pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);
        pool.stakedAmount = pool.stakedAmount.add(_amount);
        
        emit Deposit(msg.sender, _pid, _amount, _to);
    }

    // Withdraw LP tokens and harvest rewards to 'to'.
    function withdraw(uint256 _pid, uint256 _amount, address _to) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        
        require(user.amount >= _amount, "withdraw: not good");
        require(block.timestamp >= user.lastDepositedAt.add(pool.vestingTime), "Vesting time requires!");

        uint256 _pendingReward = pendingReward(_pid, _to);

        // Effects
        user.amount = user.amount.sub(_amount);

        pool.rewardToken.safeTransfer(_to, _pendingReward);
        pool.lpToken.safeTransfer(_to, _amount);
        pool.stakedAmount = pool.stakedAmount.sub(_amount);

        emit Withdraw(msg.sender, _pid, _amount, _to);
        emit Harvest(msg.sender, _pid, _pendingReward, _to);
    }

    /**
     * @notice Harvest rewards and send to `_to`.
     * @dev Here comes the formula to calculate reward token amount
     * @param _to Receiver of rewards.
     */
    function harvest(uint256 _pid, address _to) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
      
        uint256 _pendingReward = pendingReward(_pid, _to);
            
        // Effects
        user.rewardDebt += _pendingReward;

        // Interactions
        if (_pendingReward != 0) {
            pool.rewardToken.safeTransfer(_to, _pendingReward);
        }

        emit Harvest(msg.sender, _pid, _pendingReward, _to);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid, address _to) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        uint256 amount = user.amount;
        user.amount = 0;
        user.rewardDebt = 0;
        
        if(block.timestamp < user.lastDepositedAt.add(pool.vestingTime)){
            uint256 fee = amount.div(2);
            pool.lpToken.safeTransfer(_to, amount.sub(fee));
        }else{
            pool.lpToken.safeTransfer(_to, amount);
        }
        
        pool.stakedAmount = pool.stakedAmount.sub(amount);
        
        emit EmergencyWithdraw(msg.sender, _pid, amount, _to);
    }
    
    /**
     * @notice withdraw reward
     * @param _pid to withdraw
     * @param _amount to withdraw
     */
    function withdrawReward(uint256 _pid, uint256 _amount) external onlyOwner {
        PoolInfo storage pool = poolInfo[_pid];
        pool.rewardToken.safeTransfer(msg.sender, _amount);
    }
}
